css:
@-moz-document url-prefix("https://www.purepc.pl") {

/* ==UserStyle==
@name            PurePC.pl - ciemny wygląd
@description     Zmienia kolor portalu na ciemny
@version         1.0.2
@namespace       https://greasyfork.org/pl/users/124677-pabli
@author          Pabli
@license         CC-BY-NC-4.0
@homepageURL     https://greasyfork.org/scripts/395040-purepc-pl-ciemny-wygląd
@supportURL      https://greasyfork.org/scripts/395040-purepc-pl-ciemny-wygląd/feedback
==/UserStyle== */
/*
NOWE WERSJE DOSTĘPNE NA: 
<a  href="https://greasyfork.org/pl/scripts/395040-purepc-pl-ciemny-wygl%C4%85d">GreasyFork.org</a>, 
<a  href="https://userstyles.world/style/154/purepc-pl-ciemny-wygl-d">UserStyles.world</a>, 
<a  href="https://github.com/pabli24/PurePC-Dark">GitHub.com</a> 
*/


body, body#top, .b_home, .b_node, .b_cat {
	background: #232323 url("data:image/jpeg;base64,") !important;
}
.promo, .latest, .lastten_grp, .footer, .main, .navbox.sticky {
	box-shadow: 0 0 50px #000!important;
}
*, .nl_item h2 a, .ln_item .ni_meta, .ln_item .ni_meta *, .nl_item .ni_meta *, .breadcrumbs *, .artbox .node .content .PageMenuList ul li a, .node .ni_meta *, .node .ni_meta, .nl_item .ni_meta, .sb_list a, .node a.live_msg_u, .b_home .ln_item .name a {
	color: #fff;
}
.ln_item p, .node p, #top > div.container > div:nth-child(1n) > div > section:nth-child(1n) > p {
	color: #ececec;
}
.nl_item p {
	color: #ececec!important;
}
.hcat.hcoth {
	color: #6f6;
}
.hcat.hcred {
	color: #f66;
}
.ln_item {
	border: 1px solid #2a2a2a!important;
	background: #222222!important;
}
.ln_item .ni_meta, .node .ni_meta, .nl_item .ni_meta {
	border: 1px solid #191919!important;
	background: #292929!important;
}
.lt_list a {
	border-bottom: 1px solid #333!important;
}
.lt_list .sbl_foto_s .noav {
	border: 1px solid #333!important;
}
.ln_item h3 a:hover {
	text-decoration: underline;
}
.latest_items {
	background: #222222;
}
.nl_item, .sb_list {
	border: 1px solid #333!important;
	background: #222!important;
}
.sb_list .sbl_foto {
	border: 1px solid #333!important;
}
.sb_list .sbl_foto_s {
	border-bottom: 1px dotted #333!important;
}
.container {
	background: none;
}
.artbox, .lt_list {
	background: #222!important;
}
hr {
	border-top: 1px solid #333!important;
}
.similar_nodes {
	background: #292929!important;
}
.wykres {
	background: rgba(0, 0, 0, 0);
}
.wykres_wynik {
	box-shadow: 1px 1px 20px 0px #000;
	border: 1px solid #333;
}
.wykres_nazwa {
	color: #ddd;
}
.PageMenuList h2 {
	border: 1px solid #404040;
	background: url("data:image/png;base64,") scroll 660px center no-repeat;
}
p.cyt, h3.cyt {
	color: #ff856e!important;
	background: url("data:image/png;base64,") no-repeat!important;
}
p.recenzja, h3.recenzja {
	color: #349dff;
	background: url("data:image/png;base64,") top left no-repeat transparent;
}
.authorx img {
	filter: opacity(0.65);
}
.authorx img:hover {
	filter: opacity(1);
}
table.specy tr td {
	border: 1px solid #121212;
}
table.specy tr:nth-child(even) td {
	background: #2a2a2a;
}
table.specy tr:nth-child(odd) td {
	background: #343434;
}
table.specy tr:first-child td {
	background: #6c0505;
}
.comment:hover, .comment .links {
	background: #202020!important;
}
.comm_meta {
	border-bottom: 1px solid #333!important;
}
.comm_avatar {
	border: 1px solid #333!important;
}
.comm_avatar img {
	background: #111;
}
.comment blockquote {
	border: 1px solid #333;
}
.comment blockquote, .quote_seemore {
	background: #232323 url("data:image/png;base64,") scroll 3px 5px no-repeat;
}
.comment blockquote blockquote {
	background: #232323;
}
.comment .submitted, .comment .qattr, .comment .qattr * {
	color: #d6d6d6;
}
#edit-comment {
	background: #191919;
	color: #fff!important;
}
.comm_seemore {
	border-top: 1px solid #444;
	background: #222;
}
.comment:hover .comm_seemore {
	background: #202020;
}
.commrate .cr_up, .commrate .cr_dw {
	filter: invert(1);
}
.commrate .cr_up:hover, .commrate .cr_dw:hover {
	filter: invert(0);
}
.content p {
	color: #ddd;
}
span[style*="color: #222"],
span[style*="color: #222222"] {
	color: #ddd !important;
}
.comm_report, .comm_uchide {
	color: #ff4848;
}
select, input, textarea, input[type=text], input[type=password] {
	background: #1d1d1d;
	border: 1px solid #444;
}
.cse .gsc-control-cse, .gsc-control-cse {
	background-color: inherit;
	border: none;
}
.loginbox input.login, .loginbox input.password {
	color: #fff;
}
::placeholder,
::-webkit-input-placeholder {
	color: #aaa;
}
.messages.error {
	border: 1px solid #982f2f!important;
	background: #801e1e!important;
}
.messages {
	background: #272727!important;
	border: 1px solid #333!important;
}
.artbox .node .content .PageMenuList ul {
	background: #2d2d2d;
}
ul.pager li a, ul.pager li {
	background: #3c3c3c;
	color: #fff;
}
ul.pager li:hover a {
	background: #616161;
}
div.content.clear-block > ul.pager > li.pager-ellipsis {
	background: #3c3c3c!important;
}
.node .content table.zestaw td:first-child {
	background: #1b1b1b!important;
}
.node .content table.zestaw tr:first-child td {
	background: #900 !important;
}
.search-results.node-results dd .search-info {
	color: #fff;
	border: 1px solid #333333;
	background: #1f1f1f;
}
.b_home .ln_item header, .b_home .ln_item p {
	border-bottom: 1px solid #333333;
}

/*[[mobile]]*/
}

style_setting_options.value:
/* mobile */
html > body,
#main {
	background: #222!important;
}
.comm_meta, #comments .submitted {
	background: #151515;
}
#comments .content {
	background: #1b1b1b;
}
#topm {
	box-shadow: 0 0 5px 2px #eee;
	filter: invert(0.9);
}
input[type=text], input[type=password], input[type=submit], textarea, select {
	background: #c33;
	border: 1px solid #444;
}
#comments .comment .comm_report {
	color: #ff4848;
}
#comments .submitted * {
	color: #49e;
}
.pager li, .pager li.pager-ellipsis {
	border: 1px solid #000;
}
.news-item a.hcat, .news-item a.comments {
	border: 1px solid #444;
}
