css:
@-moz-document domain("forum.everypony.ru") {
.night .wrap-wrap, .wrap-wrap  {
	background-color: rgba(50, 64, 119, 0.5);
}

.night .night-unread .forumtitle, .night .night-unread .topictitle {
    color: #AD87CB!important;
}

li.row strong, dl.details dt, h3 {
    color: #8F94CE;
}

.night blockquote, .night blockquote blockquote, .night blockquote blockquote blockquote, .night .bsph {
    background: rgba(3, 3, 45, 0.5) none;
    color: #b4b4cc;
}

.night, .night .content, .night .panel, .night ul.topiclist li, .night .pagination, .night .post:target .content, .night #topicreview .postbody, body.night .icon a, .night .postprofile strong, .night label, .night fieldset dl:hover dt label, .night fieldset.polls dl.voted, .night fieldset.polls dt, .night fieldset.polls dl, .night #cp-main h2, dl.details dd {
    color: #c8c8ce;
}

.night .post {
    border-bottom-color: #000000;
}

.night .post-meta a, .night .notice a {
    color: #8f9dd2;
}

.postbody .post-meta a.edit-post {
    color: #ad36d2;
    text-decoration: underline;
}

.postbody .post-meta a.edit-post, .night a.postlink, .night a.org, .night a.edit-post, .night #topicreview a, .night .postprofile a, .night .panel a, .night .navbar a, .night .forumbg a {
    color: #D2C6EA;
}

.night .pagination a, .header-profile a {
    background-color: transparent!important;
    border: none!important;
    color: #c781e2!important;
}

.night a.button2, .night input.button2, .night input.button3, .night a.button1, .night input.button1, #navigation a, #navigation a:hover, #tabs .activetab a {
    background-color: rgba(0, 0, 0, 0.34);
    border: 1px solid #bc81e2;
    color: #d2c6ea !important;
}

#navigation #active-subsection a 
{
     background-color: rgba(125, 78, 179, 0.34);
    border: 1px solid #bc81e2;
    color: #d2c6ea !important;
}

.error {
    color: #d86d6d;
}

ul.linkler li, dl.mini dt, #tabs a span {
    color: #8a95cc;
}

#tabs a span:hover, a:hover {
	color: #C36CC9;    
}

#page-footer {
    background-color: #01002f;
}

#header .main-menu li a {
    color: #cdcae9;
    text-decoration: underline;
}
#tabs .activetab a span 
{
    color: #c6c6ea;
}

#tabs .activetab a span:hover {
    color: #bc7eca;
}

ul.topics {
    background-color: rgba(0, 7, 43, 0.43);
}

ul.linklist li.rightside, p.rightside {
    float: right;
    color: #c781e2;
    margin-left: 5px;
    text-align: right;
}

table.table1 td {
    color: #a986d6;
}

.night .bg2, .night .bg3, .night .bg1, .night ul.forums, .night li.row:hover, .night .forumbg, .night #postform #preview {
    background-color: rgba(1, 0, 37, 0.33);
}

h2 {
    color: #cdcae9;
}

a:visited {
    color: #82b8d4;
}

table.table1 tbody tr:hover {
    background-color: rgba(0, 0, 0, 0.5);
}
}
