css:
@-moz-document domain("boto3.readthedocs.io") {
/*
 * Style:   zDark - Blue Grey
 * Site:    boto3 Docs
 * Author:  -z-
 * License: MIT
 * File:    boto3/docs.css
 * Date:    07/25/16
 */

::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #152128;
}

::-webkit-scrollbar-thumb {
    background: #37474F;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75);
}

::-webkit-scrollbar-corner {
    background: #000;
}

body {
    color: #78909C;
    background: #152128;
    font-family: 'Lucida Grande', Arial, sans-serif;
}


a {
    color: #9E9E9E;
}

a:hover {
    color: #B0BEC5 !important;
}

select, input {
    border-color: #152128;
    background: #0f181d;
    color: #a7b5bc;
}

input[type="text"], div.sphinxsidebar input {
    background-color: #030e13;
    border: 1px solid #6c828d;
    border-radius: 3px;
    line-height: 1.3em;
    font-size: 1em;
    padding: 2px 5px;
    width: 11em;
}

input[type="submit"] {
    border: 1px solid #6c828d;
    font-size: 1em;
    line-height: 1.3em;
    background-color: #455A64 !important;
    border-radius: 3px;
    padding: 2px 5px;
}

h1, h2, h3, h4, h5 h6, #content h2 {
    border-bottom: 1px solid #637983;
    margin: 0.6em 0;
    padding: 0.1em 0;
    background: none;
    color: #699bad !important;
        font-family: 'Lucida Grande', Arial, sans-serif;
}

p {
    padding: 0;
    font-family: inherit;
    font-size: inherit;
    color: #78909C;
}

table.indextable tr.cap {
    margin-top: 10px;
    background-color: #1d2b31;
}

table.docutils thead tr {
    background-color: #37474F;
}

table.docutils tbody tr.row-even {
    background-color: #0b191f;
}

table.docutils tbody tr.row-odd {
    background-color: #010a0e;
}

table.docutils td, table.docutils th {
    background-color: inherit;
    border: 1px solid #455A64 !important;
    border-radius: 3px;
}

table.docutils th {
    color: #B0BEC5;
    padding: .5em;
}

dt:target, .highlight, .highlighted {
    background-color: rgba(141, 160, 35, 0.22);
    color: #EEE;
}

dt, dl.function > dt, dl.attribute > dt, dl.classmethod > dt, dl.method > dt, dl.class > dt, dl.exception > dt {
    color: #78909C;
    background: #172227;
    padding: 4px;
    margin: .5em;
    border-bottom: 2px solid #455A64;
}

dl {
    border-bottom: 1px solid #1d2b31;
}

dt {
    font-weight: normal;
}

dt em {
    color: #538ead;
}

tt {
    background: none;
}

tt.descname {
    font-size: 120% !important;    
    font-weight: bold;
}

tt.descclassname {
    color: #78909C;
}

pre {
    background: #0f181d;
    color: #B0BEC5;
    padding: 5px;
}

blockquote {
    border-color: #4892b3;
    background-color: #172227;    
}

dt:target, .highlight, .highlighted {
    background-color: rgba(141, 160, 35, 0.22);
    color: #EEE;
}

code {
    background-color: inherit !important;
}

div.document {
    background: transparent;
}

div.body {
    background: #263238;
    color: #78909C;
    padding: 20px 15px 30px;
    margin-left: 0;
}

div.body dl {
    border-bottom: 1px solid #1d2b31;
}

div.body img {
    -webkit-filter: invert(1);
    filter: invert(1);
}

div.body a {
    color: #CFD8DC;
}

div.body a:visited {
    color: #B0BEC5;
}

div.body a:hover {
    color: #ECEFF1;
}

div.body hr {
    border: 0;
    background-color: #78909C;
    height: 1px;
}

div.body pre {
    background-color: #0f181d;
    border: 1px solid #455A64;
}

div.body dt {
    color: #78909C;
    background: #172227;
    padding: 4px;
    margin: .5em;
    border-bottom: 2px solid #455A64;
}

div.body h1, div.body h2, div.body h3, div.body h4, div.body h5, div.body h6,
div.body h1 a, div.body h2 a, div.body h3 a, div.body h4 a, div.body h5 a, div.body h6 a {
    border-bottom: 1px solid #637983;
    margin: 0.3em 0;
    background: none;
    color: #699bad !important;
    width: 100%;
}

div.body h1 a:hover, div.body h2 a:hover, div.body h3 a:hover, div.body h4 a:hover, div.body h5 a:hover, div.body h6 a:hover {
    color: #ECEFF1 !important;
}

#left-column {
    background-color: #152128;
        border-right: 5px solid #0c171d;
}

#left-column a {
    color: #607D8B;
}

#right-column, #right-column .breadcrumb {
        background: #455A64;
        color: #B0BEC5;
}
#right-column {
    padding: 0 0 20px 0;
    border-right: 5px solid #0c171d;
    border-bottom: 5px solid #0c171d;
}
#right-column .breadcrumb {
    margin-bottom: 0;
    padding: 1em;
}

#right-column a, #right-column .breadcrumb a {
        color: #ECEFF1;
}

.sphinxsidebar > .sidebar-block:not(:last-child):after {
        border: 0;
        border-top: 1px solid #455A64;
}

.sidebar-toc ul li.current > a {
    color: #CFD8DC !important;
    background-color: #455A64;
}

.sidebar-toc ul li a:hover, .sidebar-toc ul li.current > a:hover {
    color: #37474F !important;
    background-color: #78909C;
}

.btn-default {
    color: #78909C;
    background: #263238;
    padding: 5px;
    font-size: 15px !important;
    border-radius: 5px;
    border: 1px solid #90A4AE;
        text-shadow: none;
}

.btn-default:hover {
        text-decoration: none;
    color: #0c171d !important;
    background-color: #ecf0f3;
}

.text-logo {
        border:0;
        background-color: #030e13;
        color: #B0BEC5;
}

div.highlight {
        background: none;
}

.code-font, code, span.pre {
    color: #607D8B;
    background-color: #192125 !important;
        border: 1px solid #1b313e;
    border-radius: 3px;
}

div.body tt.descname, div.body code.descname {
    color: #81a1b1;
}

div.body dt em {
    color: #538ead;
}

code.descname {
    color: #81a1b1;
}

div.related {
    float: right;
    background-color: #455A64;
    color: #B0BEC5;
    padding: 0.5em 0;
    border-top: 1px solid #444;
    margin: 0;
    min-height: 2em;
    border: 0;
    width: calc(100% - 230px);
    line-height: 40px;
}

div.related:first-child {
    border-top: 0;
    border-bottom: 1px solid #000;
}

div.related a {
    color: #ECEFF1;
}

div.related .right {
    color: #455A64;
    font-size:0;
}

div.related .right a {
    color: #78909C;
    background: #263238;
    padding: 5px;
    font-size:15px !important;
    border-radius: 5px;
    border: 1px solid #90A4AE;
}

div.related .right a:hover {
    text-decoration: none;
    color: #0c171d !important;
    background-color: #ecf0f3;
}

div.related ul li {
    float: left;
    padding: 0 4px 0 0;
}

div.related ul .right {
    float: right;
    padding: 0;
}

div.related img {
    margin-top: .75em !important;
}

div.admonition, div.topic, .deprecated, .versionadded, .versionchanged, div.note, div.warning, div.seealsom, #index-nav, div.inherited-member {
    box-shadow: none;
    border-radius: 5px;
    margin: 0.5em 0;
    border: none;
    padding: 0.5em;
}

div.warning .admonition-title {
    color: #f39227;
}

div.topic {
    background-color: #152128;
    border: 1px solid #40535d;
}

.highlight .go /*output*/
{
    color: #AAA;
}

.highlight .gt, .highlight .nf /*tracebacks*/
{
    color: #4772D1;
}

.highlight .nd {
    color: #aaa;
}

table.indextable tr.cap {
    margin-top: 10px;
    background-color: #1d2b31;
}

table.docutils thead tr {
    background-color: #37474F;
}

table.docutils tbody tr.row-even {
    background-color: #0b191f;
}

table.docutils tbody tr.row-odd {
    background-color: #010a0e;
}

table.docutils td, table.docutils th {
    background-color: inherit;
    border: 1px solid #455A64 !important;
    border-radius: 3px;
}

table.docutils th {
    color: #B0BEC5;
}

div.note {
    background-color: #2d3f48;
    color: #aec6d2;
    border-color: #607D8B;
}

div.warning {
    border-color: #803737;
    color: #ab6530;
    background-color: #332418;
}

.admonition.danger, .admonition.error {
    border-left-color: #d9534f;
    border: 1px solid #d9534f;
    background: #481c1c;
}

div.seealso {
    color: #b9b278;
    background-color: rgba(72, 66, 18, 0.31);
    border: 1px solid #a19631 !important;
}

div.seealso dd {
    margin-left: 10px;
}

div.seealso dt {
    color: #b5b49f;
    background: #1d211b;
    border-bottom: 2px solid #6b654d;
}

div.deprecated, div.deprecated-removed {
    background-color: rgba(43, 9, 5, 0.48);
    color: #923d3d;
    padding: .1em .5em;
    border-radius: 5px;
    border: 1px solid #752e2e;
    margin-top: .5em;
}

div.note, #docs-bottom-navigation, .release-status .inline-alert, .download .inline-alert, div.involved #content ul.nav, div.library #content ul.nav {
    background-color: #2d3f48;
    color: #aec6d2;
    border: 1px solid #607D8B;
}

div.note .admonition-title {
    color: #89adbe !important;
}

div.note {
        border: 1px solid #89adbe !important;
}

div.warning, .admonition.important, .admonition.warning, .admonition.attention, .admonition.caution {
        border: 0;
    border: 1px solid #803737;
    color: #ab6530;
    background-color: #332418;
}

div.seealso ul {
    margin-right: 1em;
}

div.modindex-jumpbox {
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
}

#search-results span.pre {
    color: #546E7A;
}

ul.search li div.context {
    color: #90A4AE;
}

.refcount {
    color: #76b976;
}

.rst-versions {
        background-color: #0f181d;
}

.rst-versions .rst-current-version {
    padding: 12px;
    background-color: #0c1419;
}

.footer-relations {
        margin-top: 0;
        border-top: 5px solid #0c171d;
        padding: 12px 15px;
}

div.footer {
    float: left;
    line-height: 150%;
    margin-top: 0;
    padding: 1em;
    text-align: right;
    margin-right: 10px;
    color: #607D8B;
}

div.footer a {
    color: #78909C;
}

}

@-moz-document domain("boto3.readthedocs.io") {

/* credits - https://github.com/kakawait/pygments-darcula */

/* ----[ BASE ]---- */

.highlight .hll { background-color: #214283 }
.highlight  { background: #2B2B2B; color: #A9B7C6 }
.highlight .c { color: #808080 } /* Comment */
.highlight .err { color: #960050 } /* Error */
.highlight .g { color: #808072 } /* Generic */
.highlight .k { color: #CB772F } /* Keyword */
.highlight .l { color: #6897BB } /* Literal */
.highlight .n { color: #A9B7C6 } /* Name */
.highlight .o { color: #A9B7C6 } /* Operator */
.highlight .p { color: #A9B7C6 } /* Punctuation */
.highlight .ch { color: #808080 } /* Comment.Hashbang */
.highlight .cm { color: #53833D } /* Comment.Multiline */
.highlight .cp { color: #808080 } /* Comment.Preproc */
.highlight .cpf { color: #808072 } /* Comment.PreprocFile */
.highlight .c1 { color: #808080 } /* Comment.Single */
.highlight .cs { color: #808080 } /* Comment.Special */
.highlight .gd { color: #A9B7C6 } /* Generic.Deleted */
.highlight .ge { color: #808072; font-style: italic } /* Generic.Emph */
.highlight .gr { color: #a94545 } /* Generic.Error */
.highlight .gh { color: #88BE05; font-weight: bold } /* Generic.Heading */
.highlight .gi { color: #88BE05 } /* Generic.Inserted */
.highlight .go { color: #78909C } /* Generic.Output */
.highlight .gp { color: #808072; font-weight: bold } /* Generic.Prompt */
.highlight .gs { color: #808072; font-weight: bold } /* Generic.Strong */
.highlight .gu { color: #A9B7C6 } /* Generic.Subheading */
.highlight .gt { color: #808080 } /* Generic.Traceback */
.highlight .kc { color: #CB772F } /* Keyword.Constant */
.highlight .kd { color: #CB772F } /* Keyword.Declaration */
.highlight .kn { color: #CB772F } /* Keyword.Namespace */
.highlight .kp { color: #CB772F } /* Keyword.Pseudo */
.highlight .kr { color: #CB772F } /* Keyword.Reserved */
.highlight .kt { color: #CB772F } /* Keyword.Type */
.highlight .ld { color: #6A8759 } /* Literal.Date */
.highlight .m { color: #6897BB } /* Literal.Number */
.highlight .s { color: #6A8759 } /* Literal.String */
.highlight .na { color: #A9B7C6 } /* Name.Attribute */
.highlight .nb { color: #8888C6 } /* Name.Builtin */
.highlight .nc { color: #A9B7C6 } /* Name.Class */
.highlight .no { color: #CB772F } /* Name.Constant */
.highlight .nd { color: #F1C829; font-weight: normal } /* Name.Decorator */
.highlight .ni { color: #A9B7C6 } /* Name.Entity */
.highlight .ne { color: #A34029 } /* Name.Exception */
.highlight .nf { color: #FFC66D } /* Name.Function */
.highlight .nl { color: #A9B7C6 } /* Name.Label */
.highlight .nn { color: #A9B7C6 } /* Name.Namespace */
.highlight .nx { color: #88BE05 } /* Name.Other */
.highlight .py { color: #A9B7C6 } /* Name.Property */
.highlight .nt { color: #F1C829 } /* Name.Tag */
.highlight .nv { color: #A9B7C6 } /* Name.Variable */
.highlight .ow { color: #A9B7C6 } /* Operator.Word */
.highlight .w { color: #A9B7C6 } /* Text.Whitespace */
.highlight .mb { color: #6897BB } /* Literal.Number.Bin */
.highlight .mf { color: #6897BB } /* Literal.Number.Float */
.highlight .mh { color: #6897BB } /* Literal.Number.Hex */
.highlight .mi { color: #6897BB } /* Literal.Number.Integer */
.highlight .mo { color: #6897BB } /* Literal.Number.Oct */
.highlight .sb { color: #6A8759 } /* Literal.String.Backtick */
.highlight .sc { color: #6A8759 } /* Literal.String.Char */
.highlight .sd { color: #6A8759 } /* Literal.String.Doc */
.highlight .s2 { color: #6A8759 } /* Literal.String.Double */
.highlight .se { color: #6897BB } /* Literal.String.Escape */
.highlight .sh { color: #6A8759 } /* Literal.String.Heredoc */
.highlight .si { color: #6A8759 } /* Literal.String.Interpol */
.highlight .sx { color: #6A8759 } /* Literal.String.Other */
.highlight .sr { color: #6A8759 } /* Literal.String.Regex */
.highlight .s1 { color: #6A8759 } /* Literal.String.Single */
.highlight .ss { color: #6A8759 } /* Literal.String.Symbol */
.highlight .bp { color: #A9B7C6 } /* Name.Builtin.Pseudo */
.highlight .vc { color: #A9B7C6 } /* Name.Variable.Class */
.highlight .vg { color: #A9B7C6 } /* Name.Variable.Global */
.highlight .vi { color: #A9B7C6 } /* Name.Variable.Instance */
.highlight .il { color: #6897BB } /* Literal.Number.Integer.Long */

/* ----[ PROPERTIES ]---- */

.highlight.properties .cm { color: #808080 } /* Comment.Multiline */
.highlight.properties .na { color: #CB772F } /* Name.Attribute */
.highlight.properties .se { color: #6A8759 } /* Literal.String.Escape */
}
