css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("panel.byethost.com") {


  .regform .opt_2 input{
    border: 0px solid;
    border-radius: 0;
  }
  
  .regform .opt_2 input:focus{
    background: white;
  }
  
  .regform{
    display:none;
  }
  
  #cpanel, #border-efx{
    border-bottom: 0px solid;
    border-top: 0px solid;
    border-right: 0px solid;
    border-left: 0px solid;
    margin-top: 15px;
    padding: 0px;
    border-radius: 7px;
  }
  
  td.stats_right{
    font-family:Roboto,Helvetica,Arial,sans-serif;
  }
  
  .drag{
    display:none;
  }
  
  #pref_header, #pref-expander{
    display:none;
  }

  
  body{
    background: url(http://xn--wxa.pe.hu/b/liofjj.png);
  }
  
  .spriteheading_compleximg{
    display:none;
  }
  
  .spriteheading_bg{
    margin-bottom:15px;
    background: url(http://Î».pe.hu/b/xtrwzu.PNG);
    background-position: center 100px;
    border-radius:7px;
  }
  
  #logo-top-logo{
    display:none;
  }
  
  #jump-search{
    border-radius: 7px;
    -webkit-box-shadow:0 1px 10px rgba(0,0,0,.1);
    -moz-box-shadow:0 1px 10px rgba(0,0,0,.1);
    box-shadow:0 1px 10px rgba(0,0,0,.1);
  }
  
  #stats-header{
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    margin-top:15px;
  }
  
  .newContent{
    border-radius: 7px;
    padding-bottom: 15px;
    -webkit-box-shadow:0 1px 10px rgba(0,0,0,.1);
    -moz-box-shadow:0 1px 10px rgba(0,0,0,.1);
    box-shadow:0 1px 10px rgba(0,0,0,.1);
    margin-top:15px;
  }
  
  .cellheading, h3, h6, #notices h6, #quota-warnings h6, #content-stats h6{
    background: #1F718C;
    border: 0px solid;
    color: white;
    font-family: Roboto,Helvetica,Arial,sans-serif;
    font-weight: 300;
    height: 25px;
    font-size: 20px;
    border-top-left-radius: 7px;
    border-top-right-radius: 7px; 
  }
  
  .index_box_header{
    border-top-left-radius: 7px;
    border-top-right-radius: 7px; 
  }
  
  .cellbox-header:hover{
    background: #1F718C;
  }
  
  #heading-bottom_bg{
    color: white;
    background: black;
  }
  
  ul{
    list-style-type:none;
  }
  
  a.bottomlink footer_li_a{
    color: white;
  }
  
  .cellbox{
    border-radius: 7px;
    -webkit-box-shadow:0 1px 10px rgba(0,0,0,.1);
    -moz-box-shadow:0 1px 10px rgba(0,0,0,.1);
    box-shadow:0 1px 10px rgba(0,0,0,.1);
  }
  
}
