css:
@-moz-document domain("nintendoage.com") {
a:link {color:#7b7b7b !important;}
a:visited {color:#5f5e5e !important;}
a:hover {color:#a7a7a7 !important;}
a:active {color:#824343 !important;}



/*Background Color*/
body {
    font-family: Verdana,Arial,Helvetica,sans-serif;
    font-size: 11px;
    line-height: 1.42857143;
    color: #ec0b0b;
    background-color: #060606;}






table {
    display: table;
    border-collapse: separate;
    border-spacing: 2px;
    border-color: #7b7b7b;}




.clr1{background-color:#060606}
.clr2{background-color:#060606}
.clr3,#ss_na{background-color:#c3d2ea}
.clr4{background-color:#060606}
.clr5{background-color:#060606}
/* corner link cube */
#cube,#cubeMenu{color:#060606}
/* primary logo text */
#acct,#acct_ds{font-family:"NintendoAge",garamond,sans-serif;font-size:24pt;line-height:1.2;color:#c3d2ea;text-shadow:1px 1px 1px #000;cursor:pointer}
/* primary site console toolbar */
.navbar-inverse .navbar-nav>li>a{color:#fff;padding:4px 20px 4px 20px}
/* collection toolbar */
.navbar-default{border-color:#E7E7E7;box-shadow:none;padding:6px}

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    color: #e00a0a;
    background-color: #171717;
}

.form-control {
    display: block;
    width: 100%;
    height: 29px;
    padding: 6px;
    font-size: 11px;
    line-height: 1.42857143;
    color: #eaeaea;
    background-color: #020202;
    background-image: none;
    border: 1px solid #e00101;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}

/* Edits */
    .clr3, .widgetTocHead, #rs caption, .bbbs, .imgButton, #ss_na, #tocBtm, #navBox, .widget_caption, .widget_footer, .rC,
    .clr2, .rG,
    .breadcrumb,
    .panel-primary > .panel-heading,
    .pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus,
    .pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus,
    .pagination > li > a, .pagination > li > span,
    .dropdown-menu .divider,
    .dropdown-menu,
    .sprites_awards,
    .well,
    .panel-footer,
    pre,
    .panel,
    .Quote, .FTQUOTE {
        background-color:#060606
    }

    .pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus,
    .pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus,
    .panel-success,
    .panel-default,
    .panel-info,
    .Quote, .FTQUOTE {
        border-color: #000;
    }

    .nav-tabs > li > a,
    .btn-default{
        background-color: #060606;
        color: #7b7b7b;
    }

    .btn-default {
        border-color: #000;
    }

    .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus,
    .panel-default > .panel-heading,
    .panel-success > .panel-heading,
    .panel-info > .panel-heading {
        background-color: #060606;
        border-color: #000;
    }

    .btn-default:hover {
        background-color: #a7a7a7;
    }

    .nav-tabs > li > a:hover, .nav-tabs > li > a:focus {
        background-color: #060606;
        color: #000;
    }

    .nav-tabs {
        border-bottom: #000;
    }

    .panel-primary,
    .panel-primary > .panel-heading,
    hr {
        border-color: #000;
    }

    .nav-tabs > li > a {
        border-bottom: 1px solid #000;
    }

    .navbar,
    .nav-bar {
        box-shadow: 0 2px 2px 1px #000;
    }

    .pagination > li > a, .pagination > li > span,
    pre {
        border: 1px solid #000;
    }

    .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
        border-top: 1px solid #000;
    }

    .well {
        border: 1px solid #000;
        box-shadow: 0 1px 0 #000;
    }

    .panel-footer {
        border-top: 1px solid #000;
    }

    .sprites_forums {
        filter: invert(100%);
    }

    .dropdown-toggle {
        background-color: #060606 !important;
    }

    .table-bordered th, .table-bordered td {
        border: 1px solid #000 !important;
    }

}
