css:
@-moz-document domain("www.bloxcity.com") {
.white {
    background:#F44336 !important;
}
.dropdown-content {
    background-color: #F44336;
    margin: 0;
    display: none;
    min-width: 100px;
    max-height: 650px;
    overflow-y: auto;
    opacity: 0;
    position: absolute;
    z-index: 999;
    will-change: width, height;
}
.dropdown-content li:hover {
background-color: #F44336;
}
nav a, nav ul a {
    color: #F0F0F0 !important;
}
nav ul a {
    transition: background-color .3s;
    font-size: 1rem;
    color: #F0F0F0 !important;
    display: inline-block;
    padding: 0 15px;
    cursor: pointer;
}
.blue {
    background-color: #ff5252 !important;
}
.site-footer { 
    background:#d50000;
    box-shadow: 0px 1px 2px #ddd;
    opacity:0.8;
}
.site-footer a {
color:#efefef !important;
}
.card-panel {
    transition: box-shadow .25s;
    padding: 20px;
    margin: 0.5rem 0 1rem 0;
    border-radius: 2px;
    background-color: #C7281C !important;
}
div[style*="background:#1976d2;color:white;padding:15px 25px;"] {
background:#F44336 !important;
}
}
