css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  .ps-room.ps-room-opaque, .dark .ps-room.ps-room-opaque {
    background: url("http://www.kemecer.com/i/pokemon-wallpaper-legendary-wallpaper-wide.jpg");
    background-size: cover;
  }
  
  .ps-room .battle-controls {
    background: rgba(0, 0, 0, 0.2);
    padding-top: 5px;
  }
  
  button[name="switchSides"], button[name="instantReplay"], .replayDownloadButton, button[name="saveReplay"], button[name="closeAndMainMenu"], button[name="closeAndRematch"], button[name="goToEnd"], button[name="skipTurn"], button[name="setTimer"], button[name="undoChoice"] {
    background: rgba(51, 51, 51, 0.8);
    padding: 3px 8px;
    border: 1px solid black;
    border-radius: 3px;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  button[name="switchSides"]:hover, button[name="instantReplay"]:hover, .replayDownloadButton:hover, button[name="saveReplay"]:hover, button[name="closeAndMainMenu"]:hover, button[name="closeAndRematch"]:hover, button[name="goToEnd"]:hover, button[name="skipTurn"]:hover, button[name="setTimer"]:hover, button[name="undoChoice"]:hover {
    background: rgba(31, 31, 31, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  button[name="goToEnd"] {
    margin-left: 2px;
  }
  
  .dark .replayDownloadButton {
    color: #EEE !important;
  }
  
  .battle-log h2, .dark .battle-log h2 {
    background: #333;
    border-color: #000;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    text-align: center;
    box-shadow: inset 0px 1px 2px #111, 1px 1px 0 rgba(255, 255, 255, .8);
  }
  
  .battle, .dark .battle {
    border-color: black;
  }
  
  .battle-log, .dark .battle-log {
    background: rgba(255, 255, 255, 0.8);
    z-index: 1;
    border-color: #000;
    box-shadow: -2px 4px 3px rgba(0, 0, 0, 0.4);
    color: black;
  }
  
  .battle-log::-webkit-scrollbar {
    width: 12px;
  }
 
  .battle-log::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0px 0px 6px rgba(0, 0, 0, 0.3); 
  }
 
  .battle-log::-webkit-scrollbar-thumb {
    background: rgba(51, 51, 51, 0.8);
    border-radius: 10px;
    box-shadow: 0px 0px 5px black inset;
  }   
  
  .battle-log-add, .dark .battle-log-add {
    background: rgba(51, 51, 51, 0.8);
    z-index: 1;
    border-color: #000;
    box-shadow: -2px 4px 3px rgba(0, 0, 0, 0.4), 0px -1px 2px rgba(0, 0, 0, 0.1);
    color: #BBB;
  }
  
  .battle-log-add .chatbox label {
    text-shadow: 0px 0px 2px #000;
  }
  
  .battle-log-add .textbox {
    background: #333;
    border-color: #000;
    border-radius: 0px;
    box-shadow: inset 0px 1px 2px #111, 1px 1px 0 rgba(255, 255, 255, .2);
    color: #EEE;
  }
  
  .battle-log-add .textbox:hover {
    background: #2B2B2B;
    border-color: #000;
    border-radius: 0px;
    box-shadow: inset 0px 1px 2px #0B0B0B, 1px 1px 0 rgba(255, 255, 255, .2);
    color: #EEE;
  }
  
  .battle-log-add .textbox:focus {
    border-color: #2F659B;
    box-shadow: inset 0px 1px 2px #111, 0px 0px 5px #699ACB;
  }
  
  .battle-log-add button {
    background: rgba(51, 51, 51, 0.8);
    padding: 3px 8px;
    border: 1px solid black;
    border-radius: 3px;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  .battle-log-add button:hover {
    background: rgba(31, 31, 31, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  .battle-log div[style="padding:5px 0"], .dark .battle-log div[style="padding:5px 0"] {
    background: rgba(51, 51, 51, 0.8);
    border-style: solid;
    border-width: 1px 1px 0px 1px;
    border-color: #000;
    border-radius: 20px 20px 0px 0px;
    color: #DDD;
    text-align: center;
  }
  
  .battle-log div[style="padding:5px 0"]:hover strong, .dark .battle-log div[style="padding:5px 0"]:hover strong {
    color: white;
  }
  
  .battle-log .rated {
    background: rgba(51, 51, 51, 0.8);
    border-style: solid;
    border-width: 0px 1px 1px 1px;
    border-color: #000;
    border-radius: 0px 0px 20px 20px;
    padding-bottom: 10px;
    margin-bottom: 15px;
    text-align: center;
  }
  
  .dark .battle-log .rated {
    background: #555555;
    border-color: #333;
    color: #DDD;
  }
  
  .battle-log .rated strong {
    border: 2px solid #718394;
    padding: 3px 4px 4px 4px;
    text-shadow: 0px 0px 1px #718394;
    box-shadow: 0px 0px 1px rgba(255, 255, 255, 0.9) inset
  }
  
  .battle-log .rated:hover strong {
    background: #97A9BC;
    text-shadow: 0px 0px 2px #718394;
    box-shadow: 0px 0px 3px rgba(255, 255, 255, 0.9) inset;
  }
  
  .battle-log div[class*="chatmessage"] {
    background: rgba(0, 0, 0, 0.05);
    border-radius: 20px;
    margin-bottom: 2px;
  }
  
  .battle-log .chat small::before {
    content: "-- ";
    margin-left: 15px;
    color: #275E94;
  }
  
  .battle-log em[style="color:#445566;display:block;"] {
    color: #275E94 !important;
    text-align: center;
  }
  
  .dark .battle-log em[style="color:#445566;display:block;"] {
    color: #637A91 !important;
  }
  
  .battle-log .utilichart, .dark .battle-log .utilichart, .dark .battle-log .message + .chat {
    background: rgba(51, 51, 51, 0.8);
    border-style: solid;
    border-width: 1px 1px 0px 1px;
    border-color: #000;
    border-radius: 20px 20px 0px 0px;
  }
  
  .battle-log .utilichart .numcol {
    padding-left: 5px;
    color: #CCC;
  }
  
  .battle-log .utilichart .pokemonnamecol a {
    color: #98C0E9;
  }
  
  .battle-log .utilichart .typecol img {
    opacity: 1;
  }
  
  .battle-log .utilichart .col {
    color: #EEE;
  }
  
  .battle-log .utilichart .labelcol em, .battle-log .utilichart .widelabelcol em, .battle-log .utilichart .pplabelcol em, .battle-log .utilichart .statcol em, .battle-log .utilichart .bstcol em {
    color: #AAA;
  }
  
  .battle-log .message + .chat, .dark .battle-log .message + .chat {
    background: rgba(51, 51, 51, 0.8);
    margin-bottom: 2px;
    border-style: solid;
    border-width: 0px 1px 1px 1px;
    border-color: #000;
    border-radius: 0px 0px 20px 20px;
    padding: 5px;
    color: #FFF;
    text-align: center;
  }
  
  .battle-log .message + .chat em {
    color: #FFF;
  }
  
  .battle-log .message + .chat font[color="#686868"] {
    color: #AAA;
  }
  
  .lstatbar {
    z-index: 1;
    left: 455px !important;
    top: 32px !important; 
  }
  
  .rstatbar {
    z-index: 1;
    left: 10px !important;
    top: 32px !important;   
  }
  
  .statbar .hpbar {
    background: #333;
    width: 151px;
    height: 12px;
    border: 1px solid #000;    
    box-shadow: 0px 0px 2px black inset;
  }
  
  .lstatbar .hpbar {
    border-radius: 0px 0px 20px 0px;
  }
  
  .rstatbar .hpbar {
    border-radius: 0px 0px 0px 20px;
  }
  
  .statbar .hpbar .prevhp {
    height: 12px;
    transition-duration: 0.5s;
    transition-timing-function: linear;
  }
  
  .statbar .hpbar .hp {
    height: 8px;
    transition-duration: 0.2s;
    transition-timing-function: linear;
  }
  
  .lstatbar .hpbar .prevhp {
    border-radius: 0px 0px 20px 0px;
  }
  
  .lstatbar .hpbar .hp {
    border-radius: 0px 0px 20px 0px;
  }
  
  .rstatbar .hpbar .prevhp {
    border-radius: 0px 0px 0px 20px;
  }
  
  .rstatbar .hpbar .hp {
    border-radius: 0px 0px 0px 20px;
  }
  
  .statbar .hpbar .hptext {
    background: #000;
    height: 16px;
  }
  
  .statbar .hpbar .hptextborder {
    border-color: #000;
  }
  
  .turn {
    background: rgba(71, 71, 71, 0.8);
    top: 0 !important;
    left: 275px !important;
    width: 90px;
    height: 80px;
    line-height: 40px;
    padding: 0px;
    border: none;
    border-bottom: 1px solid #000;
    border-radius: 0px;
    color: #AAA;
    text-align: center;
    text-shadow: 0px -1px #000;
    opacity: 1 !important;
    box-shadow: 0px 0px 2px #000;
  }
  
  .weather {
    text-shadow: none;
  }
  
  .weather em {    
    z-index: 1 !important;
    width: 80px;
    margin: 15px auto 0px auto;    
    color: #AAA;
    text-align: center;   
  }
  
  .trainer {
    top: 0;
    opacity: 1 !important;
  }

  .trainer div {
    opacity: 1 !important;
  }
  
  .leftbar {
    background: rgba(51, 51, 51, 0.8);
    width: 275px;
    height: 80px;
    border-bottom: 2px solid #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  .leftbar .trainer strong {
    float: right;
    margin-right: 5px;
    margin-top: 5px;
    color: #EEE;
    text-shadow: 0px -1px #000;
  }
  
  .leftbar .trainer .teamicons {
    float: left;
    position: relative;
    top: -80px;
    left: 0;
  }
  
  .leftbar .trainer .trainersprite {
    position: relative;
    top: 15px;
    left: 0px;
  }
     
  .leftbar .trainer div {
    margin-right: 0;
  }
    
  .rightbar {
    background: rgba(51, 51, 51, 0.8);
    width: 275px;
    height: 80px;
    border-bottom: 2px solid #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  .rightbar .trainer strong {
    float: left;
    margin-left: 5px;
    margin-top: 5px;
    color: #EEE;
    text-shadow: 0px -1px #000;
  }
  
  .rightbar .trainer .teamicons {
    float: right;
    position: relative;
    top: -80px;
    right: 0;
  }
  
  .rightbar .trainer .trainersprite {
    position: relative;
    top: 15px;
    right: 210px;
  }
  
  .rightbar .trainer div {
    margin-right: 0;
  }
  
  .messagebar {
    background: rgba(51, 51, 51,.5);
    bottom: 0;
    left: 0;
    right: 0;
    width: 100%;
    border-top: 2px solid #000;
    border-radius: 0px;
    color: black;
    text-align: center;
  }
  
  .messagebar strong {
    color: black;
  }
  
  .moveselect button {
    color: #7B80B8;
    text-shadow: 1px 1px #373952;
    padding-top: 0px;
    padding-left: 0px;
  }
  
  .movecontrols {
    background: rgba(51, 51, 51, 0.8);
    width: 230px;
    height: 120px;
    position: absolute;
    top: -150px;
    right: 10px;
    padding: 5px;
    padding-top: 0px;
    border-width: 1px;
    border-style: solid;
    border-color: #000;
    border-radius: 7px;
    box-shadow: 0px 0px 2px black inset;
    overflow: hidden;
  }
  
  .movemenu {
    background: #FCFAFB;
    width: calc(100% - 10px);
    padding: 5px;
    border-radius: 7px;
    box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);
    display: inherit;
  }
  
  .movemenu button {
    width: calc(50% - 4px);
    margin-right: 4px;
    font: 10pt/100% Verdana, sans-serif;
    position: relative;
    padding: 1px 0px 0px 0px !important;
  }
  
  .megaevo, .megaevo:hover {
    background: none;
    border: none;
    float: right;
    top: -115px;
    left: 32px;
    color: #252636;
    text-shadow: 0px 0px 1px #7B80B8;
    display: table-cell !important;
    vertical-align: middle !important;
  }
  
  .switchselect button {
    color: #FA4040;
  }
  
  .switchmenu button::before {
    content: url(http://i.imgur.com/2wCxQO5.png?1);
    position: absolute;
    top: -22px;
    left: 10px;
    width: 80px;
  }
  
  .switchmenu button {
    background: rgba(51, 51, 51, 0.8);
    border-color: #000;
    box-shadow: 0px 0px 2px black inset;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    overflow: visible;
    margin-bottom: 20px;
  }
  
  .switchmenu button:hover {
    background: rgba(31, 31, 31, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  .switchmenu button .hpbar, .switchmenu button .status {
    border-color: #000;
  }
  
  .switchmenu button:hover {
    background: rgba(51, 51, 51, 0.9);
    border-color: #000;
  }
  
  .switchmenu button.disabled, .switchmenu button:disabled {
    background: rgba(101, 101, 101, 0.5) !important;
    border-color: #000 !important;
    color: #FFF !important;
  }
  
  .switchmenu button.disabled::before, .switchmenu button:disabled::before {
    content: '';
  }
  
  .battle-log .chat strong, .dark .battle-log .chat strong {
    color: #275E94;
  }
  
  .small-layout .battle-chat-toggle {
    background: rgba(51, 51, 51, 0.8);
    padding: 3px 8px;
    border: 1px solid black;
    border-radius: 3px;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  .small-layout .battle-chat-toggle:hover {
    background: rgba(31, 31, 31, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  .small-layout .battle-chat-toggle {
    z-index: 1;
  }
  
  .battle-controls .whatdo {
    color: #CCC;
  }
  
  .battle-controls .whatdo small {
    border-color: #000;
    color: #000;
  }
  
  .dark .battle-log .message-throttle-notice, .dark .battle-log .message-error {
    color: #992222;
  }
  
  div[id*="battle"].ps-popup {
    background: #333;
    border-color: #000;
    box-shadow: 0px 0px 2px black;
    color: #DDD;
  }
  
  div[id*="battle"].ps-popup a {
    color: #98C0E9;
  }
  
  div[id*="battle"].ps-popup button {
    background: rgba(31, 31, 31, 0.8);
    padding: 3px 8px;
    border: 1px solid black;
    border-radius: 3px;
    color: #EEE;
    text-shadow: 0px 0px 2px #000;
    box-shadow: 0px 0px 2px black inset;
  }
  
  div[id*="battle"].ps-popup button:hover {
    background: rgba(17, 17, 17, 0.8);
    border: 1px solid black;
    box-shadow: 0px 0px 2px black;
  }
  
  #tooltipwrapper {
    text-align: left;
    color: #DDD;
    text-shadow: 0px 0px 2px #000;
  }

  #tooltipwrapper .tooltip {
    width: 300px;
    background: #333;    
    border-color: #000;
    border-radius: 3px;
    box-shadow: 0px 0px 2px black inset, 0px 0px 5px rgba(0, 0, 0, 0.4);
  }

  #tooltipwrapper .tooltip h2 {
    padding: 8px 15px;
    margin: 0;
    border-bottom: 1px solid #000;
    font-size: 10pt;
    color: #FFF;
    text-shadow: 0px 0px 2px #000;
  }

  #tooltipwrapper .tooltip p {
    padding: 4px 8px;
    margin: 0;
    font-size: 8pt;
  }

  #tooltipwrapper .tooltip p.section {
    border-top: 1px solid #000;
  }
}
