css:
@-moz-document domain("userstyles.org") {
body {
 background:#efefef; 
}
    #main-header {
    font-family: Trebuchet MS;
    background-color: #ffec8b;
    margin-bottom: 10px;
    margin-left: 5px;
    height: 100%;
    border-radius: 5px 5px 0px 0px;
    padding: 10px;
    box-shadow: 0 1px 2px #ccc;
}
  #main-article {
    margin-left: 190px;
    padding: 10px;
    background: white;
    box-shadow: 0 1px 2px #ccc;
}
  #front-page-best {
    display: inline-block;
    max-width: 45%;
    vertical-align: top;
    background: white;
    padding: 10px;
    border-right:1px solid #efefef;
}
#front-page-newest {
    display: inline-block;
    max-width: 45%;
    vertical-align: top;
    background: white;
    padding: 10px;
}
.install {
    background-color: #88acc6 !important;
    cursor: pointer;
    background-image: linear-gradient(#88acc6,#88acc6);
}
.install:hover {
background:#62ACC4;
}
.install-info {
    background-color: #62ACC4 !important;
    background-image: -moz-linear-gradient(#62ACC4,#62ACC4);
    cursor: default;
}
a {
	color:#227D9C;
	text-decoration:none;
}
a:hover {
	-webkit-transition: all 0.3s ease 0s;
	-moz-transition: all 0.3s ease 0s;
	-ms-transition: all 0.3s ease 0s;
	-o-transition: all 0.3s ease 0s;
	transition: all 0.3s ease 0s;
        color:#9043cc;
}
a:visited {
    color:#227D9C;
    text-decoration:none;  
}
#left-sidebar>* {
    background-color: #d1eeee;
    border-radius: 5px;
    margin: 5px;
    padding: 5px;
    box-shadow: 0 1px 2px #ccc;
}
#screenshots {
    margin-bottom: 10px;
    border: 1px solid #DDD;
    padding: 5px;
    border-radius: 5px;
    min-height: 100px;
    background: #DDD;
}
#info-tables {
    clear: left;
    border-bottom: 1px solid #efefef;
    padding: 5px;
}
}
