css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(https://bittybot.xyz/) {
   
#leaderboard {
   float:right;
   display:inline-block;
   width:200px;
   margin-top:-33px;
   font-size: 13px;
}

#header_image {
   float:left;
   width:100px;
   border-radius:5000px;
   display:inline-block;
}

#header_text {
   display:inline-block;
}

#header {
}

.right {
   float:right;
}

img.right {
   border-radius:10px;
}

a, a:active, a:visited {
   color:#0E7496 !important;
}

a:hover {
   text-decoration: underline;
}

#invite {
   padding:0px;
   box-sizing:border-box;
   height: 50px;
   font-weight:bold;
   background: #333333;
   text-decoration:none;
}

small {
   color:lightgray;
}

#mw-panel {
   width: 200px !important;
   background: none;
}

a:hover {
   color: lightblue;
}

i3 {
   color: gold;
}


b {
   color: whitesmoke;
}


#mp-left, .MainPageBG, #mp-left div, #mp-right div, #mp-topbanner div, .list,.collapsible th, .autocollapse th {
   color: #ddd !important;
   background-color: #333 !important;

}


body {
   background-color: #333333 !important;
   color: #ddd;
   font-size: 12px;	
}

p {
   font-weight: 200 !important;
   line-height: 1.9 !important;
}

.Boxmerge {
   border: 0px !important;
}

#footer-info-copyright, #footer-info-lastmod {
   color: #777 !important;
}

.portal, .portal table {
   background: transparent !important;
}

pre {
   margin:0px;
   max-width:1110px;
   margin:auto;
}

#container {
   display:inline-block;
   max-width:800px;
   margin-top:-20px;
   float:left;
}

#thanks {
   max-width:800px !important;
   margin:auto;
}

}
