css:
@-moz-document domain("whatsapp.com") {
/*===Minimum size===*/
.app-wrapper-main {
    min-height: 200px !important;
}
@media screen and (max-height:500px){
    .app-wrapper-main {
        top: 0;
    }
}

/*===Changing background===*/
.pane-chat-tile-container {
    background-image: url("http://hdwallpaperbackgrounds.net/wp-content/uploads/2016/05/Abstract-Background.jpg");
    background-size: auto 112%;
    /**filter: blur(0px);
    transition: filter 0.2s;
}
#side:hover ~ #main .pane-chat-tile-container {
    filter: blur(2px);
    transition: filter 1s;/**/
}

/*===Introduction===*/
.intro-text-container h1:before {
    content: 'Welcome to';
    font-size: 32px;
}
.intro-text-container h1:after {
    display: block;
    content: 'WhatsApp Web';
    font-size: 32px;
}
.intro-text-container h1 {
    font-size: 0;
}
.intro-text-container .intro-text:before {
    content: 'You are using this web app with the WhatsApp\AStylish sheet version 1.2 by downquarks.';
    white-space: pre;
    font-size: 14px;
    line-height: 20px;
}
.intro-text-container .intro-text:after {
    display: block;
    content: 'Your phone needs to be connected to the internet (wifi recommended).';
    font-size: 14px;
    line-height: 20px;
}
.intro-text-container .intro-text {
    font-size: 0;
}

/*===Search bar on top===*/
.butterbar-container {
    display: none;
}
.pane-subheader {
    z-index: 50;
    background-color: rgba(251, 251, 251, 0.1);
    border-bottom: none;
    box-shadow: inset 0 50px 50px -25px rgba(251, 251, 251, 1);
    
    margin-left: 5.5px;
    right: 5.5px;
}
.cont-input-search {
    box-shadow: 0 5px 10px 5px rgba(251, 251, 251, 0.4);
}
.pane-body {
    margin-top: -60px;
    padding-top: 60px;
}

/*===new features in 1.1===*/
.pane-chat-footer {
    background-color: rgba(200, 200, 200, .5);
}
.chat-drag-cover .chat .chat-avatar .avatar {
    transition: transform 5s, border-radius 5s;
}
.chat-drag-cover .chat .chat-avatar .avatar-image {
    transition: transform 5s, border-radius 5s;
}
.chat-drag-cover:hover .chat .chat-avatar .avatar,
.chat-drag-cover .active.chat .chat-avatar .avatar {
    transform: rotate(-10deg);
    border-radius: 40% !important;
}
.chat-drag-cover:hover .chat .chat-avatar .avatar-image,
.chat-drag-cover .active.chat .chat-avatar .avatar-image {
    transform: rotate(15deg);
    border-radius: 40% !important;
}
.message-list {
    padding-bottom: 55px;
}
.emojik {
    transition: transform 0.2s, opacity 0.2s;
}
.emojik:hover {
    transform: scale(1.2);
}
.emojik:active {
    transform: scale(0.9);
    opacity: 0.8;
}
/*.emoji-panel {
    height: 300px !important;
    min-height: 234px;
}
.emoji-panel-body {
    height: 100%;
    padding-bottom: 55px;
}*/


/*===new features 1.2===*/
.popup {
    border-top: 4px solid #f65;
    border-bottom: 4px solid #f65;
    border-right: 1px solid white;
    border-left: 1px solid white;
}
.subheader-search:after {
    display: none;
}
.message-out {
    background-color: #bfa;
}
.message-out:before {
    filter: saturate(2.5);
}
.pane-chat-body::-webkit-scrollbar-thumb {
    background-color: rgba(0, 0, 0, 0.5);
}
.chatlist .active.chat {
    background-color: #d6dbdf;
}
.pane-chat-header {
    background-color: rgba(240, 240, 240, 0.6);
}
.intro-image {
    filter: saturate(1.2);
}

}
