css:
@-moz-document url-prefix("http://www.ohmydollz.com/forum/") {
#contenu {
    background: url("http://data.nimages.fr/4qi.png") no-repeat fixed 50% 0 rgba(0, 0, 0, 0);
    height: 100%;
    margin: 0;
    padding: 3px 0;
    width: 100%;
}
/*HEADER*/

#head_forum {
    background-image: url("http://data.nimages.fr/zo4.png");
    background-repeat: no-repeat;
    height: 300px;
    position: relative;
    width: 950px;
}
#head_links {
    bottom: 135px;
    left: 555px;
    position: absolute;
}
#head_links a {
    color: #B9B9B9;
    font-size: 13px;
    text-decoration: none;
}
#head_links a:hover {
    color: #FFF;
}
#brdmenu {
    background-image: url("http://data.nimages.fr/1ij4.png");
    background-repeat: no-repeat;
    height: 30px;
}
#footer_box {
    background-image: url("http://data.nimages.fr/0xs5.png");
    background-repeat: no-repeat;
    height: 100px;
    width: 950px;
}

#footer_box p, #footer_box a {
    color: #053D39;
    font-weight: bold;
    margin-top: 45px;
    padding-right: 12px;
    right: 0;
}

#brdwelcome {
    background-image: url("http://data.nimages.fr/2etc.png");
    background-repeat: no-repeat;
    color: #B3A690;
    font-weight: bold;
    height: 60px;
    padding: 10px;
    width: 950px;
}
.pun H2,
#brdmenu {
    background-image: url("http://data.nimages.fr/1ij4.png");
    background-repeat: no-repeat;
}
.pun {
    color: #698C85;
}
DIV.box, .pun BLOCKQUOTE, DIV.codebox, #adminconsole FIELDSET TH, .rowodd, .roweven {
    background-color: #0D352D;
}
.pun BLOCKQUOTE {
    border: 1px solid #c6af8e;
}
.box {
    border: 1px solid #09231E;
}
.pun A:link, .pun A:visited {
    color: #8ED0C3;
}
.pun A:hover {
    color: #FFFFFF;
}
DIV.block DIV.inbox, DIV.blockmenu DIV.inbox {
    padding: 0 6px;
    color: #A7977E;
}
.pun TH {
    background-image: url("http://data.nimages.fr/3syf.png");
    background-repeat: no-repeat;
    color: #C3AB84;
    font-size: 13px;
    height: 100%;
    text-align: center;
}
.inbox tr {
    border: 1px solid #F6F2EE;
}
.pun .tcr .byuser {
    color: #CEC2BF;
}
TD.tc2, TD.tc3, TD.tcmod, #postpreview, #viewprofile DD, DIV.forminfo, #adminconsole FIELDSET TD, DIV.blockmenu DIV.box, #adstats DD {
    background-color: #0D352D;
}
TD DIV.tclcon {
    color: #9B9391;
    float: left;
    margin-left: 10px;
    width: 475px;
}

.pun .tcr {
    border-right: 1px solid #BFAA9B;
}
DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {
    background-color: #435C65;
    color: #7B8292;
}

DIV.blockpost DIV.box {
    background-color: #0F3830;
}

DIV.postleft, DIV.postfootleft, DIV.blockpost LABEL {
    background-color: #435C65;
    color: #F5ECDC;
}

DIV.postright, DIV.postfootright {
    background-color: #0F3830;
}

.pun BLOCKQUOTE {
    background-color: #C1C1C1;
}
  
DIV.postright, DIV.postfootright {
    border-left: 18em solid #435C65;
}


DIV.postleft DT, DIV.postmsg H4, TD.tcl H3, DIV.forminfo H3, P.postlink, DIV.linkst LI, DIV.linksb LI, DIV.postlinksb LI, .blockmenu LI, #brdtitle H1, .pun SPAN.warntext, .pun P.warntext {
    font-weight: bold;
    color: #032C28;
}

DIV.icon {
    background-image: url("http://data.nimages.fr/23l7.png") !important;
}
DIV.inew {
    background-image: url("http://data.nimages.fr/3gtz.png") !important;
}
DIV.blockpost H2 SPAN.conr {
    color: #3F498E;
    text-align: right;
}
.pun H2 A:link, .pun H2 A:visited {
    color: #FFF4E1;
}
DIV.linkst .conl,
DIV.linksb .conl,
DIV.postlinksb .conl {
    width: 18em;
    color: #8E6D6A;
}
}
