css:
@-moz-document url-prefix("https://fantasy.premierleague.com/a/team/my") {

/*
 @name        anjal
 @include     https://fantasy.premierleague.com/a/team/my
 @include     https://fantasy.premierleague.com/a/squad/transfers
 @version     1.0.1 */

#mainContent{
	margin-top: -4.8rem;
}

#ismr-main{
    padding-top :0rem;
	margin-top: 3.8rem;
}

#ismr-scoreboard{
	transform: scale(0.8);
}

.ism .ism-section-title{
	font-size: 1.8rem;
	margin-bottom:10px;
}

#ismr-chips{
	transform: scale(0.8);
    margin-top: -0.625rem;
}

.ism .ism-header {
    transform: scale(0.8);
    margin-top: -2.625rem;
    padding-top: -2.625rem;   
}

.ism .ism-container {
    transform: scale(0.9);
    margin-top: -10rem;
}

.ism #ismr-side.ism-sidebar{
    margin-top: -14rem;
    transform: scale(0.82);
}

.ism #ismr-buttons{
	margin-top: 2rem;
    transform: scale(0.8);
}

.ism .ism-squad-wrapper {
    margin-top: -7.4rem;
    margin-bottom: -7.4rem;
    padding-top: 0.8rem;
    transform: scale(0.8);
    font-size:6rem;
}

.ism .ism-element-row--pitch {
    padding-bottom: 0.625rem;
    padding-left: 0.625rem;
    max-width:80rem;
}


.ism .ism-pitch {
    min-height: 485px;
    max-height: 485px;
    background-size: 895px 510px;
    padding-left: 80px;
    padding-right: 80px;
    background-image: url(http://i.imgur.com/SLXDa5X.png)
}
.ism .ism-element__shirt {
    padding-bottom: .6rem;
}
.ism .ism-element__name {
    padding-top: .2em;
    padding-bottom: .2em;
}
.ism .ismr-scoreboard {
    transform: scale(0.8);
    width : 500px;
}

.ism #ismr-summary-starters.ism-pitch{
 padding-top: 0.825rem;
}


.ism .ism-bench__unit {
    padding-top: .5rem;
}
.ism .ism-element picture {
    display: block;
    -webkit-transform: scale(0.6);
    transform: scale(0.6);
    margin-top: -0.625rem;
    margin-bottom: -0.625rem;
}

.ism .ism-bench {
    padding-top: 0.625rem;
}
.ism .ism-element__name {
    background-color: #09562c;
    font-size:15px;
    line-height:15px;
    margin-top: -1.8rem;
    
}
.ism .ism-element__data {
    font-size:14px;
    line-height:14px;
}

#ismr-success:empty {
    display: none;
}

.ism .ism-fixtures{
	margin-top: -1.5rem;
}

#ismr-fixtures-table{
	transform: scale(0.8);
    margin-top: -6rem;
}


.ism #ismr-buttons{
 margin-top: -3.8rem;
}

.mainFooter{
margin-top : -20rem;
}

}

