css:
  @-moz-document domain("weheartit.com") {
/* dashboard */

body {
    background-image: url(http://static.tumblr.com/bmdsqsc/2Wcm7uqoa/tumblr_luvodahgub1r05h8d.jpg);
    padding-top: 56px;
}

html, body {
    color: #000;
    font-family: arial;
    font-size: 13px;
}

.bg-primary {
    background-color: transparent !important;
    color: #fff;
}
.text-primary {
    color: #f0f0f0 !important;
}
.tabs-light .active>a {
    box-shadow: 0 -2px 0 gray inset;
    color: #fff;
}
.panel {
    background-image: url(http://static.tumblr.com/bmdsqsc/2Wcm7uqoa/tumblr_luvodahgub1r05h8d.jpg) !important;
}
.e-section {
    background: rgba(255, 255, 255, 0.35) !important;
}
.e-box {
    background: rgba(255, 255, 255, 0.35) !important;
}
.entry-similar .heading {
    background: rgba(0, 0, 0, 0.35)!important;
}
.floating-window .floating-window-content {
    background: rgba(0, 0, 0, 100)!important;
}
.icon-web.icon-primary {
    background-image: url(https://s31.postimg.org/gl5c3l863/Untitled.png);
}
.icon-upload.icon-primary {
    background-image: url(https://s31.postimg.org/rfd4uy5y3/Untitled.png);
}
/* perfil */

.header-content .avatar {
    box-shadow: 0 0 0 4px rgba(0, 0, 0, 0.35)!important;
    background: rgba(0, 0, 0, 100)!important;
}
/* poscards */

.bg-offwhite {
    background-color:transparent !important;
}
.list-tabs .active {
    border-right: 2px solid gray;
}
  }
