css:
@-moz-document url-prefix("http://forums.nrvnqsr.com/") {
  
  html {
	background:#0d0d0d  !important;
	}
  
	.body_wrapper {
	background: #0d0d0d !important;
}
	
	.announcerow .announceinfo, .announcerow .announcement {
	background: none !important;
	_background-image: none !important;
}
	
	.forumbit_nopost .subforumdescription {
	color: white !important;
	background: none !important;
}
	
	.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
	color: white !important;
	background: #1a1a1a !important;;
	_background-image: none;
	
}
	
	
	body a {
	color: #ff9933 !important;
	text-decoration:none;
}
	
	
	.navtabs {
	background:none !important;
}
	
	
	.navtabs ul {
/*	height: 26px; */
	position:absolute;
	top:26px;
	left:0px;
	width:100%;
	border-top:1px solid #5a7f97;
	-moz-border-radius-bottomleft:5px;
	-moz-border-radius-bottomright:5px;
	-webkit-border-bottom-left-radius:5px;
	-webkit-border-bottom-right-radius:5px;
	border-bottom-left-radius:5px;
	border-bottom-right-radius:5px;
	margin-right:-80px;
/* This is to fix RTL menu issue under Opera */
	direction:ltr;
}
	
	
	.navtabs li.selected a.navtab {
	color:rgb(255, 255, 255);
	background:#1a1a1a !important;
	position:relative;
	top:-6;
	padding-top:6;
	z-index:10;
}
	
	
	.navtabs li a.navtab {
	border-top: 1px solid #Cedfeb;
	border-left: 1px solid #Cedfeb;
	display:block;
	background:#1a1a1a !important;
	min-width:60px;
	width:auto !important;
	width:60px;
	text-align:center;
	color: #ffffff;
	font:  bold 12px Arial, Calibri, Verdana, Geneva, sans-serif;
	text-decoration:none;
	line-height:26px;
	padding: 0 10px;
}
	
	
	.navbar_search input {background: #1a1a1a; color: #ffffff;
	}
	
	
	.globalsearch .textboxcontainer span {
	border-top: 0px solid none !important;
	border-left: 0px solid none !important;
	display: inline-block;
	height: 22px;
	padding-left: 2px;
	padding-right: 2px;
	padding-top: 3px;
	margin-bottom: 1px;
	width: 147px;
}
	
	
	.announcements .announcerow {
	background: none !important;
	_background-image: none !important;
	margin-top: 0px;
	display:block;
	float: left;
	clear:both;
	width: 100%;

	-moz-box-shadow: 0px 0px 0px none !important;
	-webkit-box-shadow: 0px 0px 0px none !important;
	box-shadow: 0px 0px 0px none !important;

	padding: 5px 0;
}
	
	

	
	.threadbit .sticky{
	background: #1a1a1a !important;
	border: 1px solid #5a7f97;
	border-top: 0;
	width: 100%;
	float: left;
	display:block;
	clear:both;
	position: relative;
}

	
	.threadbit .alt {
	background: none !important;
}

	
	.threadbit .nonsticky, .threadbit .discussionrow {
	background: #1a1a1a !important;
}
	
	
	
}
