css:
@-moz-document domain("eldarya.fr"), domain("fa1.eldarya.com"), domain("www.eldarya.ru"), domain("eldarya.com"), domain("eldarya.com.br"), domain("eldarya.de"), domain("eldarya.es"), domain("eldarya.fi"), domain("eldarya.hu"), domain("eldarya.it"), domain("eldarya.pl") {
#header #header-top {
    background: url("https://s9.postimg.cc/ombmmj14f/image.png") fixed top center;
}
#footer {
    background: url("https://s9.postimg.cc/ombmmj14f/image.png");
}
body.night {
    background: #4da8fd url("https://s9.postimg.cc/fd9gcdhxr/image.png") fixed top center;
    background-size: cover;
}
body {
    background: #34386f url("https://s9.postimg.cc/fd9gcdhxr/image.png") fixed top center;
    background-size: cover;
    overflow-y: scroll;
    transition: background-image 4s linear;
}
#player-infos .player-currencies {
    border: solid 2px rgb(184, 111, 197);
}
#player-infos .player-currencies {
    background-color: rgb(112, 46, 140);
}
#player-infos .player-name {
    border: solid 2px #bf96ef;
    background-color: #4a227b;
}
#index-news {
    background-color: #702e8c;
    border: 2px solid rgb(174, 152, 206);
}
#news-list {
    background: linear-gradient(0deg, rgb(158, 109, 204) 0%, #e6e0ff 100%);
}
#index-news .date {
    background-color: #8c48b9;
}
#index-news .text a {
    color: rgb(87, 20, 115);
}
#news-list .news + .news {
    border-top: 2px solid rgb(158, 104, 192);
}
#container .page-main-container {
    background-color: rgba(101, 74, 134, 0.52);
    box-shadow: inset 1px 1px 3px 1px #69346f;
}
#container .page-main-container:after {
    background-image: linear-gradient(180deg, #fff 4%, #d2d5f9 14%, #bb3fd0 74%, #c4c7f7 90%, #fff 97%), linear-gradient(180deg, #fff 4%, #d2d5f9 14%, #983fd0 74%, #c4c7f7 90%, #fff 97%), linear-gradient(#fff, #fff), linear-gradient(#fff, #fff);
    box-shadow: 1px 2px 3px 0 rgba(47, 62, 113, 0.75);
}
#main-menu ul:after {
    background-image: linear-gradient(180deg, #fff 4%, #d2d5f9 14%, #853fd0 74%, #c4c7f7 90%, #fff 97%), linear-gradient(180deg, #fff 4%, #d2d5f9 14%, #983fd0 74%, #c4c7f7 90%, #fff 97%), linear-gradient(#fff, #fff), linear-gradient(#fff, #fff);
}
#main-menu ul {
    background-color: rgba(150, 124, 195, 0.54);
    box-shadow: inset 1px 1px 3px 1px #34386f;
}
#ranking-guards .ranking-guard-position {
    background: url('https://s9.postimg.cc/lxc8j2qxb/image.png');
}
#ranking-guards li.bronze .ranking-guard-position,
#ranking-guards li.bronze .ranking-guard-caption p {
    color: rgb(202, 187, 245);
}
#ranking-top-players .ranking-top-players-number {
    box-shadow: 0px 0px 2px #6f3459;
    background-color: rgba(46, 0, 53, 0.9);
    border: 2px solid rgb(123, 35, 226);
}
#container h1 {
    background-image: url("https://s9.postimg.cc/6ras5q5nz/image.png");
}
#container h1 .title-end {
    background-image: url("https://s9.postimg.cc/uia5nudkv/image.png");
}
#pet-info-main {
    background-color: #362050;
}
.flavr-button,
.button.button {
    background-color: #7140ad;
    border: 2px solid #62116d;
}
#treasurehunt-fold {
    background: #8c6abf;
}
Style Attribute {
    color: rgb(30, 22, 45);
}
#treasurehunt-container h2 {
    color: white;
    border-right: 1px solid #bd9bf1;
}
#main-bonus-progressbar {
    background-color: rgba(38, 16, 66, 0.47);
}
#main-bonus-progressbar > div.open:not(.transition_bonus) {
    border: 2px solid #d65fff;
}
#main-bonus-progressbar > div.open > .label,
#main-bonus-progressbar > div.transition_bonus.open {
    background-color: #7a41a9;
}
.flavr-button.purple,
.button.button.button-purple {
    background-color: #862c9e;
    border-color: #d69bff;
}
#main-bonus-progressbar > div.current > .label,
#main-bonus-progressbar > div.transition_bonus.current {
    background-color: #a95ba3;
}
#main-bonus-progressbar > div.current:not(.transition_bonus) {
    border: 2px solid #c744bd;
}
#pet-info-food img {
    border: 1px solid #7140ad;
    background: #d3bde6;
}
#player-info-level {
    background-image: url(https://s9.postimg.cc/5auo9q31r/image.png);
}
#player-info-dots .player-info-dot#dot5 {
    top: 60px;
    right: 18px;
}
#player-info-dots .player-info-dot.lit {
    background-image: url(https://s9.postimg.cc/w8olbusy7/image.png);
}
#header-portraits > div {
    position: absolute;
}
#player-headerPortrait {
    background-image: url(https://s9.postimg.cc/aelxy8r3z/image.png);
}
}
