css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.qwant.com") {
    
    element.style {
        background: #29303b;
    }
    body {
        background: #29303b !important
    }
    .result__favicon {
        background: transparent;
    }
    html {
        background: #29303b;
    }
    .footer {
        background: #383c4a;
    }
    .sidebar {
        background: #383c4a;
    }
    .autocomplete__input {
        color: #b5b5b5;
        background: rgba(29, 38, 47, .10);
    }
    .results-column--knowledge-ribbon {
        background-color: #383c4a;
    }
    .results-page--all .ribbon__tabs__tab.ribbon__tabs__tab--selected {
        color: #dadada;
        border-bottom: 3px solid #cc575d;
    }
    .knowledge-ribbon__title {
        font-family: Ubuntu;
        color: #dadada;
    }
    .knowledge-ribbon__description {
        font-family: Ubuntu;
        line-height: 21px;
        color: #b5b5b5;
    }
    .knowledge-ribbon__url {
        color: #dadada;
    }
    .knowledge-ribbon__url:hover {
        color: #dadada;
    }
    .results-page h2 a {
        color: #b5b5b5;
    }
    .results-page h2 {
        font-family: Ubuntu;
        color: #dadada;
    }
    .result h3 a:visited {
        color: #714ea1;
    }
    .result h3 a {
        color: #5294e2;
    }
    .results-column--images h2 {
        background: #383c4a;
    }
    .results-column--images h2 {
        color: #dadada;
    }
    .result_filter__item__label {
        color: #dadada;
    }
    .result_filter_dropdown .wrapper-dropdown .select-placeholder {
        color: #b5b5b5;
    }
    .wrapper-dropdown .icon-dropdown {
        color: #b5b5b5;
    }
    .snippet-ff {
        background: none;
        color: #b5b5b5;
    }
    .autocomplete__dropdown p.autocomplete_dropdown_item {
        background: #383c4a;
        color: #dadada;
    }
    .autocomplete__dropdown p.autocomplete_dropdown_item:hover {
        background: #383c4a;
        color: #dadada;
    }
    .autocomplete__dropdown {
        border-top: 2px solid #cc575d;
        background: #353f49;
        box-shadow: 0 1px 4px #1d252e;
    }
    .search_bar .search_bar__form__input {
        color: #858d98;
        background: rgba(255, 255, 255, .02);
    }
    .home__links__tab {
        color: #757b81;
    }
    .home__links__tab:hover {
        color: #515151;
    }
    .home__links__tab--active {
        color: #515151;
    }
    .trends a {
        color: #515151;
    }
    .result_actions__circle {
        fill: rgba(255, 255, 255, .2);
    }
    .result_filters_container__filters .result_filter__item__label {
        color: #dadada;
    }
    .result_filters_container__filters .wrapper-list li a.selected {
        color: #909aa7;
    }
    .result_filters_container__filters .wrapper-list li a, .result_filters_container__filters .wrapper-list li label {
        color: #727a86;
    }
    .header__user__icon_settings .icon {
        color: #909aa7;
    }
    .header__user__icon_settings, .header__user__language, .header__user__login {
        color: #909aa7;
    }
    .result_filter__item--dropdown .result_filter__item__label {
        color: #909aa7;
    }
    .wrapper-dropdown {
        color: #676c71;
    }
    .results-column--videos h2 {
        background: #424757;
    }
    .result_filter__item--dropdown .result_filter__item__label {
        color: #dadada;
    }
    .form_page {
        background: #383c4a;
    }
    .form_page h1 {
        color: rgba(194, 194, 194, 0.7);
    }
    .alert__item {
        background: #383c4a;
        box-shadow: 0 0 8px rgba(0, 0, 0, .20);
        border: 1px solid #383c3f;
    }

    .alert__item__text {
        color: #838990;
    }
}
