css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("allmusic.com") {

/* Hacks and Fixes */
    
.ctoolbar-desktop.tb-solid, .tb-toggle, .header-leaderboard, .sidebar .advertising, .sidebar .promo, .sidebar .sidebar-promo, div.saambaa_small_video {
height: 0px !important;
visibility: hidden !important;
margin-bottom: 0px;    
}
    
.filters {
    overflow: hidden;    
}
    
/* Fonts */    
    
html, body, .basic-info .release-date h4, .basic-info .duration h4, .basic-info .genre h4, .basic-info .styles h4, .basic-info .recording-date h4, .basic-info .writer h4, .basic-info .recording-location h4, .basic-info .active-dates h4, .basic-info .birth h4, .basic-info .death h4, .basic-info .aliases h4, .basic-info .group-members h4, .basic-info .member-of h4, .basic-info .period h4, .basic-info .label h4, .basic-info .format h4, .basic-info .duration h4, .basic-info .release-info h4, .basic-info .catalog-number h4, .basic-info .featured-artist h4, .content-container .content section .headline, section.song-highlights table thead tr, section.song-highlights table tbody tr td.performer a, .moods h4, .themes h4, .content section table thead tr, .content section.track-listing table tbody .composer a, .content section.track-listing table tbody .primary a, .content section.track-listing table tbody .featuring a, .content-container .top-headline, .discover-adv-search .adv-search-section input, .sidebar .discography-browser h4 a, section.user_review h3.review-author.headline a, section.user_review h3.review-author.headline a:hover, .content section table thead tr a, .content section.similar-albums nav.view-type .condensed, .content section.similar-albums nav.view-type .full, section.recent-releases ul li .rating, .content section table thead tr th a, .content-container .featured .styles a, .module .headline, section.all-new-releases table tbody .artist a, section.all-new-releases table tbody .genre a, section.all-new-releases .filters .filter-by, .module .headline .main, .featured-new-releases .new-release .genres a {
color: #bbb; 
}

/* Backgrounds */    
    
.row, .mood-container, .theme-container {
    background: none;
    background-image: none;
}
    
.overflow-container, .stripe-container.rotating-banner, .stripe-container.footer-leaderboard {
    background-repeat: repeat-x;
    background-color: rgb(0, 0, 0);
}

.overflow-container .content-container {
    width: 960px;
    overflow: hidden;
    margin: 0px auto;
    padding: 18px 10px 0px;
    background-repeat: repeat-x;
    background-color: rgb(0, 0, 0);
}

.filters li.active, .tabs .tab {
    background-color: rgb(120, 130, 140);
}

.sidebar section, .sidebar .module, .sidebar > div, .module, .staff-picks, .staff-picks .headline, .results, .content-container .featured, .content-container section.featured-new-releases, .content section, .content .editors-choice, .content-container .content section, .overflow-container.advanced-search #cmn_wrap .content-container .content, .pull-down, .overflow-container.all_newreleases .content-container .content, .overflow-container.genres .content-container .content, .overflow-container.all_moods .content-container .content, .overflow-container.all_themes .content-container .content {
    background-color: rgb(60, 70, 80);
    background-image: none;
}

.pick, .content .editorschoice {
    background: transparent -moz-linear-gradient(right center , rgba(90, 100, 110, 0) 10%, #456 100%) repeat scroll 0% 0%;
}    
    
.read-more-text {
    background-color: rgb(70, 80, 90) !important;
    border-top: 1px solid rgb(72, 82, 92) !important;
}
    
.read-more.collapsed .read-more-toggle {
    background: transparent -moz-linear-gradient(center top , rgba(60, 70, 80, 0) 0%, #456 60%) repeat scroll 0% 0%;
}    

section.all-new-releases .filters .filter-by  {
    background-color: rgb(70, 80, 90);
}
    
.content section.discography .release-types, .filters hr, .content-container .jump-to, .content-container .filter-by, .filters-month, .filters-genre, .content .results .intro-text, section.all-new-releases .filters div {
    background-color: rgb(75, 85, 95);
}

/* Shadows */ 
    
.filter .text-filter {
    box-shadow: 0px 12px 12px -2px rgba(60, 70, 80, 0.9);    
}

.pull-down {
    box-shadow: 0px -12px 12px -2px  rgba(60, 70, 80, 0.9);
}  
    
/* Borders */     
    
.content .theme-discover, .content .mood-discover, .content .style-discover, .filters-month {
    border: 0px !important;
}

.sidebar section, .sidebar .module, .sidebar > div, .module, .staff-picks, .discover-adv-search .adv-search-section input, .filter .text-filter input, .filter.artists input, .content-container section.featured-new-releases, .overflow-container.advanced-search #cmn_wrap .content-container .content, .overflow-container.album .content section, .overflow-container.descriptor .content-container .content section, section.editors-choice-content, .overflow-container.all_newreleases .content-container .content, .overflow-container.genres .content-container .content, .overflow-container.all_moods .content-container .content, .overflow-container.all_themes .content-container .content, .overflow-container.mood .content-container .content section, .overflow-container.artist .content-container .content section, .module.home-adv-search .adv-search-section input, .overflow-container.search .results {
    border: 1px solid rgb(72, 82, 92);
}
    
.tabs .tab {
    border: 1px solid rgb(110, 120, 130);    
}    
    
.content section.track-listing table tbody tr.pick td.pick-prefix, .content section.track-listing table tbody tr.pick td.pick-suffix, .content section.discography table tbody tr.pick td.pick-prefix, .content section.discography table tbody tr.pick td.pick-suffix {
    border-bottom: 1px solid rgb(60, 70, 80);
    border-top: 1px solid rgb(60, 70, 80);
}    
    
.content section.discography .release-types, .featured-new-releases .new-release .rating.allmusic, .module-pagination, section.recent-releases ul li .rating.allmusic, .content-container .featured .rating.allmusic, div.discover-genre .genre-info, .editors-choices .row:first-child, .content .editors-choice .rating.allmusic, .content table, section.all-new-releases table thead tr {
    border-top: 1px solid rgb(72, 82, 92);
}

div.pull-down.hand-cursor {
    border-top: 1px solid rgb(72, 82, 92) !important;
}    

.content section .headline, .content section.discography table thead tr th, .content section.discography table tbody tr td, .partners h4, .partners .partner-buttons, .content section.discography .release-types, .module .headline, .module.home-adv-search .adv-search-section, .featured-new-releases .new-release .rating.allmusic, .featured-new-releases .new-release .rating.average, .content section table thead tr, .content section table tbody tr, .results h1, .results .search-results li, .content section.track-listing table tbody tr td, .content section.track-listing table thead tr th, .featured-new-releases .new-releases-container, .content-container .content section .headline, section.recent-releases ul li .rating, section.recent-releases ul li, .content-container .row, .content-container .week-of-headline, .content-container .filter-nav, .content-container section.featured-new-releases .releases-headline, section.album-list li .table-cell, .content-container .featured .rating, .content-container .content .module .top-container, div.discover-genre .genre-info, div.discover-genre .genre-info .genre-releases .genre-releases-row, div.discover-mood .mood-desc, div.discover-mood .theme-desc, div.discover-theme .mood-desc, div.discover-theme .theme-desc, .discover-adv-search .adv-search-section, .content .editors-choice .rating.allmusic, .content .editors-choice .rating, section.editors-choice-content .editors-headline, .content .active-filters header, .filter .text-filter, .genre, .headline, .mood-container, .theme-container, .blog-entries .row.first {
    border-bottom: 1px solid rgb(72, 82, 92);
}

.content .results header  {
    border-bottom: 1px solid rgb(60, 70, 80);
}    
    
.content-container .top-headline, .content header .headline {
    border-bottom: 1px solid rgb(40, 40, 40);
}
    
.partners .partner-buttons li, .featured-new-releases .new-release.middle, .mood-list, .theme-list {
    border-left: 1px solid rgb(72, 82, 92);
}

.module.home-adv-search .adv-search-section .middle-left, .featured-new-releases .new-release.middle, .content-container .odd, div.discover-genre .genre-info .genre-artist-list, .discover-adv-search .adv-search-section .middle-left, .genre.left, .genre.middle {
    border-right: 1px solid rgb(72, 82, 92);
}

section.all-new-releases .filters div {
    border-right: 1px solid rgb(60, 70, 80);
}    
    
.content .editors-choice.middle {
    border-left: 1px solid rgb(72, 82, 92);
    border-right: 1px solid rgb(72, 82, 92);
}
    
.featured-new-releases .new-release > a, .staff-picks .staff-pick > a {
    border-color: rgb(72, 82, 92) -moz-use-text-color rgb(72, 82, 92) rgb(72, 82, 92);
}


}
