css:
@-moz-document domain("www.furvilla.com") {
body {
    background: #fff3f9 url("https://i.sli.mg/LRQzqs.png") repeat 100% 0;
}
Style Attribute {
    color: #ae72b8 !important;
    margin: 0;
    font: normal 14px/20px Arial, Helvetica, sans-serif;
    height: 100%;
    background-color: #fff;
}
.gender-radio input[type='radio']:checked ~ label {
    color: #eba2ba;
    background-color: #eba2ba;
    border: 1px solid rgba(255, 255, 255, 0);
}
.gender-radio label {
    float: left;
    width: 40px;
    border-radius: 2px;
    border: 1px solid rgba(209, 211, 212, 0);
    text-align: center;
    margin: 2px;
    display: block;
    line-height: 25px;
}
.content {
    background-image: url(https://i.sli.mg/LRQzqs.png);
    background: #F9C7D1;
    background-color: rgba(255, 255, 255, 0.6);
    background-repeat: repeat;
    background-position: top center;
    color: #ad72b8!important;
}

element.style {
    color: #ad72b8;
    font-size: medium;
}
.content h2 {
    color: #ad72b8;
    font-size: 24px;
    margin: 0 0 20px 0;
}
.textarea {
    border-width: 1px;
    border-color: rgba(173, 114, 184, 0.37);
    border-style: solid;
    border-radius: 8px;
    background-color: #fff;
    padding: 10px;
    width: 100%;
    margin-bottom: 10px;
    outline: none;
    resize: none;
    font-family: Raleway, sans-serif;
}
textarea {
    color: #ad72b8;
}
.content h3 {
    color: #ad72b8;
    font-size: 16px;
    margin: 0 0 6px 0;
}
.villager-data-info-wide {
    margin: 0 2% 20px 0;
    border: 1px solid rgba(173, 114, 184, 0.39);
    border-radius: 8px;
    padding: 10px;
    width: 100%;
    float: none;
    clear: both;
}
.villager-data-info-box {
    margin: 0 2% 20px 0;
    border: 1px solid rgba(173, 114, 184, 0.1);
    border-radius: 8px;
    padding: 10px;
    float: left;
    width: 48%;
}
.animal-item-actions {
    position: absolute;
    background: #fff;
    left: 5px;
    right: 5px;
    border-radius: 3px;
    overflow: hidden;
    height: 100%;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    -ms-flex-align: center;
    align-items: center;
    text-align: center;
    opacity: 0;
    z-index: -1;
    transition: opacity .3s ease;
}
.label-success {
    background-color: rgba(235, 162, 186, 0.26);
}
.label-primary {
    background-color: rgba(255, 255, 255, 0.49);
}
.label-warning {
    background-color: rgba(173, 114, 184, 0.18);
}
label {
    font-size: 13px;
    font-weight: bold;
    text-transform: uppercase;
    color: #ad72b8;
}
.job-text p {
    font-size: 21px;
    color: #ad72b8;
}
.panel-default {
    border-color: #eba2ba;
}
.fur-residents-item-actions {
    position: absolute;
    background: rgba(255, 255, 255, 0.77);
    left: 5px;
    right: 5px;
    border-radius: 6px;
    overflow: hidden;
    height: 100%;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    -ms-flex-align: center;
    align-items: center;
    text-align: center;
    opacity: 0;
    z-index: -1;
    transition: opacity .3s ease;
}
.fur-residents-item-info .status {
    color: #eba2ba;
    display: block;
    text-align: center;
    font-size: 12px;
    font-weight: normal;
}
.fur-residents-item-info:hover {
    border-color: #eba2ba;
    box-shadow: 0 0 10px rgba(173, 114, 184, 0.4);
}


.counter {
    font-size: 16px;
    height: 24px;
    line-height: 20px;
    border: 1px solid #ad72b8;
    padding: 0 10px;
    border-radius: 12px;
    vertical-align: middle;
    position: relative;
    top: -0.1em;
}
.widget .forum-posts p,
.widget .forum-posts p a {
    margin: 0;
    color: #ad72b8;
    font-size: 12px !important;
    line-height: 12px;
}
.label-default {
    background-color: #ad72b8;
}
hr {
    display: block;
    height: 1px;
    border: 0;
    border-top: 1px dashed #ad72b8;
    margin: 1em 0;
    padding: 0;
}
.well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: rgba(255, 255, 255, 0.15);
    border: 1px dashed #ad72b8;
    border-radius: 2px;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
}
.modal-header h2 {
    padding: 0;
    margin: 0;
    color: #ad72b8;
    font-size: 48px;
}
.table tr:nth-child(even) td {
    background: rgba(255, 255, 255, 0.15);
    border-bottom: 1px solid rgba(255, 255, 255, 0);
}
.table tr td {
    border-bottom: 1px solid rgba(255, 255, 255, 0);
}
.header-right-toolbar-box {
    float: right;
    width: 150px;
    border: 1px solid rgba(255, 255, 255, 0.15);
    text-align: center;
    padding: 10px;
    border-radius: 2px;
    background: rgba(255, 255, 255, 0.5);
}
.header-right-toolbar {
    font-size: 14px;
    color: #ad72b8;
    font-weight: normal;
    width: 400px;
    position: relative;
    top: -10px;
}
.forum-category {
    background-image: linear-gradient(#eaa2ba, #b074b8) !important;
    border-bottom: 3px solid #ae72b8 !important;
    height: 40px;
    color: #fff6ce;
    /* font-weight: bold; */
    
    font-size: 14px;
    padding: 0 20px;
    text-shadow: 1px 1.732px 2px #ad72b8;
}
.forum tr:nth-child(even) td:not(.forum-category) {
    background: rgba(242, 251, 254, 0.75);
}
.forum tr:nth-child(even) td:not(.forum-category) {
    background: #f2fbfe;
    border-bottom: 1px dashed #eba2ba;
}
.forum tr td:not(.forum-category) {
    border-bottom: 1px dashed #eba2ba;
}
.forum-header,
.forum tr:hover td.forum-header {
    background: #ad72b8 !important;
    height: 38px !important;
    color: #fff;
    font-weight: bold;
    font-size: 12px;
    padding: 0 10px;
    text-transform: uppercase;
}
.content h1 {
    color: #eba2ba;
}
.user-panel {
    background: #ad72b8!important;
    color: #fff;
}
header nav {
    border-radius: 2px;
    background-image: linear-gradient(#eba2ba, #ad72b8);
}
header nav a {
    color: #fff !important;
}
.user-panel a {
    color: #eba2ba!important;
}
.fa {
    color: #ffcddd!important;
}
.content h1 small {
    color: #eba2ba;
}
.content h1 {
    color: #ad72b8;
}
.user-info h4 {
    color: #eba2ba;
}
.widget-content {
    opacity: .7;
}
.widget-header h3 {
    border-radius: 2px !important;
    background-image: linear-gradient(#eba2ba, #ad72b8);
    color: #fff;
}
.widget-header {
    border-radius: 1px !important;
    border-bottom: 1px dashed rgba(255, 255, 255, 0.15);
}
.toolbar-title h2,
.toolbar-title h3 {
    color: #ad72b8;
}
.toolbar-title h2:before, .toolbar-title h3:before {
    content: "";
    display: block;
    height: 1px;
    background: #eba2ba;
    top: 6px;
    left: 110%;
    right: -700px;
    position: absolute;
}
.btn {
    background-image: linear-gradient(#eba2ba, #ad72b8);
}
a {
    color: #eba2ba!important;
}
#logo a {
    background: url("https://i.sli.mg/8dTGf6.png") no-repeat;
}
.btn-link {
    color: #fff !important;
}
.search {
    opacity: .5;
    background: #fff;
}
.help-block {
    color: #ad72b8;
}
.sod_select:hover,
.sod_select.open,
.sod_select.focus {
    border-color: #ad72b8!important;
}
.sod_select {
    border: 1px solid rgba(255, 255, 255, 0);
    border-radius: 8px;
    background-color: #fff;
    height: 40px;
    line-height: 38px;
    padding: 0 10px;
    width: 100%;
    min-width: 60px;
    position: relative;
    display: block;
    color: rgb(173, 114, 184);
    font-weight: bold;
    font-size: 14px;
    cursor: pointer;
    outline: none;
    margin-bottom: 10px;
}
.sod_select .sod_option.active {
    background: #ad72b8;
    color: #fff;
}
.sod_select .sod_list_wrapper {
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    display: none;
    height: auto;
    margin: 0 -1px;
    border: 1px dashed rgba(255, 255, 255, 0.00);
    border-top: none;
    background: #fff;
    z-index: 999;
    font-size: 12px;
}
strong.blue {
    color: #ad72b8;
}
.inventory-item-info,
.item-block {
    border: 1px dashed #eba2ba;
    background: rgba(255, 255, 255, 0.6);
}
.inventory-item-info .counter,
.item-block .counter {
    border-color: #ad72b8;
    color: #191919;
}
.pagination > li > a,
.pagination > li > span {
    color: #ffffe0;
    background-color: #ad72b8;
    border: 1px dashed #ad72b8;
}
.pagination > .active > a,
.pagination > .active > a:hover,
.pagination > .active > a:focus,
.pagination > .active > span,
.pagination > .active > span:hover,
.pagination > .active > span:focus {
    color: #ffffe0;
    background-color: #eba2ba;
    border-color: #eba2ba;
}
.pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 6px 12px;
    line-height: 1.42857;
    text-decoration: none;
    color: #337ab7;
    background-color: #eba2ba !important;
    border: 1px solid rgba(255, 255, 255, 0)!important;;
    margin-left: -1px;
}
a {
    color: rgba(173, 114, 184, 0.35)!important;
}
.fur-residents-item-info {
    border: 1px dashed #ad72b8;
    background: #fffafd;
}
.thread-user-info {
    background: rgba(255, 255, 255, 0.6);
}
.textarea {
    background-color: rgba(255, 255, 255, 0.6);
}
textarea {
    color: #ad72b8;
}
.content .left-column {
    float: left;
    width: 716px;
    border-radius: 2px;
}
.table thead,
.table tr:hover .table thead {
    background: #ad72b8 !important;
}
.nav-pills > li.active > a,
.nav-pills > li.active > a:hover,
.nav-pills > li.active > a:focus {
    color: #C20095;
    background-color: #fff;
}
.modal-content {
    background-color: rgba(255, 255, 255, 0.8);
}
.modal-content .logo {
    position: absolute;
    width: 253px;
    height: 140px;
    top: -70px;
    left: 50%;
    margin-left: -127px;
    display: block;
}
.animal-item-info {
    background: rgba(255, 255, 255, 0.46);
}
.animal-item-info {
    display: block;
    text-align: center;
    font-weight: bold;
    border: 1px dashed #eba2ba;
    border-radius: 6px;
    margin: 0 5px;
    padding: 20px 0;
    background: #fff;
    text-decoration: none !important;
    height: 270px;
    box-shadow: 0 0 0 transparent;
    transition: border-color .3s ease, box-shadow .3s ease;
}
.villager-name {
    text-shadow: -1px -1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff, 1px 1px 0 #fff;
}
::selection {
    background: #ad72b8;
    text-shadow: none;
}
/* show breedableness in import animal dialog */

.modal-dialog .inventory-block .inventory li {
    width: 33%;
    flex: 0 0 33%;
    font-size: 12px;
}
.modal-dialog .inventory-block .inventory .inventory-item-info .info.hide {
    display: initial !important;
}
.modal-dialog .inventory-block .inventory .inventory-item-info .info.hide .text-center,
.modal-dialog .inventory-block .inventory .inventory-item-info .info.hide hr,
.modal-dialog .inventory-block .inventory .inventory-item-info .info.hide > div > p > b {
    display: none !important;
}
}
