css:
@-moz-document domain("ferzu.com") {
#main {
	background-color: #333;
    
	/* Fix weird backound colour bleedthrough from .page */
    -moz-border-radius: 0.1px;
    -webkit-border-radius: 0.1px;
    border-radius: 0.1px;
}

body {
	color: #bfbfbf;
}

h1, h2 {
	color: #12b0cb !important;
}

/* general */
.with-borders .photo-thumbnail,.with-borders .member-thumbnail,.with-borders .photo-icon,.with-borders .photo-thumbnail-sm,.with-borders .photo-thumbnail-lg {
	border: 1px solid #666;
}
.with-borders .photo-thumbnail-lg.embed {
	background-color: #4a4a4a;
}
.color-sec {
	color: #888;
}
.retina, .column img {
    border: 8px solid #ccc;
    border-radius: 5px;
}
.home-features .column img {
	border: none;
}
.ui-dialog {
	background-color: #333 !important;
}
.hero-background {
	background-color: #4a4a4a;
}

/* sidebar */
table td.left.navt {
	background-image: linear-gradient(to right, #333333, #595959);
}

/* sidebar links */
.actionitems a {
	color: #bfbfbf;
}
.actionitems a:hover {
	color: #ea4a35
}

/* sidebar top hashtags / searches */
.popular-list a {
    background-color: #d9d9d9;
    color: #4d4d4d;
}
.popular-list a:hover {
    background-color: #4d4d4d;
    color: #d9d9d9;
}

/* post / comment styles */
.post-add, .comment, .comment-add {
	background-color: #4a4a4a;
}
.post {
    border-top: 1px solid #4d4d4d;
}
.post.sticky {
	background: none;
}
div .post.photostream-overquota {
    background-color:#333;
    color:#bfbfbf;
}

/* chat */
.ui-widget-content {
    background-color: transparent;
    background: transparent;
    color: #bfbfbf
}
.ui-tabs-panel {
    color: #bfbfbf;
    padding: 5px !important;
    border: 2px solid #999 !important;
    border-top: 0 !important;
    border-bottom-left-radius: 3px;
    border-bottom-right-radius: 3px;
}
.ui-tabs-nav {
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
}
.ui-tabs-active {
	background-color: #333 !important;
}
.ui-tabs-active a {
	color: #eee !important;
}
table .messages tr {
	background-color: #4a4a4a;
}
table .messages tr.unread {
	background-color: #535353;
}
table .messages tr.unread td.msg-photo {
	border-left: 4px solid #11e348;
}
td .msg-from {
	color: #999;
}

/* language select */
.languages a {
	color: #bfbfbf;
}

/* form elements */
input[type="text"], input[type="password"], input[type="file"], select, textarea, .ui-multiselect {
    color: #404040;
    background-color: #ccc;
    border: 1px solid #eee !important;
}
.editor-label label {
	color: #12b0cb;
}
.editor-field select option {
    background: #4a4a4a;
	color: #bfbfbf;
}
.editor-field select option:hover, .editor-field select option:checked {
    box-shadow: 0 0 10px 10px #737373 inset;
}
.ui-multiselect-checkboxes {
    background: #4a4a4a;
}
ul.ui-multiselect-checkboxes .ui-state-hover {
    color: #737373;
}
.ui-multiselect span span {
    color: #ff4d4f !important;
}
.slider {
    background-color: #4a4a4a;
}
.jslider-label, .jslider-value {
    background-color: #ccc;
    color: #333;
}

/* profile */
.statbox.statbox-location, .statbox.color3 {
    background-color: #eee;
}
.col-right {
    border-left: 1px dotted #808080 !important;
}
span.profile-icon {
    color: #999;
}

/* member thumbnails */
/* fixes sizing to stop some thumbnail with long location from overflowing */
.member-thumbnail {
    height: 261px;
}
.member-thumbnail .miniprofile {
    height: 142px;
    background-color: #4a4a4a;
}
.member-thumbnail .miniprofile h4 {
    text-shadow: none;
}
.member-thumbnail .miniprofile h4 a {
    color: #f07937 !important;
}
.member-thumbnail.tile-header {
    background-color: #666;
}

/* settings - overview */
.t-header, .t-grid-header {
    color: #bfbfbf;
    background: #4a4a4a;
}
.t-widget, .t-link:link, .t-link:active, .t-link:visited, .t-popup {
    color: #666;
    background-color: #eee;
}

/* settings - plans */
td.planitem {
    color: #bfbfbf;
    background-color: #4a4a4a;
    border: 2px solid #333;
}
td.planpremium {
    border-left: 2px solid #333;  
}
td.planbasic {
    background-color: #4a4a4a;
}
td.planbasic h3 {
    color: #bfbfbf !important;
}

/* settings - buy */
#Buy > div:nth-of-type(2) {
	background-color:#4a4a4a !important;
}

/* settings - site prefs */
.editor-field {
    color: #bfbfbf;
}
.editor-field hr {
    border-bottom: 1px dotted #808080 !important;
}
}
