css:
/* i really want this to be global */
.chart-widget[data-type='bignumber'] .chart {
    padding: 0;
    width: auto;
}

.sdk-media-chart .sdk-chart-error {
    padding: 10px;
}

div.space {
    background-color: #404040;
    background-image: none;
}

div.space .highcharts-axis text, div.space .highcharts-yaxis-labels text {
    color: #cecece ;
    fill: #cecece ;
}

div.space .sdk-media-chart .sdk-chart-name {
    background: #292929;
    color: #ECECEC ;
    font-size: 160%;
    padding: 1px;
}

div.space div.chart {
    background-color: #292929;
}

div.space path[stroke="#235a94"] {
    stroke: #3881D1;
}

div.space path[stroke="#27629e"] {
    stroke: #60A0E6;
}

div.space path[stroke="#F2F2F2"] {
    stroke: #585858;
}

div.big_number__value {
font-size: 100px !important;
}

div.big_number {
background-color: #2863A0 !important;
}

div.big_number__dimension_value {
display: none;
}
