}  



/* hide favicons on unpinned */

.tabbrowser-tab:not([pinned]) .tab-icon-image {
    display: none !important;     
}

/*remove blue browser border 
:not([chromehidden*="toolbar"])[sizemode="normal"]
*/
#main-window {
