#TabsToolbar .tabbrowser-tab[pinned]:hover .tab-close-button,
#TabsToolbar .tabbrowser-tab[pinned] .tab-icon-overlay {
	display: none !important;
}

#TabsToolbar:not([inFullscreen]) .tabbrowser-tab .tab-stack {
	margin-top: -3px !important;
}

#TabsToolbar .tabbrowser-tab:hover .tab-close-button {
	display: block !important;
	margin-left: -4px !important;
	margin-right: 2px !important;
	margin-top: 0 !important;
}

#TabsToolbar[inFullscreen] .tabbrowser-tab:hover .tab-close-button {
	display: block !important;
	margin-left: -4px !important;
	margin-right: 2px !important;
	margin-top: 1px !important;
}

#TabsToolbar .tabbrowser-tab:hover .tab-close-button .toolbarbutton-icon {
	/*list-style-image: url(closetab.png) !important;*/
  width: 12px !important;

}

#TabsToolbar .tabbrowser-tab:hover .tab-close-button:hover .toolbarbutton-icon {
	background: rgba(90, 90, 90, .2) !important;
	border-radius: 16px !important;
}

#TabsToolbar:-moz-window-inactive .tabbrowser-tab:hover .tab-close-button {
	opacity: .45 !important;
}

#TabsToolbar .tabbrowser-tab[pinned]:hover .tab-icon-image {
	display: block !important;
}

