css:
@-moz-document regexp("http://incurved.cf"), regexp("http://incurved.cf/"), regexp("http://incurved.net/") {
.heading:after {
             content: " ";
             display: block;
             width: 100px;
             height: 2px;
             margin: 20px auto 20px;
             background: #26db26;
}
.section-gray {
             background: #5b5b5b;
}
.section-darkgray {
             background: #252525;
}
h2 {
             color: white !important;
}
.row.text1 p {
             color: white !important;
}
.intro .content {
             position: relative;
             width: 100%;
             top: 50%;
             -webkit-transform: translate(0, -50%);
             -ms-transform: translate(0, -50%);
             transform: translate(0, -50%);
             z-index: 3;
}
.intro.colour-background {
             background: #26db26;
}
.intro.image-background {
             background: url('http://i.fiirmly.cf/6BSqfUHn.png') center center no-repeat;
             -webkit-background-size: cover;
             -moz-background-size: cover;
             -o-background-size: cover;
             background-size: cover;
             -webkit-backface-visibility: hidden;
}
.intro.image-background .overlay {
             display: block;
             position: absolute;
             left: 0;
             top: 0;
             width: 100%;
             height: 100%;
             filter: alpha(opacity=93);
             z-index: 2;
}
.intro .icon:hover,
.intro .icon:focus {
             border: solid 2px #26db26;
}
.intro.colour-background {
             background: #1bff1b;
}
.intro a:hover,
.intro a:focus {
             color: #26db26;
}
.members:hover {
             color: #26db26;
             cursor: pointer;
}
.media {
             color: #fff;
}
.media:hover {
             color: #26db26;
             cursor: pointer;
}
.nav .open > a,
.nav .open > a:hover,
.nav .open > a:focus {
             background-color: #eeeeee;
             border-color: #26db26;
}
.nav-pills > li.active > a:hover,
.nav-pills > li.active > a:focus {
             color: #ffffff;
             background-color: #26db26;
}
.navbar-default .navbar-nav > li > a:hover,
.navbar-default .navbar-nav > li > a:focus {
             color: #1bff1b;
             background-color: transparent;
}
.navbar-default .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > a:hover,
.navbar-default .navbar-nav > .active > a:focus {
             color: #26db26;
             background-color: transparent;
}
.navbar-default .navbar-toggle:hover,
.navbar-default .navbar-toggle:focus {
             background-color: #26db26;
}
.navbar-default .navbar-nav > .open > a,
.navbar-default .navbar-nav > .open > a:hover,
.navbar-default .navbar-nav > .open > a:focus {
             background-color: transparent;
             color: #26db26;
}
.navbar-default .navbar-nav .open .dropdown-menu > li > a:hover,
.navbar-default .navbar-nav .open .dropdown-menu > li > a:focus {
             color: #26db26;
             background-color: transparent;
}
.navbar-default .navbar-nav .open .dropdown-menu > .active > a,
.navbar-default .navbar-nav .open .dropdown-menu > .active > a:hover,
.navbar-default .navbar-nav .open .dropdown-menu > .active > a:focus {
             color: #26db26;
             background-color: transparent;
}
.navbar-default .navbar-link:hover {
             color: #26db26;
}
a {
             color: #26db26;
             text-decoration: none;
}
.btn-primary {
             color: #26db26;
             background-color: #ffffff;
             border-color: #26db26;
}
.btn-primary:hover,
.btn-primary:focus,
.btn-primary:active,
.btn-primary.active,
.open .dropdown-toggle.btn-primary {
             color: #26db26;
             background-color: #ebebeb;
             border-color: #2972c3;
}
.btn-primary[disabled],
fieldset[disabled] .btn-primary,
.btn-primary.disabled:hover,
.btn-primary[disabled]:hover,
fieldset[disabled] .btn-primary:hover,
.btn-primary.disabled:focus,
.btn-primary[disabled]:focus,
fieldset[disabled] .btn-primary:focus,
.btn-primary.disabled:active,
.btn-primary[disabled]:active,
fieldset[disabled] .btn-primary:active,
.btn-primary.disabled.active,
.btn-primary[disabled].active,
fieldset[disabled] .btn-primary.active {
             background-color: #ffffff;
             border-color: #26db26;
}
.btn-primary .badge {
             color: #ffffff;
             background-color: #26db26;
}
.btn-primary:hover,
.btn-primary:focus,
.btn-primary:active,
.btn-primary.active {
             background: #26db26;
             color: #ffffff;
}
.form-control:focus {
             border-color: #26db26;
             outline: 0;
             -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(80, 145, 218, 0.6);
             box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(80, 145, 218, 0.6);
}
.pager li > a,
.pager li > span {
             background-color: #ffffff;
             border: 1px solid #26db26;
             border-radius: 0;
}
.pager li > a:hover,
.pager li > a:focus {
             text-decoration: none;
             color: #fff;
             background-color: #26db26;
}
.pagination > li > a:hover,
.pagination > li > span:hover,
.pagination > li > a:focus,
.pagination > li > span:focus {
             color: #26db26;
             background-color: #cee0f5;
             border-color: #dddddd;
}
.pagination > .active > a,
.pagination > .active > span,
.pagination > .active > a:hover,
.pagination > .active > span:hover,
.pagination > .active > a:focus,
.pagination > .active > span:focus {
             z-index: 2;
             color: #ffffff;
             background-color: #26db26;
             border-color: #26db26;
}
/* type */

.text-primary {
             color: #26db26;
}
.bg-primary {
             color: #fff;
             background-color: #26db26;
}
blockquote {
             padding: 10px 20px;
             margin: 0 0 20px;
             font-size: 14px;
             border-left: 5px solid #26db26;
}
.blockquote-reverse,
blockquote.pull-right {
             border-right: 5px solid #26db26;
}
.panel-primary {
             border-color: #26db26;
}
.panel-primary > .panel-heading {
             color: #ffffff;
             background-color: #26db26;
             border-color: #26db26;
}
.panel-primary > .panel-heading + .panel-collapse .panel-body {
             border-top-color: #26db26;
}
.panel-primary > .panel-footer + .panel-collapse .panel-body {
             border-bottom-color: #26db26;
}
a.list-group-item.active > .badge,
.nav-pills > .active > a > .badge {
             color: #26db26;
             background-color: #ffffff;
}
}
