@-moz-document domain("zhidao.baidu.com") {
@media print{
	.userbar,
	.search-box,
	.nav-menu-container,
	.ask-info,
	.accuse-enter,
	.f-pipe,
	.answer-share-widget,
	.wgt-bottom-union,
	.comment,
	.related-more,
	.grid-r,
	.wgt-push,
	.wgt-footer-new,
	.jump-top-box,
	.qb-side,
	.mr-15,
	.carefield,
	.newbest-content-meta,
	.wgt-replyer-best {
		display: none!important;
	}

	#header,#top-nav-bar,#wgt-topic {
		display: none!important;
	}

	.wgt-replyer-special-bg {
		padding-top: 2px;
		padding-bottom: 2px;
		background: #e9fadf;
	}

	.wgt-ask {
		background-color: #EEEEEE;
		padding: 20px 60px 18px;
		position: relative;
		z-index: 1;
		line-height: 20px;
	}

	.qb-section .qb-content {
		width: 1070px;
	}
    
	.wgt-relate-search {
    display: none!important;
	}
    .wgt-ask {
    background-color: #EEEEEE;
    padding: 10px 0px 10px 0px;
    position: relative;
    z-index: 1;
    line-height: 20px;
	}
    .wgt-ask h1 .i-status-being {
    margin-top: 4px;
    margin-right: 10px;
    margin-left: 3px;
    font-weight: 400;
	}
}
