css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("camelcamelcamel.com") {
  body, html, #main_menu_row, .img_box, #watch_forms table thead tr, #maintab, .radioboxen a:link, #footer_outer, #content_container, .buttons a, div.pagination span.current, div.pagination a, td.price_type .title, td.current_price .title, td.watching.last .title, #products_list table td.price_type .price1, .price_new, .watch, #products_header form input[type="text"], #products_header form input[type="submit"], #products_filter form input[type="submit"], #watch_forms input.price[type="text"], th, td, #products_filter form label:first-child {background: #222!important; color: #ddd!important;}
  .deal_top_inner, .deal_bottom_inner, #footer a:link, #usermenu a, #qm1 a, select, ul.toggler li.left_piece a.off, ul.toggler li.middle_piece a.off, ul.toggler li.right_piece a.off, #sq, #maintab a.on, .radioboxen a:hover, #pricetabs a.pricetype.off, table.product_pane thead tr th, .msg_bar, #subtitle, p, .notb, #leftpart, ul.nobullets li, #products_header, #products_list table tr, #products_filter {background: #444!important; color: #ddd!important; border-color: #888!important;}
  #toggle_active_sales_rank, #toggle_active_details, .tabs a:hover, #pricetabs a.pricetype.off:hover {background: #888!important; color: #ddd!important;}
  #top_right_menu_2014 a:link, #top_right_menu_2014 a:visited, h3 a:visited, a:visited, h2 a, h3 a, p a {color: #aaa!important;}
  a:hover, h3 a:hover, #user_dash div.dash_metric.olive a, #user_dash div.dash_metric.green a, a#alert_counter.total_alerts, .black, .price_type_label, .controlbox label, .controlbox label a, blockquote, div {color: #ddd!important;}
  a.gooddealbadge.votey, .sss, .col.quarter, .gooddealbadge, #MB_window #inner_frame, #MB_frame, .rightAlign, .leftAlign, .sh, .legend {background: #222!important;}
  .big_buttons div.retailer_link, .button, #watch_forms table, #pagination_bar, #products_list td.price_type div:nth-child(2n+1), #products_list td.current_price div:nth-child(2n+1), #products_list td.watching div:nth-child(2n+1) {background: #444!important;}
  #main_menu_2014 a:hover, #user_dash div.dash_metric.red a:hover, #user_dash div.dash_metric.green a:hover, #user_dash div.dash_metric.olive a:hover, #alert_counter.total_alerts:hover, .button:hover, .big_buttons div.retailer_link:hover, .section_list li a:hover, .buttons a:hover, .watch:hover, #products_header form input[type="submit"]:hover, div.pagination a:hover, a.right_slide {background: #888!important;}
  ul.toggler li.middle_piece a.off:hover {color: #aaa!important;}
  img#summary_chart, img#sales_rank_chart, input#searchbutton, div.hpsprite.hpsprite-ctlogo, div.hpsprite.hpsprite-pcwlogo, div.hpsprite.hpsprite-lifehackerlogo, div.filter_box, label.button, input#p {border-color: transparent!important; filter: invert(87%)}
  .img_box, div.col.clearfix {opacity: 0.7}
}
