css:
 /**
* AnimeAddict_FixMenu v2.1
* ZeroProcess : 2016.07.21.
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animeaddicts.hu") {
body {
padding-top: 55px;
}

#menu-container {
z-index: 10000;
position: fixed;
top: 0px;
width: 100%;
margin: auto;
background: #27313d;
height: 50px;
}
#menu ul{ top: 30px; }
#menu li { border-right:1px solid rgb(150,150,150); color: #FEFEFE; }
#menu li a { color: #FEFEFE; }
#menu ul li ul { border: 1px solid rgb(150,150,150); background: #27313d; top: 20px; }
#menu ul li ul li { background:#27313d; border-bottom:1px solid rgb(150,150,150); }

#menu ul li span:hover, #menu ul li:hover{
background: #FEFEFE;
color: rgb(30,30,30);
box-shadow: 0px 0px 4px 1px rgb(200,200,200);
border-right: 1px solid #FEFEFE;
}
#menu ul li ul li:hover, #menu ul li ul li a:hover, #menu ul li ul li ul li:hover{
background: #FEFEFE;
color: rgb(30,30,30);
}

#menu li.top-menu-1 { width:69px; }
#menu li.top-menu-2 { width:101px; }
#menu li.top-menu-3 { width:80px; }
#menu li.top-menu-4 { width:105px; }
#menu li.top-menu-5 { width:106px; }
#menu li.top-menu-6 { width:104px; }
#menu li.top-menu-7 { width:82px; }
#menu li.top-menu-8 { width:104px; }
#menu li.top-menu-9 { width:71px; margin:0; border-right: 0;}

#menu ul li { padding:0; height:20px; }
#menu li.home, #menu li.home:hover{ padding:0; height:20px; visibility:hidden; }
#menu li a span{ line-height: 20px; }

#top{
position: fixed;
margin-left: -496px;
left: 50%;
top: 0px;
z-index: 10001;
height: 28px;
}
#login{
margin: 0;
height: 28px;
width: 345px;
}
#login div{ width:57px; visibility:hidden; padding: 0 2px;}
#login div.touchable, #login div.button { visibility:visible; }
#login div.settings { width:25px; height:25px; visibility:visible; margin-top: 1px;}
#login div.settings img { width:18px; height:18px; }

#login div.button { width:100px; height:25px; margin-right:5px; margin-top: 1px;}
#login div.button img { margin-left: 40px;}
.i40 { width: 15px; }
#login div.button:first-child span, #login div.button span { margin-top:2px; font-size: small; }
#login div.button span { display: none; }


#login img.avatar{ position: fixed; margin-top: 0px; }
#login a { float: left; }

#online{ margin: 0; float: right; margin-top: 3px; }
.i32 { width: 21px; }

#search{
margin:0;
height: 30px;
width: auto;
padding: 1px 0 0 0;
float: left;
}
#search form { height:28px; }
#search #search-text { width:480px; height:19px; }
#search #search-send { width:15px; height:15px; }
#search p { display: block; }
}
