css:
/**
* AnimeAddicts_Mobile_Style 0.0.2.12
* ZeroProcess : 2016.09.24.
*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("animeaddicts.hu") {
  body {
    width: 100%;
    background: rgb(57,63,76);
  }
  
  /*TOP*/
  #top { width: 320px; height: 190px; }
  #login { float: none; width: 320px; margin: 12px auto 0 auto; }
    #login>a { display: none; }
    #login div:nth-child(1) { float: none; }
    #login div.settings:nth-last-of-type(1) { margin-right: 0; }   
    #login div.button { width: 145px; }
      #login div.button:nth-of-type(1) { float: left; }
      #login div.button:nth-of-type(2) { display: none; }
      #login div.button:nth-of-type(3) { margin-right: 0; }
  #online { width: 100px; float: none; border: 0; margin: 7px auto 0 auto; }
    #online>p {background: #2B333B; border: 1px solid #757F8B; border-radius: 4px;  }
      #online>p:nth-child(1){ margin-right: 5px; }
      #headerapp_online img, #headerapp_chat img { /*width: 25px; /*display: none;*/ }
      #headerapp_online b, #headerapp_chat b { display: none; }
    #search { float: left; width: 320px; margin: 2px auto 0 auto; }
      #search #search-text { width: 253px; }
  
  /*LOGIN Form*/
  #login-form {
    width: 100%;
    height: 174px;
    position: fixed;
    top: 50%;
    left: initial;
    margin-left: 0; margin-top: -87px;
    padding: 5px 0;
    border-left: 0; border-right: 0;
  }
  #login-form #login_name { width: 50%; padding: 5px 5px 5px 0px; border-left: 0; }
  #login-form #login_password { width: 50%; padding: 5px 5px 5px 0px; position: absolute; top: 45px; border-left: 0; border-right: 0; }
  #login-form #login_submit { width: 100%; height: 65px; position: absolute; top: 110px; left: 0px; background: url(http://animeaddicts.hu/theme/apps/login/login.png) 5px 0px no-repeat;}
  #login-form h1 { width: 100px; }
  
  /*MENÜ*/
  #menu-container { width: 100%; height: 210px; background: none; border-bottom: none; }
    #menu { width: 100%; }
      #menu ul { width: 100%; }
        #menu>ul>li>a { pointer-events: none; }/*ez azért kell h a menügombra katt-va ne ugorjon el hanem nyíljon le a menü*/
        #menu li.home a { pointer-events: fill; }
        #menu li, #menu li.home, #menu li.home:hover { 
          display: block; float: none; width: 100%!important; 
          height: 30px; background: #FBFBFB; border: 1px double #757F8B;
          border-radius: 4px; margin-bottom: 2px; border-left: 0; border-right: 0;
          padding: 0;
        }
        /*#menu li.home, #menu li.home:hover { float: none; width: 100%!important; }*/
        #menu>ul>li { width: 50%!important; float:left; }
          #menu li a span { line-height: 27px; }
       #menu ul li ul { 
          top: 30px; left: 0px; background: #303A50; 
          border-top: 1px solid #303A50; border-left: 1px solid #303A50;
          border-right: 1px solid #303A50; border-bottom: 1px solid #303A50;
       }
          #menu li.top-menu-8 ul, #menu li.top-menu-9 ul { left: 0px; }
       #menu ul li ul li { background: #C1CADE; }
         /*#menu ul li ul li ul {  right: -220px!important;;}*/
  
       #menu>ul>li>a span:hover, #menu ul li:hover, #menu li.home:hover{/*SZÍNVÁLASZTÁS NEM A LEGJOBB*/
         background: #D4D8EA;
          box-shadow: 0px 0px 4px 1px #BCBFD1;
       }
  /*FEJLÉCKÉP*/
  #headerpic { display: none; }
  
  /*TARTALOM*/
  #content { width: 100%; }
  
  /*BOTTOM MENÜ*/
  #bottom-menu { height: auto; }
  #bottom-menu ul { width: 80%; height: auto; margin-left: 10%; margin-right: 10%; }
    #bottom-menu li{ width: 100%!important; padding: 5px 0px; margin: 0; margin-bottom: 5px; border-left: 0; border-right: 0; }
      #bottom-menu li.bottom-menu-1 { margin-top: 5px; }
      #bottom-menu li.bottom-menu-8, #bottom-menu li.bottom-menu-9, #bottom-menu li.bottom-menu-10 { display: none; }
      #bottom-menu li.bottom-menu-11 { position: fixed; bottom: 0px; left: 0px; margin-bottom: 0px; z-index: 6000; }
        #bottom-menu li.bottom-menu-11 img { float: none; }
  #bottom-text { width: 100%; }
    #bottom-text #rss { width: 80%; margin-left: 10%; margin-right: 10%; text-align: center; float: none; }
      #bottom-text #rss img { display: none; }
      #bottom-text #rss div { float: none; }
    #bottom-text #copyright { width: 80%; margin-top: 0px; margin-left: 10%; margin-right: 10%; padding: 17px 0px 60px 0px; text-align: center; float: none; }
}

/*FŐOLDAL*/
@-moz-document url("http://animeaddicts.hu/news.php?news") {  
  /*#left, #right { display: none; }/*ideiglenes code*/
  
  /*LEFT*/
  #left { width: 100%!important; }
  #left>div:nth-of-type(3), #left>div:nth-of-type(4) { display: none; }
  #left>div:nth-last-of-type(4) table { display: none; }
  @media only screen and (max-width:480px){    
    #left>div.box{ width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; }   
    #ot1-forum, #ot1-picture, #ot1-other { width: 100%!important; padding: 6px 0; border-left: 0; border-right: 0; }
     #ot1-forum>.headline3, #ot1-picture>.headline3, #ot1-other>.headline3 { width: 100%!important; padding: 2px 0; border-left: 0; border-right: 0; }    
    #slide_projects { margin: 0 auto!important; }    
    #ot3-anime, #ot3-manga, #ot3-drama, #ot3-vn, #ot3-game, #ot3-ln { margin: 0 auto!important; } 
    #left>div:nth-last-of-type(4) { display: none; } /*#chart { width: 250px; margin: 0 auto; }*/
    div.box .content-rounded { margin: 5px auto; }
    #left>div:nth-last-of-type(2) .content  { width: 220px; margin: 0 auto; }
  }
  @media only screen and (min-width:481px){
    #left { width: 90%!important; margin-left: 5%; margin-right: 5%; }
    #left>div { float: left; margin-left: 10px; }
  }
 /*RIGHT*/
  @media only screen and (max-width:720px){ #right { display: none; } }
  @media only screen and (min-width:721px){ 
    #right {  width: 100%!important; }
    #right>div.box { margin: 0 auto 10px auto; }
  }
  
  /*Nem vagy bejelentkezve*/
  #content>div[style^="float:left;"] { width: 100%; }
    #content>div[style^="float:left;"]>div { width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; }
      #content>div[style^="float:left;"]>div table { width: 100%!important; }
  
  /*Frissítések*/
  #content>div[style^="width:131px;float:right;margin:0;"] { width: 100%!important; }
    #content>div[style^="width:131px;float:right;margin:0;"]>div { width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; }
      #content>div[style^="width:131px;float:right;margin:0;"]>div .thumb { width: 50%; height: 20px; float: left; border-left: 0; border-right: 0; padding: 2px 0; }
      #content>div[style^="width:131px;float:right;margin:0;"]>div .thumb:nth-last-of-type(1) { border-right: 1px solid #CCCCCC; }
      #content>div[style^="width:131px;float:right;margin:0;"]>div .thumb img { /*width: 85px!important;*/ display: none; }
  
  /*Hírek*/
  div.infinite-scroll { width: 100%!important; }
  div.news { width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; }
    div.news div.content { width: 100%!important; padding: 8px 0 5px 0!important; }
  div.news div.headline3, div.news div.headline3:hover { width: 100%!important; height: auto; padding: 2px 0; border-left: 0; border-right: 0; }
  @media only screen and (max-width:480px){
    /*480px szélesnél kisebb kijelzőn ne jelenjen meg a dátum és kiskép a hírdetés fejléceknél*/
    div.news div.headline3>h1 { width: auto!important; float: none!important; } 
    div.news div.headline3>h2 { width: auto!important; margin-bottom: 11px; }
    div.news div.headline3>img, div.news div.headline3>div { display: none; }
  }
  div.news-footer { width: 100%!important; }
    div.news-footer>div { width: 50%!important; margin: 0 0 5px 0; }
  
  .newspager { width: 100%; padding: 5px 0px; border-left: 0; border-right: 0; }
}

/*HIBA OLDAL*/
@-moz-document url-prefix("http://animeaddicts.hu/error.php?") { 
  #left { display: none; }
  #right { width: 100%; }
    #right div.box { width: 100%; padding: 5px 0; border-left: 0; border-right: 0; }
      #right div.box table { width: 100%; }
        #right div.box table img { width: 200px; }
        #right div.box table h1{ margin: 90px 0 0 0!important;}
}

/*HÍR*/
@-moz-document url-prefix("http://animeaddicts.hu/forum.php?viewtopic.40"){
  #right { width: 100%!important; }
    #right div.box { width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; }
    #right div.box .headline3 { width: 100%!important; padding: 2px 0!important; }
    #right div.box:nth-of-type(1)>.content { width: 100%!important;  padding: 5px 0!important; }
    #right div.box:nth-of-type(1)>.headline3>h1 { width: auto!important; }
    #right div.box:nth-of-type(1)>.headline3>h2 { width: 300px!important; }
    img[alt*="Elválasztó"] { display: none; }
  @media only screen and (max-width:480px){
    /*480px-nél kisebb szélsességű kijelzőkön a cikkkek fejlécénél nem jelenik meg a dátum és a kiskép*/
    #right div.box:nth-of-type(1)>.headline3>img, #right div.box:nth-of-type(1)>.headline3>div { display: none; }
    /*480px-nél kisebb szélsességű kijelzőkön a cikkkek képei kisebbek*/
    #right div.box:nth-of-type(2)>.content div.thumb2 { width: 250px!important; }
    #right div.box:nth-of-type(2)>.content div.thumb2 img { width: 100%; }
  }
  @media only screen and (max-width:680px){
    /*680px-nél kisebb szélsességű kijelzőkön a videók max kiejelző szélességüek*/
    iframe, #online_video, #right div.box:nth-of-type(2)>.content div[id*="dummy_div_"].thumb2 { width: 100%!important; }
  }
   div.titlebox { height: auto; }
}

/*FÓRUM FŐOLDAL*/
@-moz-document url-prefix("http://animeaddicts.hu/forum.php?main"){
  #right { width: 100%!important; }
    #right div.box { width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; }
  #right div.box table { width: 100%!important; border-left: 0; padding: 5px 0; table-layout: fixed; } /*320-as felbontásnál szöveg túlfolyás*/
  #right div.box table>tbody>tr>td:nth-of-type(1) { display: none; }
}
@-moz-document url-prefix("http://animeaddicts.hu/forum.php?viewforum"){
  #right { width: 100%!important; }
    #right>div  { width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; table-layout: fixed; } 
  div.titlebox { height: auto;}
  #organic-tabs-7>div.headline2 { height: auto; }
  #ot7-normal, #ot7-archive {  width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; }
  #ot7-normal>tbody>tr>td:nth-of-type(1), #ot7-archive>tbody>tr>td:nth-of-type(1) { display: none; }
  #ot7-normal>tbody>tr>td, #ot7-archive>tbody>tr>td { width: auto!important; } 
  @media only screen and (max-width:360px){
     #ot7-normal>tbody>tr>td:nth-last-of-type(1), #ot7-archive>tbody>tr>td:nth-last-of-type(1) { display: none; }
  }
}

/*KERESÉS*/
@-moz-document url-prefix("http://animeaddicts.hu/search.php"){
  #left { display: none; }
  #right { width: 100%; }
    #right div.box, #right div.box table  { width: 100%!important; padding: 5px 0; border-left: 0; border-right: 0; }
    div.box .headline2 { height: auto; }
    div.search .input { width: 100%!important; padding: 2px 0; }
    div.search input.searchsubmit { width: 100%!important; padding: 0!important; margin: 0!important; }
}
