css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("fakespot.com") {

  body, .nav.navbar-nav.navbar-right li a, .form-control, .frm-result-vis .product-analysis-section .analysis-tab-frame li a,   .pagination > .active > a, .pagination > .active > a:hover, .pagination > .active > a:focus, .pagination > .active > span{background: #222!important; color: #ddd!important;}
  .home-plugin-links-container .card-container .card, .product-single .card, .card-new, .form-control, .btn, .product-thumb-card .card-info,   .pagination > .active > a, .pagination > .active > a:hover, .pagination > .active > a:focus, .pagination > .active > span, .pagination > .active > span:hover, .pagination > .active > span, .pagination > li > a:hover, .grade-big .grade-box {background: #444!important; color: #ddd!important;}
  .icon-image, .row.top, .navbar-default .navbar-collapse, .container-fluid, .card-section .row-image, .card.company-info-block.list-small {background: #222!important;}
  .nav.navbar-nav.navbar-right li a:hover, .frm-result-vis .product-analysis-section .analysis-tab-frame li a:hover, .btn:hover {background: #888!important;}
  .index-container .card-container {background: transparent!important;}
  .card h1.page-header, .card h2 {text-shadow: 1px 1px 1px white,
                                              -1px -1px 1px white,
                                              -1px 1px 1px white,
                                               1px -1px 1px white;}
  #as-seen-on-text, .card-header, .home-plugin-links-container .card-container .card .quote .quote-text {color: #ddd!important;}
  .home-plugin-links-container .card-container .card .quote .speaker, .frm-result-vis .product-info-section .product-data-section .product-link, .frm-result-vis .product-info-section .product-data-section .title, .frm-result-vis .product-analysis-section h3, .frm-result-vis .product-analysis-section .analysis-wysk-section h4, h3, h4, a, .title-section {color: #009999!important;}
  .margin-item-left {color: #ddd!important;}
  .pagination > li > a, .form-control:hover {background: #888!important; color: #ddd!important;}
  .container.index-container {opacity: 0.6}
  html {opacity: 0.8}
}
