css:
@-moz-document url-prefix("https://japaneseclass.jp/lessons/vocabulary/"), url-prefix("https://japaneseclass.jp/lessons/oblivion/vocabulary"), url-prefix("https://japaneseclass.jp/lessons/weak/vocabulary"), url-prefix("https://japaneseclass.jp/lessons/new/vocabulary") {
div#vocabulary_lesson > div#vocabulary-list div.japanese > .text-s + .text-s {
    display: none;
}
}

@-moz-document url("https://japaneseclass.jp/practice") {
div[data-reactid=".0.0.0.1.1.0.3"] {
    display: none;
}
}

@-moz-document url("https://japaneseclass.jp/practice"), url-prefix("https://japaneseclass.jp/lessons/vocabulary/"), url-prefix("https://japaneseclass.jp/lessons/oblivion/vocabulary"), url-prefix("https://japaneseclass.jp/lessons/weak/vocabulary"), url-prefix("https://japaneseclass.jp/lessons/new/vocabulary") {
@font-face {
    font-family: "mog_Blank";
    src: local("Adobe Blank");
    unicode-range: U+21-7E, U+30FB;
}
div.answer-option > div.inline + span.option {
    font-family: "mog_Blank", "lucida grande", "ヒラギノ角ゴ Pro W3", "Hiragino Kaku Gothic Pro", メイリオ, Meiryo, Osaka, "ＭＳ Ｐゴシック", "MS PGothic", verdana, sans-serif;
    word-spacing: 10px;
}
div.answer-option > div.inline:hover + span.option {
    word-spacing: 10px !important;
}
}
