css:
@-webkit-document url-prefix(https://www.gutenberg.org/ebooks/search) {


/* GLOBAL STYLE */

html,body {
	background: #2d0000 !important;  
    font-family: Segoe UI Light, Calibri, Arial, sans serif !important;
}


/* TOP MENU BAR */

/* Capitalize menu options text */

td#menubar a {
 	text-transform: uppercase !important;
}

/* Change background color of search options, TODO: still needs work */
li.navlink {
    background: #82807a !important;  
}

/* Change the width to half and center it */
#screen-head {
    min-width: 300px;
    width: 55% !important;
    margin: 0px auto !important;
    
}


/* CONTENT / SEARCH RESULTS TITLE */

/* Change the spacing to be more aesthetic */
div#content {
 	margin: 0px auto !important;
    letter-spacing: 0.04em;
    
}
h1 {
    margin: 0px auto !important;
}

/* Change the title width to half and center it */
div.header {
    margin: 0px auto !important;
    padding: 100px 0px 30px 0px;
    width: 55% !important;
    background: #eceadf !important;  
}


/* CONTENT / SEARCH RESULTS */

/* Change the width to half and center it */
div.body {
 	margin: 0px auto;
    width: 55% !important;
    background: #eceadf !important;  
}

/* Make the generic book/audioclip icon larger and centered */

span.icon_book, span.icon_audiobook {
    transform: scale(1.6,1.6);
	margin: 20px;
}

/* Hide the word "downloads" for a cleaner look by using a div to occlude it */

span.extra{
  position: relative;
}
span.extra:before {
    position: absolute;
    width: 75px;
    margin-left: 23px;
    top:0;
    bottom:0;
    z-index:2;
    background: #eceadf !important;  
    content:" "
}

/* Remove underline from book name, and make larger */

span.title{
    text-decoration: none !important;
    font-size: 1.2em;
}

/* Add text color fade on hover effect to book search results */

span.content {
  -o-transition:.5s;
  -ms-transition:.5s;
  -moz-transition:.5s;
  -webkit-transition:.5s;
  /* ...and now for the proper property */
  transition:.3s;
}
span.content:hover {
    color:#982727;
}   


/* ---------------------------- */
/*        READ ONLINE           */
/* ---------------------------- */

/* Hide Gutenberg pre-text */
pre {
	display: none !important;
    
}

h1 {
    background: #eceadf !important;  
}

body p {
    background: #eceadf !important;  
}


}
