css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sfgate.com") {
/* clean up comment buttons */
.hdn-comments {
	padding-top: 0 !important;;
}
.article-footer {
	display: none !important;
	margin: 0 !important;
}
/* hide top deals */
.dealnews {
	display: none !important;
}
/* remove video player mini */
#vembaSticky, #vembaStickyMove, #vemba_zone_vpke9, #vemba_player_wrapper_vpke9 {
	display: none !important;
}

/* shrink gallery flipper */
.hst-lvgallery-container, .hst-resgallery-container {
/*	float: unset !important; */
	margin: 0;
	width: 60% !important;
	padding: 0 !important;
	margin: 0 !important;
}
.article-body .asset_gallery {
	width: 60% !important;
	margin-top: 0 !important;
	margin-left: 10px !important;
	margin-bottom: 15px !important;
	margin-right: 25px !important;
	padding: 0 !important;
}

/* position left slider around */
.no-touch .hst-resgallery-container .hst-resgallery-wedge, .no-touch .hst-resgalleryitem .img-wrap {
	/* height: 432px; */
	padding: 0;
}

.hst-lvgallery-container, .hst-resgallery-container {
	/* float: unset !important; */
	width: 100% !important;
	padding: 0 !important;
	margin: 0 !important;
	margin-right: 25px !important;
	padding-right: 25px !important;

}
.article-body ul.hst-resgallery li.hst-resgalleryitem {
	height: 100%;
	position: relative;
	margin-right: 0px;
}
/* shrink additional video player content such as sports counters */
.article-body .asset_poll, .article-body .asset_relatedlinks, .article-body .asset_factbox, 
  .article-body .asset_photo, .article-body .asset_media, .article-body .asset_item {
	clear: left;
	float: left;
	width: 60%;
	margin-right: 25px;
}
.videoCenter, .jw-controlbar, .jw-display-icon-container, .jw-dock, .jw-logo, .jw-media, 
  .jw-overlays > div, .jw-skip, .hst-freeform {
	display: none !important;
}
/* set width of bottom related links */
.asset_relatedlinks {
	width: 290px !important;
}
/* shrink headlines from 44px */
.article-title .headline, .articletitle .headline {
	font-size: 25px;
	line-height: 110%;
}
}
