css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url('https://www.tumblr.com/'),
  url('http://www.tumblr.com/'),
  url-prefix('http://www.tumblr.com/'),
  url-prefix('https://www.tumblr.com/') {

body {
  
background:#272727 url(http://i63.tinypic.com/2iqiiqb.png) repeat center fixed! important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(0,0,0,0.1) !important;;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(0,0,0,0.1) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
  opacity: 0.0 !important;
}

.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 0.1) !important;
}

.radar { display: none !important }
.l-header-container--refresh {
    box-shadow: 0 0 0 1px hsla(0,0%,100%,.13);
    -webkit-transform: translateZ(0) translateY(0);
    transform: translateZ(0) translateY(0);
    z-index: 90;
    background-color: #ffe5e5;
    position: fixed;
    width: 100%;
    opacity: .9%
}

#return_to_top .return_to_top_icon{
background: url('http://i64.tinypic.com/10yf7s4.png') no-repeat transparent!important}


}
