css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https?://(blog\.)?golang\.org.*$"), domain("godoc.org"), regexp("^https?://localhost:6060.*$")
{
  
  html, body
  {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
    color: #fff !important;
  }
  
  body div#topbar,
  body nav,
  body nav div.container ul li.active a,
  body div#x-footer
  {background-color: transparent !important;}
  
  body div#topbar div#heading a,
  body nav div.container ul li.active a
  {color: #375EAB !important;}
  
  body div#page div.container h2,
  body div#page div.container pre,
  body div#page div.container code,
  body div#page div.container div.playground,
  body div#page div.container div.input,
  body div#page div.container div.output,
  body div#page div.container div.code,
  body div#page div.container textarea.code,
  body div#page div.container div#sidebar,
  body div.container div#x-projnav,
  body div.container pre,
  body div.container div.panel
  {background-color: #111 !important;}
  
  body div.container pre,
  body nav,
  body div.container div.panel,
  body div#x-footer
  {border: none !important;}
  
  body,
  body nav div.container ul li a,
  body div.container pre
  {color: #aaa !important;line-height: 1.4}
  
  body h3.highlighted,
  body h4.highlighted
  {background-color: #4C004C !important;}
  
  body div.play textarea
  {color: #DDD;}
  
  body div.jumbotron
  {background-color: rgba(48, 48, 48, 0.5);}
  
  #page>div.container{margin: auto calc((100vw - 960px)/2) ;}
  img{background: white;}
}
