css:
@-moz-document domain("new.x.sankuai.com") {

::-webkit-input-placeholder{color: rgba(181, 212, 240, 0.24) !important;}


/*消息----------------------------------------------------------------------------------------------*/

/*一级导航*//*一级导航*//*一级导航*/

/*背景*/
.wrapper-main .wrapper-navbar {
    background-color: #313335;
    /* box-shadow: 0px 0px 4px 0px rgba(0, 0, 0, 0.24); */
    width: 60px;
}

/*菜单icon-normal*/
.cust-app-item .icon-app {
    color: rgba(181, 212, 240, 0.38);
    border: none;
}
.cust-app-item {
    border-left: none;
}

/*菜单icon-hover*/
.cust-app-item:hover {
    background-color: rgba(17,139,250,.05);
    border-left: none;
}
.cust-app-item:hover .icon-app {
    color: #B5D4F0;
}

/*菜单icon-选中*/
.cust-app-item.actived .icon-app {
    color: #B5D4F0;
}
.cust-app-item.actived {
    background-color: rgba(17,139,250,.2);
    border: none;
}

/*菜单模块icon-我的空间*/
.cust-app-item svg {
    fill: rgba(181, 212, 240, 0.38);
}
.cust-app-item:hover .icon-app {
    color: #B5D4F0;
    fill: #B5D4F0;
}

/*菜单模块icon-更多应用*/
.ctn-more .dxicon {
    color: rgba(181, 212, 240, 0.38);
}


/*内容区*/
.wrapper-main .wrapper-app-outer .wrapper-app {
    left: 60px;
}


/*消息列表*//*消息列表*//*消息列表*//*消息列表*/

/*背景*/
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-left {
    background: #313335;
}
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-left {
    width: 280px;
}

/*背景分割线*/
.wrapper-left-chat {
    border-right: none;
}

/*搜索框背景*/
.comp-suggest .ctn-ipt>input {
    background-color: rgba(255,255,255,0.05);
    border-radius: 4px;
}

/*搜索框激活*/
.comp-suggest .ctn-ipt>input:focus {
    background-color: rgba(255,255,255,0.05);
    padding-left: 27px;
}

/*搜索icon*/
.comp-suggest .ctn-ipt>.dxicon {
    color: rgba(181, 212, 240, 0.38);
}

/*搜索框下分割线*/
.wrapper-left-chat .wrapper-suggest {
    border-bottom: 1px solid rgba(0, 0, 0, 0);
}

/*发起聊天icon*/
.wrapper-left-chat .wrapper-suggest .message {
    color: rgba(181, 212, 240, 0.5);

}

/*头像40*40*/
.comp-avator.avator-base img, .comp-avator.avator-xlarge img {
    border: 1px solid rgba(255,255,255,0.1);
}


/*列表_hover*/
.comp-session:hover {
    background-color: rgba(0,0,0,.1);
    border-radius: 4px;
    width: 270px;
    height: 100%;
    margin-left: 20px;
}

/*列表选中*/
.comp-session.selected {
    background: #2B2B2B;
    border-radius: 4px;
    width: 270px;
    height: 100%;
    margin-left: 20px;
}

/*主标题栏*/
.comp-session .name {
    margin-top: 1px;
}

/*主标题文字*/
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-left {
    color: #B5D4F0;
}

/*副标题栏*/
.comp-session .desc {
    color: rgba(181, 212, 240, 0.54);
    margin-top: 3px;
}

/*时间线*/
.comp-session .time {
    color: rgba(181, 212, 240, 0.38);
}

/*置顶*/
.comp-session.top:after {
    border: 6px solid rgba(17,139,250,0.1);
    border-bottom-color: transparent;
    border-left-color: transparent;
}

/*关闭提醒icon*/
.comp-session .notice {
    color: rgba(181, 212, 240, 0.3);
}


/*会话窗口*//*会话窗口*//*会话窗口*//*会话窗口*/

/*整体*/
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-right .wrapper-right-outer {
    left: 280px;
}

/*会话窗口背景左分割线*/
.wrapper-right-chat .wrapper-right-outer {
    border-left: none;
}
.medium-size .wrapper-right-background .wrapper-right-inner {
    border: none;
    box-shadow: none;
}
.medium-size .wrapper-right-background .wrapper-right-inner.wrapper-chat-inner {
    left: 0px!important;
    right: 0px!important;
    top: 0px!important;
    bottom: 0px!important;
}

/*搜索弹出框*/
.comp-suggest .ctn-result .ctn-list {
    padding: 0 10px 10px;
}
.comp-suggest .ctn-result {
    border-radius: 0 0 4px;
    background-color: #404040;
    color: #b5d4f0;
    border: none;
    box-shadow: 0 4px 16px 0 rgba(0,0,0,0.5);
}
.wrapper-left-chat .wrapper-suggest .suggest-session .ctn-result {
    top: 40px;
    left: 0px;
    bottom: 40px;
    width: 340px;
    height: 100vh;
    max-height: 90vh;
    padding-bottom: 0px;
    overflow: hidden;
    border-radius: 4px;
}
.wrapper-left-chat .wrapper-suggest .suggest-session .tab-header {
    background: rgba(64, 64, 64, 0);
}
.comp-suggest .ctn-result .ctn-list .item .avator {
    margin-right: 15px;
}
.comp-suggest .ctn-result .ctn-list .item .info .detail {
    max-width: 300px;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    display: block;
    margin-top: 0px;
    font-size: 12px;
    color: rgba(181, 212, 240, 0.5);
}

/*vcard*/
.comp-dialog .comp-dialog-inner {
    background: #404040;
    box-shadow: 0 4px 16px 0px rgba(0, 0, 0, 0.5);
    border: none;
}
.person-vcard-dialog .content .ctn-content .list .item .tips {
    margin-right: 10px;
    color: rgba(181, 212, 240, 0.5);
    width: 36px;
}
.person-vcard-dialog .content .ctn-content .list .item {
    color: #b5d4f0;
}
.person-vcard-dialog .footer {
    border-top: 1px solid rgba(255,255,255,0.05);
}
.person-vcard-dialog .wrapper-uservcard .footer .btn:first-child {
    border-right: 1px solid rgba(255,255,255,0.05);
}
.person-vcard-dialog .wrapper-uservcard .footer {
    font-size: 14px;
    line-height: 39px;
}

/*表情弹层*/
.smiley-container .wrapper-smiley {
    background-color: #404040;
    box-shadow: 0 4px 16px 0 rgba(0, 0, 0, 0.5);
    border: none;
    border-radius: 4px;
}
.smiley-container .wrapper-smiley .smiley-panel .icon-smiley-emotions {
    border: 1px solid rgba(255,255,255,0);
    margin: 2px;
}
.smiley-container .wrapper-smiley .smiley-tabbar {
    background-color: #505050;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
}
.smiley-container .wrapper-smiley .smiley-tabbar .tabbar-item.active {
    background-color: #404040;
}
.smiley-container .wrapper-smiley .smiley-tabbar .tabbar-item {
    width: 56px;
}
.smiley-container .wrapper-smiley .smiley-tabbar .arrow-down {
    background: #505050;
}




/*右键菜单弹层*/
.comp-contextmenu .dropdown-menu {
    background-color: #404040;
    border: none;
    padding: 4px 0;
    box-shadow: 0 4px 16px 0 rgba(0, 0, 0, 0.5);
}
.comp-contextmenu .dropdown-menu li {
    color: #b5d4f0;
}
.comp-contextmenu .dropdown-menu li:hover {
    background-color: #118bfa;
    color: #ffffff;
}



/*标题栏背景、标题文字大小*/
.titlebar {
    background-color: #2B2B2B;
    border-bottom: 1px solid rgba(255,255,255,0.0);
    font-size: 18px;
}

/*标题文字*/
.wrapper-main .wrapper-app-outer .wrapper-app .wrapper-right .wrapper-right-outer {
    color: #B5D4F0;
}


/*下拉菜单入口icon*/
.titlebar .title .title-menu-panel .title-menu-icon {
    color: rgba(181, 212, 240, 0.5);
}

/*返回旧版入口*/
.wrapper-main .oldversion-entry img {
    width: 0px;
    height: 0px;
}

/*标题栏右边icon*/
.titlebar .ctn-oper .item .dxicon {
    font-size: 24px;
    color: rgba(181, 212, 240, 0.5);
}

/*内容区背景*/
.wrapper-msglist {
    background-color: #2B2B2B;
}

/*聊天成员名_对方*/
.bubble-item.you .talker-name {
    color: #4996ca;
}

/*聊天成员名_自己*/
.bubble-item.me .talker-name {
    color: #a78a40;
}

/*左气泡背景*/
.medium-size .message-text .content {
    background-color: rgba(17,139,250,0.1);
}
.medium-size .message-text {
    background-color: rgba(17,139,250,0);
}

/*左气泡尖角背景*/
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.left.lfc {
    color: rgba(17,139,250,0.1);
}

/*左气泡尖角偏移*/
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.left {
    left: -7px;
}

/*气泡文字*/
.wrapper-msglist {
    color: #B5D4F0;
}

/*右气泡背景*/
.medium-size .me .message-text .content {
    background-color: rgba(255,255,255,0.05);
}
.medium-size .me .message-text {
    background-color: rgba(255,255,255,0);
}

/*右气泡尖角背景*/
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.right.rfc {
    color: rgba(255, 255, 255, 0.05);
}

/*右气泡尖角偏移*/
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.right {
    right: -7px;
}

/*时间线文字*/
.medium-size .wrapper-msglist-inner .time-line .time {
    color: rgba(181, 212, 240, 0.38);
}

/*时间线分割线*/
.medium-size .wrapper-msglist-inner .time-line .line {
    border-top: 1px solid rgba(0,0,0,.0);
}

/*靠左模式-时间线*/
.bubble-item .message-item .right-content .person .bubble-item-time {
    color: rgba(181, 212, 240, 0.38);
}

/*被引用消息*/
.message-text .quoted-text {
    color: rgba(181, 212, 240, 0.5);
}
.message-text .quoted-msg-gray {
    color: rgba(181, 212, 240, 0.5);
}

/*气泡展示区下分割线*/
.wrapper-msglist .msglist .wrapper-msglist-inner {
    border-bottom: 1px solid rgba(255, 255, 255, 0.0);
}

/*气泡展示区-引用icon*/
.bubble-item .message-item .right-content .message-container .quote-msg-btn {
    color: rgba(181, 212, 240, 0.5);
}

/*气泡展区-下拉加载文字*/
.comp-dynamic-loader .wrapper-loading {
    color: rgba(181, 212, 240, 0.38);
    font-size: 12px;
}
.comp-loading .loading {
    -webkit-animation: .85s loadingCircle steps(8) infinite;
    animation: .85s loadingCircle steps(8) infinite;
    width: 14px;
    height: 14px;
    background-size: cover;
}

/*气泡展区-系统消息*/
.message-event {
    color: rgba(181, 212, 240, 0.38);
    margin: 0 0 14px;
}

/*气泡展区-图片消息*/
.medium-size .me .message-image {
    background-color: rgba(255,255,255,0.1);
}
.medium-size .you .message-image {
    background-color: rgba(17,139,250,0.1);
}
.message-image .image-mask, .message-image>img {
    max-height: 260px;
}

/*气泡展区-长文本消息*/
.medium-size .you .message-long-text {
    background-color: rgba(17,139,250,0.1);
}
.message-long-text .long-text-info {
    color: rgba(181, 212, 240, 0.38);
}

/*气泡展区-回执消息*/
.bubble-item .receipt-content .tag-receipt {
    color: rgba(181, 212, 240, 0.24);
}
.bubble-item .receipt-content {
    color: rgba(181, 212, 240, 0.38);
}
.bubble-item.you .receipt-status {
    color: rgba(181, 212, 240, 0.5);
}

/*气泡展区-单图文消息*/
.message-link.cochat, .message-link.cschat, .message-link.pubchat {
    border: 1px solid rgba(17,139,250,0.1);
}
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.left.llc {
    color: rgba(17,139,250,0.1);
}

/*气泡展区-多图文消息*/
.message-multilink .first-link-message {
    border: none;
}
.message-multilink .other-link-message {
    border: none;
}

/*气泡展区-文件消息*/

.message-file {
    background-color: rgba(255,255,255,0.05);
    border: none;
}
.message-file .file-name a {
    color: #b5d4f0;
    text-decoration: none;
}
.message-file .file-content .file-info .file-size {
    color: rgba(181, 212, 240, 0.38);
}
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.right.rlc {
    color: rgba(255,255,255,0);
}
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.right.rfc.is-line {
    right: -7px;
    color: rgba(255,255,255,0.05);
}
.medium-size .you .message-file {
    border: none;
    background-color: rgba(17,139,250,0.1);
}
.medium-size .bubble-item .message-item .right-content .bubble-corner-container .bubble-corner.left.lfc.is-line {
    left: -7px;
    color: rgba(17,139,250,0.1);
}

/*气泡展区-行事历消息*/
.message-calendar {
    background-color: rgba(255,255,255,0.05);
    border: 1px solid rgba(255,255,255,0);
}
.message-calendar .message-calendar-content {
    color: rgba(181, 212, 240, 0.38);
}
.message-calendar .message-calendar-content .content-line .message-calendar-location {
    color: #b5d4f0;
}
.message-calendar hr {
    background-color: rgba(255,255,255,0.05);
}

/*气泡展区-地图消息*/
.medium-size .me .message-gps {
    background-color: rgba(255,255,255,0.05);
}
.message-gps {
    border: 1px solid rgba(255,255,255,0);
}
.message-gps .location-simple .message-gps-name {
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
}

/*气泡展区-个人名片消息*/
.message-vcard {
    border: 1px solid rgba(255,255,255,0);
    background-color: rgba(255,255,255,0.05);
}
.message-vcard .message-vcard-title {
    height: 12px;
    line-height: 12px;
    color: rgba(181, 212, 240, 0.38);
}
.message-vcard hr {
    background-color: rgba(255,255,255,0.05);
}
.message-vcard .message-vcard-info .message-vcard-content .message-vcard-name {
    color: #b5d4f0;
}
.message-vcard .message-vcard-info .message-vcard-content .message-vcard-org {
    color: rgba(181, 212, 240, 0.5);
}

/*输入框-功能icon*/
.message-send .dxicon {
    color: rgba(181, 212, 240, 0.5);
}

/*输入框-快捷键提示文案*/
.text-input>span {
    color: rgba(181, 212, 240, 0.24);
    margin: 5px 10px;
    line-height: 26px;
}

/*输入框-背景*/
.text-input {
    width: inherit;
    height: 62px;
    position: absolute;
    top: 38px;
    right: 20px;
    left: 20px;
}
.text-input textarea {
    padding: 4px 8px;
    background-color: rgba(255,255,255,0.05);
    border-radius: 4px;
}

/*输入框-发送按钮*/
.text-input .send-message-button {
    margin: 5px 0;
    width: 50px;
    height: 26px;
}
.text-input .send-message-button {
    font-size: 12px;
}


/*群信息*//*群信息*//*群信息*//*群信息*//*群信息*/

/*群信息-展开背景*/
.wrapper-right-chat .wrapper-right-outer .wrapper-right-background .wrapper-right-inner.wrapper-chat-inner.showslidebar .slidebar {
    background-color: #313131;
}

/*群信息-背景展开尖角*/
.titlebar .ctn-oper .item.item-group .arrow {
    color: rgba(0,0,0,0);
}
.titlebar .ctn-oper .item.item-group .arrow-top {
    color: #313131;
}

/*群信息-分割线*/
.bubble-group .bubble-group-panel {
    padding-left: 20px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.05);
    position: relative;
    overflow: auto;
}

/*群信息-标题icon*/
.bubble-group .bubble-group-panel .bubble-group-panel-title .bubble-group-panel-icon {
    color: rgba(181, 212, 240, 0.38);
}

/*群信息-标题文字*/
.bubble-group .bubble-group-panel .bubble-group-panel-title {
    color: rgba(181, 212, 240, 0.5);
}

/*群信息-添加成员icon*/
.group-members-list .member-add .group-add-member-icon {
    color: rgba(181, 212, 240, 0.5);
}

/*群信息-添加成员文字*/
.group-members-list .member-add .name {
    color: rgba(181, 212, 240, 0.5);
}

/*群信息-成员名*/
.group-members-list .member .name {
    color: rgba(181, 212, 240, 1);
}

/*群信息-群资料*/
.bubble-groupinfo .bubble-groupinfo-title {
    color: rgba(181, 212, 240, 0.38);
}
.bubble-groupinfo .bubble-groupinfo-name {
    color: #b5d4f0;
}
.bubble-groupinfo .bubble-groupinfo-managers .bubble-groupinfo-manager span {
    color: #b5d4f0;
}
.wrapper-group-notice .head, .wrapper-group-notice .title {
    color: rgba(181, 212, 240, 0.38);
}


/*个人信息*//*个人信息*//*个人信息*//*个人信息*//*个人信息*/
.bubble-personinfo .bubble-personinfo-info .bubble-personinfo-passport {
    color: rgba(181, 212, 240, 0.5);
}
.bubble-personinfo .bubble-personinfo-info .bubble-personinfo-org {
    color: rgba(181, 212, 240, 0.5);
}
.bubble-personinfo .bubble-personInfo-static {
    color: rgba(181, 212, 240, 0.38);
}
.bubble-personinfo-add-member {
    color: rgba(181, 212, 240, 0.5);
}
/*公众号信息*/
.bubble-pubinfo .bubble-pubinfo-info .bubble-pubinfo-passport {
    color: rgba(181, 212, 240, 0.5);
}
.bubble-pubinfo .bubble-pubinfo-static {
    color: rgba(181, 212, 240, 0.5);
    line-height: 14px;
}


/*群组----------------------------------------------------------------------------------------------*/


/*群组列表-标题*/
.groupList-wrapper .room .groupName {
    max-width: 175px;
}

/*群组列表-头像*/
.groupList-wrapper .room .avatar {
    margin: 0 15px 0 20px;
}

/*内容区-背景*/
.wrapper-right-group .wrapper-right-outer {
    border: none;
    background-color: #2b2b2b;
}

/*内容区-标题栏-分割线*/
.infoPanel .header-wrapper {
    border-bottom: 1px solid rgba(181,212,240,0.05);
}

/*内容区-群成员-群主名*/
.infoPanel .content .groupMembers .members .moderator {
    color: #ff9801;
}

/*内容区-群成员个数*/
.infoPanel .header .header-left .membersNum {
    color: rgba(181, 212, 240, 0.5);
}

/*内容区-群设置小标题*/
.infoPanel .content .session .title {
    color: rgba(181, 212, 240, 0.5);
}
.infoPanel .content .session .editIcon {
    color: rgba(181, 212, 240, 0.5);
}







/*通讯录----------------------------------------------------------------------------------------------*/

/*列表-层级icon*/
.comp-tree .node-container>.header-wrapper .toggle>.dxicon {
    color: rgba(181, 212, 240, 0.38);
}

/*内容区-背景*/
.wrapper-roster-right .wrapper-right-outer {
    background-color: #2b2b2b;
}

/*内容区-用户信息-mis号*/
.wrapper-ctn .ctn-member .header .basic-info .account {
    color: rgba(181, 212, 240, 0.5);
}

/*内容区-用户信息-副标题*/
.wrapper-ctn .ctn-member .ctn-person-info .info-item>.title {
    color: rgba(181, 212, 240, 0.5);
    margin-left: -50px;
}




/*设置----------------------------------------------------------------------------------------------*/

/*大背景*/
.wrapper-setting {;
    background-color: #313131;
}

/*主要文字*/
.wrapper-setting {
    color: #b5d4f0;
}

/*提示文字*/
.wrapper-setting .ctn-setting .subitems .item .tips {
    color: rgba(181, 212, 240, 0.38);
}

/*单选框*/
.comp-checkbox .dxicon.dxicon-uncheck {
    color: rgba(181, 212, 240, 0.5);
}




}
