css:
@namespace url(http://www.w3.org/1999/xhtml);

/*	Created by Kyle-E	*/ 

@-moz-document domain("katcr.co") {

    
                         /* imgre */    
    
img[src="https://katcr.co/community/Themes/default/images/smflogo.png"] {
    background: rgba(0, 0, 0, 0) url("https://extraimago.com/images/2016/07/30/fb4eb610350e8d09a23c819c6e1a00ef.png") no-repeat scroll 0 0 !important;
    box-sizing: border-box !important;
    height: 41px !important;
    padding-left: 231px !important;
    width: 231px !important;
}
    
img[src="https://katcr.co/community/Themes/default/images/useroff.gif"], img[src="https://katcr.co/community/Themes/default/images/buddy_useroff.gif"] {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    border: 3px solid #ca845f;
    border-radius: 50px;
    box-sizing: border-box;
    display: inline-block;
    height: 4px;
    line-height: 1px;
    padding-left: 4px;
    padding-top: 4px;
    vertical-align: baseline;
    width: 4px;
}

img[src="https://katcr.co/community/Themes/default/images/useron.gif"], img[src="https://katcr.co/community/Themes/default/images/buddy_useron.gif"], img[src="https://katcr.co/static/privatevpn/10Off-Coupon-privatevpn.png"] {
	background: rgba(0, 0, 0, 0) none repeat scroll 0 0;
    border: 3px solid #56cc41;
    border-radius: 50px;
    box-sizing: border-box;
    display: inline-block;
    height: 4px;
    line-height: 1px;
    padding-left: 4px;
    padding-top: 4px;
    vertical-align: baseline;
    width: 4px;
}
    
img[src="https://katcr.co/static/seed4me/kat-b-ani.gif"], img[src="https://katcr.co/static/sponsor/seedpleaase_272x90.gif"], img[src="https://katcr.co/static/privatevpn/privatepvn_logo.gif"] {
    display: none;
}
    
iframe[src="https://www.youtube.com/embed/rHCt2hemsKs?rel=0;autoplay=0"], a[href^="https://affiliate.privatevpn.com/scripts/click.php"] {
    display: none;     
}
    
/*************************************************/

  
a:link, 
a:visited, 
a.new_win:link, 
a.new_win:visited {
    color: #968e76;
} 
   
a:link:hover {
    color: #c8bd9e;
}
  
body {
    background: #303030 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAIAAAAmkwkpAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAClJREFUeNpiFBQU5OXlZQADJmFhYQYYYPr16xeQ+vz5M4gDEYPIAwQYAJUPBptp0pKhAAAAAElFTkSuQmCC") repeat scroll 0 0 !important;
}
    
body.rich_editor {
    background: #333 none repeat scroll 0 0 !important;
    color: #fff !important;
}

body, td, th, tr {
    color: #a1a1a1;
}
    
.content {
    background: #333 none repeat scroll 0 0;
    border-radius: 3px;
    color: #fff;
}
    
h1, h2, h3, h4, h5, h6 {
    color: #968e76;
}
    
          /* Tables noot noot */
tr:nth-of-type(2n), 
.table_list tbody.content tr:hover, .table_grid tbody tr:hover {
    background: #232323;
}

tr:nth-of-type(2n+1), 
.table_list tbody.content tr:nth-child(2n+1), .table_grid tbody tr:nth-child(2n+1) {
    background: #333333;
} /**/
    
    
/*************************************************/    
    
    

.birthday {
    color: violet;
    text-shadow: -1px 1px #191919;
}
    
    .event {
        color: #30b230;
        text-shadow: -1px 1px #191919;
    }

.calendar_today {
    background-color: #444;
    border: 1px solid #666;
}
    
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th {
    background: rgba(0, 0, 0, 0) none repeat scroll 0 0 !important;
}
    
    .table_grid tr.catbg, .table_grid tr.titlebg, table.table_grid td {
        border-bottom: 1px solid #3f3f3f!important;
    }
    
       .table_grid .catbg th, table.table_grid td {
          border-right: 1px solid #3f3f3f!important;
       }
    
          .table_grid .catbg th:last-child, table.table_grid td:last-child {
             border-right: none!important;
           }

    
code.bbc_code {
    background: #121212 none repeat scroll 0 0;
    border: 1px solid #666;
    color: #e9e9e9;
    padding: 5px 1em 3px;
}
       
.counter {
    color: #848484;
}
    
.description, .description_board, .plainbox {
    background: #303030 none repeat scroll 0 0;
    border: 1px solid #353535;
}
    
#display_jump_to > input, #message_index_jump_to > input {
    cursor: pointer;
    text-transform: uppercase;
}
       
div#profile_success {
    background: #203021 none repeat scroll 0 0;
    border-color: #2c412c;
    color: lightgreen;
    text-shadow: -1px 1px 0 #191919;
}
    
#siteslogan,/* img#smflogo,*/ img#upshrink {
    visibility: hidden;
} 
    
#upper_section {
    margin-bottom: 0;
}
    
.pagelinks a.navPages /*.pagelinks strong*/ {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #4c4c4c 0%, #464646 100%) repeat scroll 0 0;
    border-radius: 2px;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.08) inset, 0 -1px 0 0 rgba(0, 0, 0, 0.03) inset, 0 1px 0 0 rgba(255, 255, 255, 0.2) inset;
    text-shadow: -1px 1px 0 #191919;    
}
    
.pagelinks a.navPages:hover, .pagelinks strong {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #3c3c3c 0%, #363636 100%) repeat scroll 0 0;
    color: #fff;
    text-shadow: -1px 1px #3c3a37;
}
  
#footer_section, 
#footer_section div.frame, 
span.lowerframe, 
span.lowerframe span, 
span.upperframe, 
span.upperframe span {
    background: transparent none repeat scroll 0 0;
}
    
.error {
    color: darkorange;
    text-shadow: -1px 1px #191919;
}
    
.errorbox {
    background-color: #3d3938;
    border: 2px solid #393534;
    margin-bottom: 1em;
    margin-top: 1em;
    padding: 1em;
}
    
    
/*.description, .description_board, */ .plainbox {
    background: #282828 none repeat scroll 0 0;
    border: 1px solid #252525;
}  
    
.quoteheader {
    color: #999;
}
    
#recent #unread.tborder.topic_table table tr td span a {
    color: #d1d1d1;
}
    
.rich_editor_frame {
    background: #333 none repeat scroll 0 0;
    width: 100% !important; /* "Preview" makes the frame go 90% for some reason. */
}
    
.richedit_resize {
    background: #1e1e1e url("https://katcr.co/community/Themes/default/images/bbc/resize-handle.gif") no-repeat scroll 50% 1px;
    border-color: #808080;
    border-radius: 0 0 2px 2px;
}
        
#upper_section div.news {
    text-align: left;
}
  
.windowbg, .windowbg2, .windowbg3 {
    color: #a1a1a1;
}
    #preview_body, h3.catbg a:hover, h4.catbg a:hover, .table_list tbody.header td a:hover{
        color: #fff;
    }
    
.windowbg span.topslice, .windowbg2 span.topslice, .windowbg3 span.topslice {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #3d3938 0%, #353231 100%) repeat scroll 0 0;
    border-radius: 3px;
    border-top: 1px inset #b5a3a2;
}
    
.windowbg.description {
    background: #3d3938 none repeat scroll 0 0;
    border: 2px solid #393534;
    color: #fff;
}    
    
    
    /******************* Grouped ***************************/

          /*Header*/
#header {
    background: transparent none repeat scroll 0 0;
    box-shadow: none;
    margin-bottom: 0;
    padding: 0;
}
    
#header, #header a, #header h2 {
    color: #faecc5;
}
    
#header div.frame {
    padding: 0;
}
    
h1.forumtitle {
    margin: 5px;
}
    
#top_section {
    background: rgba(0, 0, 0, 0) linear-gradient(#323232, #282828) repeat scroll 0 0;
    box-shadow: 0 1px rgba(255, 255, 255, 0.1) inset, 0 1px 3px rgba(0, 0, 0, 0.5);
    height: 48px;
    min-height: 48px;
}    
              /*nav*/
        #main_menu {
            float: right;
            position: absolute;
            right: 40px;
            top: 0;
            width: auto;
        }
        .dropmenu li a.firstlevel {
            background: transparent none repeat scroll 0 0;
        }
        .dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel {
            background: transparent none repeat scroll 0 0;
            box-shadow: 0 1px rgba(255, 255, 255, 0.1) inset, 0 1px 3px rgba(0, 0, 0, 0.5);
        }
        .dropmenu li ul {
            background: #282828 none repeat scroll 0 0;
            border-radius: 0 0 3px 3px;
            box-shadow: 0 3px 3px rgba(0, 0, 0, 0.5);
        } 
        .dropmenu li li a:hover, .dropmenu li li:hover > a {
            background: rgba(0, 0, 0, 0) linear-gradient(#323232, #282828) repeat scroll 0 0 !important;
            border-radius: 3px;
            box-shadow: 0 1px rgba(255, 255, 255, 0.1) inset, 0 1px 3px rgba(0, 0, 0, 0.5) !important;
        } /**/  
    
                  /* Avatar */    
            #header #upper_section .avatar img {
                border: 3px solid #111;
                border-radius: 33px;
                margin: 0;
                max-height: 35px;
                max-width: 35px;
                position: absolute;
                right: 9px;
                top: 4px;
            } /**/
    /**/

    
    
          /* Header Search */
div#upper_section div.news form {
    left: 235px;
    padding-bottom: 0;
    position: absolute;
    top: 9px;
}

#header #search_form [name="search"] {
    background: #1e1e1e none repeat scroll 0 0;
    border: medium none;
    border-radius: 2px;
    box-shadow: 0 1px 0 1px rgba(135, 135, 135, 0.2);
    box-sizing: border-box;
    color: #7c6a59;
    font-size: 14px;
    line-height: 20px;
    margin: 9px 0 0 -5px;
    padding: 5px 60px 5px 8px;
    position: relative;
    width: 330px;
    z-index: 1;
}
    ::-webkit-input-placeholder {
        color: #7c6a59;
    }

.news.normaltext #search_form .button_submit, #header #search_form [name="search"] + input {
    background: transparent url("https://extraimago.com/images/2016/07/31/a3a3c0deb02e1b4d8f17f395d0896748.png") no-repeat scroll 0 0;
    border-radius: 0;
    box-shadow: none;
    color: transparent;
    font-size: 0;
    height: 20px;
    margin-left: 0;
    margin-right: 0;
    margin-top: 5px;
    position: relative;
    right: 30px;
    top: -5px;
    text-shadow: none;
    width: 20px;
    z-index: 3;
}          
          form#search_form {
             color: transparent;
             font-size: 0;
}

          form#search_form font, form#search_form > a {
             display: none;
}/**/

  
    

          /* Category header */
h4.titlebg, h3.titlebg {
    background: transparent; /*#4c4c4c none repeat scroll 0 0;*/
    color: #968e76;
    padding-left: 9px;    
}
    
div.cat_bar {
    background: transparent none repeat scroll 0 0;
    padding-left: 0;
}
    
h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg, div.title_bar {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #505050 0%, #494949 100%) repeat scroll 0 0 !important;
    border-color: #454545 #313131 #313131 -moz-use-text-color;
    border-style: solid solid solid none;
    border-width: 1px 1px 1px medium;
    padding-left: 9px;
} /**/
   
    
    
          /* Button & Search */
.buttonlist ul li a {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #4c4c4c 0%, #464646 100%) repeat scroll 0 0;
}
	
.buttonlist ul li a:hover, .buttonlist ul li a.active {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #424242 0%, #3c3c3c 100%) repeat scroll 0 0;
    color: #fff;
}
    
.button_submit, .button_reset {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #4c4c4c 0%, #464646 100%) repeat scroll 0 0;
    border: medium none;
    border-radius: 2px;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.08) inset, 0 -1px 0 0 rgba(0, 0, 0, 0.03) inset, 0 1px 0 0 rgba(255, 255, 255, 0.2) inset;
    text-shadow: 0 1px 0 #191919;   
}
    
.button_submit:hover, .button_reset:hover {
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, #424242 0%, #3c3c3c 100%) repeat scroll 0 0;
}
    
input, button, select, textarea {
    background: #333 none repeat scroll 0 0;
    border: 1px solid #777;
    border-radius: 3px;
    color: #fff;
    font: 13px/115% verdana,Helvetica,sans-serif;
    padding: 2px 5px 3px;
} 
    
input:hover, textarea:hover, button:hover, select:hover, input:focus, textarea:focus, button:focus, select:focus {
    border: 1px solid #888;
}

.roundframe .padding .button_submit.floatright {
    margin-bottom: 8px;
} /**/
    
    
    
          /* Post Wrapper*/
.post, .personalmessage {
    color: #fff;
}
    
.poster {
    border-right: medium none;
    font-size: 12px;
    width: 233px;
}
    
.poster h4 {
	margin-right: -40px;
    padding: 0 0 3px;
}

.poster h4, .poster h4 a {
    color: #968e76;
    padding-right: 2px;
}
    
.keyinfo {
    padding-top: 2px;
}
    
.post_wrapper {
    background: #333333 url("https://extraimago.com/images/2016/07/30/6e1ef713d6a631782ab085b6da2ba970.png") repeat-y scroll 249px 0 !important;
    border-radius: 3px;
    padding: 0 5px;
} 
    
.modifybutton {
    float: none;
    left: 97%;
    position: relative;
} /**/
    


          /* Persoal Message - Post Area */   
.postarea, .moderatorbar {
    background: #333333 url("https://extraimago.com/images/2016/07/30/6e1ef713d6a631782ab085b6da2ba970.png") repeat-y scroll 249px 0 !important;
    border-radius: 3px;
    margin: 0;
    padding-left: 270px;
} /**/
    
    
    
          /*Forum table*/
table {
    border-spacing: 0;
}
    
.table_list tbody.content td.info a.subject {
    color: #d6d6d6;  
}
    
.table_list tbody.content tr td, .table_grid tbody tr td {
    font-size: 12px;
}
 
.table_grid tbody tr td div span a, .table_grid tbody tr td div span a:hover {
    color: #d1d1d1;
}
    
tr.windowbg td, tr.windowbg2 td, tr.approvebg td, tr.highlight2 td, 
.table_list tbody.content tr td, .table_grid tbody tr td {
    border-bottom: 1px solid #3f3f3f;
    border-right: 1px solid #3f3f3f;
}
    tr.windowbg td:last-child, tr.windowbg2 td:last-child, tr.approvebg td:last-child, tr.highlight2 td:last-child {
        border-right: none;
    }

.divider tr {
    background: transparent none repeat scroll 0 0 !important;
} /**/    


    
          /* Sticky|Locked topics bg color */  
.stickybg {
    background: #33322e none repeat scroll 0 0;
}
    
.topic_table td.stickybg2 {
    background-color: #33322e;
}
    
.lockedbg {
    background: #332e2e none repeat scroll 0 0;
}
    
.topic_table td.lockedbg2 {
    background-color: #332e2e;
} /**/



          /*KAT Community - Info Center | footer*/
.roundframe {
    background: #282828 none repeat scroll 0 0;
    border: 1px solid #383838;
    border-radius: 3px;
    padding: 0;
} 
    
div.title_barIC,
div.title_barIC h4.titlebg {
    background: #353535 none repeat scroll 0 0;
} /**/
    
    
    
          /* Quote posts*/    
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    background-color: #292929;
    border: 1px solid #b5ac8b;
    border-top: 1px solid #8e8978!important;
    color: #999;
}
    
.quoteheader {
    background-color: #252525;
    border: 1px double #b5abab !important;
} /**/
    
    
    
          /* Recent posts */   
#recent_posts_content.hslice {
    background: transparent;
}    
    
dl#ic_recentposts dt {
    border-bottom: 1px solid #3f3f3f;
    font-size: 12px;
    padding: 4px 6px;
}

dl#ic_recentposts dd {
    border-bottom: 1px solid #3f3f3f;
    padding: 4px 6px;
}
    
#ic_recentposts strong a {
    color: #d6d6d6;
}
      
#ic_recentposts.middletext dt:nth-of-type(2n), #ic_recentposts.middletext dd:nth-of-type(2n)  {
    background: #232323;
}
    
#ic_recentposts.middletext dt:nth-of-type(2n+1), #ic_recentposts.middletext dd:nth-of-type(2n+1)  {
    background: #333333;
} /**/
 
    
    
 /* Profile */
#main_admsection #basicinfo h4 {
    color: #fff;
}


}
