css:
@-moz-document url-prefix("https://netpeak.planfix.ru") {
.main-menu .main-menu-head::after {
    background: #00B8E4 !important;
    box-shadow: 0 0 11px 1px #005165;
}
.main-menu.initial-background .main-menu-head .cube-menu-tail-start {
    background: #00b8e4 !important;
    box-shadow: 0 0 11px 1px #007e9c !important;
}

.main-menu.initial-background .main-menu-head {
    background: #007e9c !important;
}

.main-menu, .main-menu.initial-background {
    background: #00B8E4 !important;
}
.main-menu .main-menu-head {
    background: #007E9C !important;
}
.main-menu-logo {
    height: 37px;
    background: url('https://sites.google.com/site/fireblastnetpeak/home/newlogo.png') no-repeat;
    margin: 8px 0 0 15px;
}
.main-menu .main-menu-tail::after {
    border-bottom: 1px solid #00627a !important
}
.tr-taskstatus-color-008000,
.tr-taskstatus-color-008000 a,
.tr-taskstatus-color-008000 a:hover {
    color: #00b8e4;
}
input.btn.ui-button.ui-state-hover {
    border: 1px solid #007e9c;
    background: #00b8e4;
}
a:hover {
    color: #007e9c;
}
.tooltip-actions {
    background: #e2f1f5;
    border-top: 1px solid #00b8e4;
    border-bottom: 1px solid #00b8e4;
}
.background-operation-progress .bar {
    background: #00B8E4;
}
.background-operation-progress .peg {
    box-shadow: 0 0 10px #00B8E4, 0 0 5px #00B8E4;
}
.b-green-button {
    background: #007e9c!important;
}
.main-menu .main-menu-head::after,
.secondary-top-menu .main-menu-head::after,
.main-menu .secondary-menu-head::after,
.secondary-top-menu .secondary-menu-head::after {
    background: #00B8E4!important;
    box-shadow: 0 0 11px 1px #007E9C!important;
}
.secondary-top-menu {
    background: #00b8e4!important;
}
.main-menu .main-menu-head,
.secondary-top-menu .main-menu-head,
.main-menu .secondary-menu-head,
.secondary-top-menu .secondary-menu-head {
    background: #007E9C!important;
}
.empty-message img {
    display: none!important;
}
.action-notify-faces li.action-notify-user-notifyed div.state,
.action-notify-faces.action-notify-faces-hidden li.action-notify-user-notifyed div.state {
    background: url('https://sites.google.com/site/fireblastnetpeak/home/16px.png') no-repeat 0 -304px!important;
}
.action-notify-faces.action-notify-faces-hidden li div.state {
    background: url('https://sites.google.com/site/fireblastnetpeak/home/16px.png') no-repeat 0 -416px!important;
}
.upload-file-block .upload-file-delete {
    background: transparent url('https://sites.google.com/site/fireblastnetpeak/home/16px.png') no-repeat 0 -64px!important;
}
.td-analitic-access-h .b-analitic-access-h-switch-hidden .b-analitic-access-h-switch-ico {
    background: url('https://sites.google.com/site/fireblastnetpeak/home/16px.png') no-repeat 0 -976px!important;
}
.td-analitic-access-h .b-analitic-access-h-switch-ok .b-analitic-access-h-switch-ico {
    background: url('https://sites.google.com/site/fireblastnetpeak/home/16px.png') no-repeat 0 -416px!important;
}
.b-ddl-menu ul li.b-ddl-menu-li-multiple span:first-child {
    background: url('https://sites.google.com/site/fireblastnetpeak/home/16px.png') no-repeat 0 -2064px!important;
}
.b-ddl-menu ul li.b-ddl-menu-li-multiple.b-ddl-menu-li-selected span:first-child {
    background: url('https://sites.google.com/site/fireblastnetpeak/home/16px.png') no-repeat 0 -2080px!important;
}
.tr-check-list-empty {
    border-left: 2px solid #007e9c;
}
.iconset-16px {
    background-image: url('https://sites.google.com/site/fireblastnetpeak/home/16px.png')!important;
}
.tr-taskstatus-color-008000.fakelink-dashed, .tr-taskstatus-color-008000.chronicle-item-expand, .task-current-status .tr-taskstatus-color-008000 {
    color: #007d9c;
    border-color: #00b8e4;
}
.main-menu-items .main-menu-item.main-menu-active-item {
    background: #007e9c;
}
.planfixcardmenu tr .link .menu-text .menu-text-group .menu-link {
    color: #00b8e4 !important;
    border-bottom-color: transparent !important;
}
.cardmenu tr.active .link, .planfixcardmenu tr.active .link {
    margin-right: -8px;
    background: #007d9c;
}
.leftmenu-new-item-container .leftmenu-new-item-edit-text .leftmenu-new-item-edit-btn {
    color: #00b8e4;
    margin-left: 10px;
    border-bottom: 1px dotted #007e9c;
    cursor: pointer;
    display: block;
    height: 14px;
    padding-top: 1px;
}
.leftmenu-new-item-container .leftmenu-new-item-edit-text .leftmenu-new-item-edit-ico svg {
    width: 16px;
    height: 16px;
    fill: #00b8e4!important;
}
.main-menu-logo .mm-logi-pic-wr svg {
    display: none;
}

.main-menu-plus-popup .main-menu-plus-item:hover {
    background: #00b8e4;
}
.sys-message.sys-message-success .sys-message-text {
    background: #007e9c;
}
.sys-message.sys-message-success::before {
    background: linear-gradient(to left,#007e9c 0,rgba(18,112,9,0) 100%);
}
.sys-message.sys-message-success::after {
    background: linear-gradient(to right,#007e9c 0,rgba(18,112,9,0) 100%);
}
}
