@-moz-document regexp("http://acm.*hdu.edu.cn/.*") {
body {
	background: none;
    margin: 0;
    font-family: Arial;
    font-size: 12px;
}

/* 隐藏首行图片 */
body>table>tbody>tr:first-child {
	display: none;
}

/* 隐藏首行导航 */
body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:first-child {
    display: none;
}


body>table {
	width: 100%;
}

body>table>tbody>tr:nth-child(2)>td>table {
    min-width: 900px;
    max-width: 1100px;
	width: 80%;
    margin: auto;
}

body>table>tbody>tr:nth-child(2)>td>table form input {
	border-style: solid;
    border-color: white;
    background-color: white;
    font-size: 12px;
}

body>table>tbody>tr:nth-child(2)>td {
	border: none !important;
    background-color: black;
}


body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:last-child a {
	color: gray;
}

body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:last-child a:hover {
	color: white;
}

body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:last-child td form table {
	color: gray;
}

body>table>tbody>tr:nth-child(2)>td>table>tbody>tr:last-child td form table tr:first-child {
	display: none;
}




body>table>tbody>tr:nth-last-child(2)>td {
    min-width: 900px;
    max-width: 1100px;
	width: 80%;
    margin: auto;
    display: block;
}


body>table>tbody>tr:nth-last-child(2)>td h1 {
	font-family: 'Open Sans';
    color: black !important;
}

body>table>tbody>tr:nth-last-child(2)>td div {
	color: black;
    font-size: 12px;
    font-family: Arial;
}

body>table>tbody>tr:nth-last-child(2)>td div>a {
	color: green;
    font-size: 12px;
    font-family: Arial;
}


body>table>tbody>tr:last-child table {
	width: 100%;
    border-width: 1px 0 0 0 !important;
    border-color: black !important;
    font-family: Arial;
    font-size: 12px;
    margin-top: 30px;
}



.table_text>tbody>tr {
/* 	line-height: 30px; */
}

.table_header {
	color: white;
    background-color: black !important;
}


.table_text a {
	color: black;
}

.code {
    background-color: white !important;
    border: black 1px solid;
    font-size: 16px;
    font-family: Consolas, Courier New, Courier !important;
    text-align: left;
    padding: 10px;
}

textarea {
    border: black 1px solid;
    background-color: transparent;
}

a {
	font-family: Arial;
    font-size: 12px;
    color: green;
}

input, select{
	border-style: solid;
    border-color: black;
    font-size: 12px;
    background-color: transparent;
}

/* 页脚 */
#contest_footer {
	width: 100%;
    border-width: 1px 0 0 0;
    border-color: black;
    font-family: Arial;
    font-size: 12px;
    margin-top: 30px;
}

form {
	font-family: Arial;
    font-size: 12px;
}

table {
	font-size: 12px;
}





/* MathJax 在 Chrome 下渲染 BUG 的修复 */
.MathJax nobr>span.math>span{border-left-width:0 !important}
}

