@-moz-document regexp("http://acm.*hdu.edu.cn/listproblem.php.*") {
body>table>tbody>tr:nth-last-child(3)>td, p.footer_link {
	min-width: 900px;
    max-width: 1100px;
	width: 80%;
    margin: auto;
    display: block;
    
}

body>table>tbody>tr:nth-last-child(3)>td a, p.footer_link a {
	color: green;
    font-size: 12px;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/contests/.*"), regexp("http://acm.*hdu.edu.cn/notification.php.*"), regexp("http://acm.*hdu.edu.cn/viewnotify.php.*"), regexp("http://acm.*hdu.edu.cn/viewcode.php.*") {
a {
	color: green;
}

#contest_nav {
    border: none;
	width: 100%;
    background-color: black;
    line-height: 54px;
}


#contest_nav a img {
	display: none;
}

#contest_nav a {
	font-weight: normal;
}

#contest_nav a:hover {
	font-weight: normal;
    text-decoration: none;
}

center>div:nth-last-child(2) h1, center>div:nth-last-child(2) h1 a {
	font-family: 'Open Sans';
    color: black !important;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/contests/contest_viewtopic.php.*"), regexp("http://acm.*hdu.edu.cn/contests/contest_listtopic.php.*") {
span.table {
	font-family: Arial;
    margin-bottom:20px;
}

form>div {
    background-color: black !important;
}

form>input, form>textarea {
	border-style: solid;
    border-color: black;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/viewnotify.php.*") {
center table table {
	border: black 1px solid !important;
    background-color: white !important;
}

center table table div {
	border-bottom: black 1px solid !important;
}

center div h1 {
	font-family: 'Open Sans';
    color: black !important;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/contests/contest_showproblem.php.*"), regexp("http://acm.*hdu.edu.cn/showproblem.php.*") {
h1 {
    font-size: 48px;
	font-family: 'Anton';
    color: black !important;
}

center>div:nth-last-child(2) font {
	font-family: 'Open Sans';
}

.panel_title {
	background: none !important;
    padding: 0 20px !important;
    font-family: 'Noto Serif', Georgia,Cambria, 'Times New Roman' !important;
	font-size: 25px !important;
    color: black !important;
}

.panel_content {
	background: none !important;
    font-family: 'Noto Serif', Georgia,Cambria, 'Times New Roman' !important;
    font-size: 21px !important;
    font-style: normal;
    line-height: 1.78;
    letter-spacing: -.003em;
}

.panel_bottom{
	background: none !important;
    padding: 0 20px !important;
    display: none;
}


pre div {
	font-family: Consolas, Courier New,Courier,monospace !important;
    font-size: 17px !important;
    line-height: 1.2;
    background-color: transparent !important;
    border: none !important;
    color: black !important;
    padding: 0 !important;
}

pre>div>div>div>i {
	font-family: 'Noto Serif', Georgia,Cambria, 'Times New Roman' !important;
	font-size: 25px !important;
    font-style: normal;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/contests/contest_submit.php.*") {
#submitform h1 {
	font-family: 'Open Sans';
    color: black !important;
}

input, textarea {
	border-style: solid !important;
    border-color: black !important;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/contests/contest_submit.php.*"), regexp("http://acm.*hdu.edu.cn/viewnotify.php.*") {
/* 这两个页面的页脚比较特殊 */
center>div>table:nth-last-child(4) {
	width: 100%;
    border-width: 1px 0 0 0 !important;
    border-color: black !important;
    font-family: Arial;
    font-size: 12px;
    margin-top: 30px;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/contests/contest_status.php.*") {
form {
	font-family: Arial;
    font-size: 12px !important;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/contests/contest_viewtopic.php.*") {
h1 a {
	font-size: larger;
}


.panel_title, .panel_content, .panel_bottom {
	background: none !important;
}

.panel_title {
    text-align: center;
    color: black  !important;
}

pre>div {
/* 	border: black 1px solid !important; */
	background-color: transparent !important;
    padding: 6px !important;
    width: 980px !important;
}

pre>div:nth-of-type(2n+1) {
	font-size: 16px;
}

pre>div:nth-of-type(2n) {
	border-width: 0 0 1px 0  !important;
    border-color: black !important;
    border-style: solid !important;
}

pre b {
	font-size: 12px;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/contests/client_ranklist.php.*"), regexp("http://acm.*hdu.edu.cn/contests/concern_person.php.*") {
center>div:nth-last-child(2)>table:first-child td {
	font-family: 'Open Sans';
    color: black !important;
}

center>div:nth-last-child(2)>form>table:nth-child(2) tr:first-child td {
    background-color: black !important;
}
}

@-moz-document regexp("http://acm.*hdu.edu.cn/viewcode.php.*") {
center>div:nth-child(2)>div {
	background-color: transparent !important;
    border: none !important;
}

center>div:nth-child(2)>div div:first-child {
	border-bottom: black 1px solid !important;
}

pre {
	font-family: consolas !important;
    font-size: 16px !important;
}
}
