/* styles for style guide */
  [class^="ias"] {
    font-family: 'open sans';
  }

  .cmp-app, .source_page, .source_clarify {
      font-family: 'open sans';
  }

  .ui-widget {
      font-family: 'open sans';
  }

  .ui-widget input, .ui-widget select, .ui-widget textarea, .ui-widget button {
      font-family: 'open sans';
  }

  .ias-tags-box {
      font-family: 'open sans' !important;
  }

  .ias-bold {
      font-family: 'open sans';
    font-weight: bold;
  }

  .ias-regular {
      font-family: 'open sans';
  }
  
  /* styles for stage */
  html, body {
    font-family: 'open sans';
  }
  #Login h2 {
    font-family: 'open sans';
  }
.wrapper .screen-title, .wrapper .screen-subtitle {
font-family: 'open sans';
    font-weight: 600;
}
  .dashboard-widget header .title {
    font-family: 'open sans';
    font-weight: 600;
  }
  .sidebar-region .panel-filter label.strong {
    font-family: 'open sans';
    font-weight: bold;
  }
  #campaignStatusFilterContainer label.strong {
    font-weight: 700;
  }
.ui-th-column th.ui-th-column, .ui-jqgrid .ui-jqgrid-htable th.ui-th-column {
    font-family: 'open sans';
    font-weight: bold;
  }

.wrapper .screen-title, .wrapper
    .screen-subtitle {
    font-family: 'open sans';
    font-weight: 600;
    font-size: 20px;               
}

.sidebar-region .panel-filter label.strong {
    font-family: 'open sans';
}
h2 {
  font-family: 'open sans';
    font-weight: 600;
}
.strong,.bold {
font-weight: 600;
}

.product-nav > div nav a {
    font-family: 'open sans';
    font-weight: 600;
}

.publisher .chart-widget-header { 
  font-family: 'open sans';
    font-weight: 300;
}

.publisher .landing-page .tabstrip-element .element-text {
font-family: 'open sans';
    font-weight: 600;
}

.ui-jqgrid .ui-th-column, .ui-jqgrid .ui-jqgrid-htable th.ui-th-column {
    font-family: 'open sans';
    font-weight: bold;
}

div.t-data-grid {
  font-family:'Open Sans',sans-serif !important;
}

.wizardBody h3 {
  font-family: 'open sans';
}

.publisher .element-text {
  font-family: 'open sans';
}

.publisher.active .element-text {
  font-family: 'open sans';
    font-weight: 600;
}

.trend-indicator-widget  .widget-title, .trend-indicator-widget  .widget-subtitle, .trend-indicator-widget .readout, .trend-indicator-widget .readout-suffix {
    font-family: 'open sans';
    font-weight: 300;
}

.ui-th-column th.ui-th-column, .ui-jqgrid .ui-jqgrid-htable th.ui-th-column .instruction {
  font-weight: bold;
}


.ui-jqgrid tr.jqfoot td  {
  font-weight: 600;
}

b, strong {
    font-weight: normal;
}

table th {
    font-weight: 600;
}

.strong.selected-metrics-header {
    font-weight: normal;
}

.report-display b {
