@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("about:config") {
  /* controls */
  .checkbox-check { border-radius: 0px !important; background: #000C17 none !important; box-shadow: none !important; border: 1px solid #00ACAF !important; }
  .checkbox-check:hover { background-color: #000C67 !important; }
  .checkbox-check[checked] { background: transparent url("http://imgur.com/brxrhpR") center no-repeat !important; }
  button.primary { background: #000C17 none !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  button.primary:hover { background: #000C67 !important; }
  
  /* warning page */
  #warningScreen { background: #000C17 !important; color: #777 !important; }
  
  #warningTitle { color: #00ACAF !important; }
  .title-text { border-bottom-color: #00ACAF !important; }
  
  /* about:config search and table */
  
  /* lowest bg color - not much - only the top search bar bg */
  deck#configDeck { background-color: #000C17 !important; }
  
  /* search bar label */
  #filterRow > label { color: #777 !important; }
  #filterRow > textbox#textbox { background: transparent none !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  
  /* tree-col images */
  .tree-columnpicker-icon { list-style-image: url(data:image/png,%89PNG%0D%0A%1A%0A%00%00%00%0DIHDR%00%00%00%0E%00%00%00%0C%08%06%00%00%00R%80%8C%DA%00%00%00%01sRGB%00%AE%CE%1C%E9%00%00%00%04gAMA%00%00%B1%8F%0B%FCa%05%00%00%00%09pHYs%00%00%0E%C3%00%00%0E%C3%01%C7o%A8d%00%00%00%19tEXtSoftware%00paint.net%204.0.10%AD%0A%0A%C0%00%00%00%3AIDAT%28Sc%18%40%B0f%FD%7F%82%18%2B%40%97%20%C4%87%03%98%A9%F80V%80.A%88%0F%07T%D3H4%C0%A6%11%24%86%8E1%00.%05%D8%C4%88%06di%C2%0A%18%18%00%D8%93b%C5%FC%D7yh%00%00%00%00IEND%AEB%60%82) !important; }
  
  .treecol-sortdirection[sortDirection] { list-style-image: url(data:image/png,%89PNG%0D%0A%1A%0A%00%00%00%0DIHDR%00%00%00%09%00%00%00%05%08%06%00%00%00%97S%C6k%00%00%00%01sRGB%00%AE%CE%1C%E9%00%00%00%04gAMA%00%00%B1%8F%0B%FCa%05%00%00%00%09pHYs%00%00%0E%C3%00%00%0E%C3%01%C7o%A8d%00%00%00%19tEXtSoftware%00paint.net%204.0.10%AD%0A%0A%C0%00%00%00ZIDAT%18Wc%40%07%ECK%D7%FC%872%B1%03%A6%E5%EB%FF%8B%3C%FB%F7_%F6%E2%3D%1C%0A%97%AE%FE%2F%F7%E8%EB%7F%8F_%7F%FF%C7%7F%FC%F5%BF%FB%D8ET%85L%2B%D7%FC%F7%F9%F2%FD%7F%FB%F7%3F%FF%97%3E%F9%F2%FF%C8%E3%2F%FFO%3D%FB%FA%FF%F0%93%AF%F8%ADF%00%06%06%00%E6%9D0%B6%D6%C9%9C%AD%00%00%00%00IEND%AEB%60%82) !important; width: 9px !important; height: 5px !important; }
  
  /* tree-col main object */
  deck#configDeck #configTree { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  
  /* tree-col headers */
  treecols { border-bottom: 1px solid #00ACAF !important; }
  treecol, treecolpicker { color: #00ACAF !important; background-color: #000C17 !important; }
  
  /* tree-col content */
  treechildren::-moz-tree-cell-text { color: #00ACAF !important; }
  treechildren::-moz-tree-row(selected) { background-color: #000C67 !important; border: 1px solid #00ACAF !important; }
  treechildren::-moz-tree-row(hover) { background-color: #000C47 !important; }
  treechildren::-moz-tree-row(default) { color: yellow !important; }
  
