@-moz-document domain("bloxcity.com") {
/* Header */
.white {
background-color: #3B3F51!important;
}
.NavTwo {
background-color: #717699!important;
    font-weight: 500;
}
.NavTwo a {
background-color: #717699!important;
    font-weight: 500;
}
.left {
            font-weight: 500;
    text-transform: uppercase!important;
}
nav a, nav ul a {
    color: #FFF;
}

nav[style*="box-shadow:none;box-shadow: 1px 2px 2px #ccc;"] {
    box-shadow: none!important;
}
nav ul li {
    transition: none!important;
}
nav ul a {
    transition: none!important;
}
.top-header-a-register {
    font-weight: 600;
    padding: 5px 10px;
    border: 1px solid #fff;
    border-radius: 5px;
}
#dropdown1 {
    color: #333!important;
    width: 156px!important;
    top: auto!important;
    transition: none!important;
    box-shadow: 0px 1px 2px #ddd!important;
	border-radius: 3px!important;
}
.dropdown-content li>a, .dropdown-content li>span {
    font-size: 16px;
    color: #514e51!important;
    display: block;
    line-height: 22px;
    padding: 14px 16px;
    background-color: #fefefd;
}
.dropdown-content li {
background-color: #fefefd;
}


.dropdown-content li:hover {
    background-color: #f6f5f8!important;
}
.side-menu-link:hover {
    background: #e9ecf3!important;
}
nav ul a:hover {
    background-color: rgba(0,0,0,0.0);
}
div[style*="color:white;margin:0;border-radius:0;"]{
    padding: 10px!important;
    background-color: #f0675a!important;
}
.side-bar-special {
	visibility: visible;
}
.test a:hover { 
    text-decoration: underline !important; 
}
.NavTwo a:first-child {
    padding-left: 20px;
}
.NavTwo a{
    color: #fff;padding-right: 10px;font-weight:500;
}
.NavTwo a:hover{
    color: #fff;padding-right: 10px;font-weight:500;
}
