css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(fast.com) {
  html, body {background:none !important; background-color:#181818 !important;}
  div, #speed-value, #speed-units {color:#333;}
  div.logo, .test-on-ookla, .footer-container, .spinner {display:none !important;}
  .bordered-speed-container, .after-test-actions-container {border: none !important;;}
  .speed-results-container.succeeded {color:#52D64B!important;}
  .speed-results-container.failed {color:#D12121!important;;}
  .oc-icon-pause {color:#333!important;}
  .oc-icon-refresh {color:#ccc!important;}
  .speed-progress-indicator.in-progress {border-color:#333!important;}
  .speed-progress-indicator.stopped {border-color:#DEBE2F!important;}
  .speed-progress-indicator.succeeded {border-color:#52D64B!important;}
}
