css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://kolotibablo.com/panel/") {
	body {
    background-image: url('http://genpas.narod.ru/img/bg.png');
    color: #fff;
}

.workmenu {
    background: rgba(0, 0, 0, 0);
    text-align: center;
    margin:10px auto;
}

.solver .params .loader {
    border: 0px;
}

input[type="text"] {
    border-radius: 0px;
    border: 0px;
    width: 70%;
}

.solver .input input[type='text'] {
    width: 240px;
    height: 25px;
    font-size: 12pt;
}

button {
    border-radius: 0px;
    border: 0px;
}

.parameter {
    background: rgba(0, 0, 0, 0);
    border: 0px;
}

.solver .params .image {
    border: 0px;
}

.solver .params .parameter {
    border: 0px;
}

.loader {
    border: 0px;
}

.blue-button {
	background: rgba(0, 0, 0, 0);
    border: 0px;
}

.blue-button:hover{
	background: rgba(0, 0, 0, 0);
    border: 0px;
}

.imenu {
   background: rgba(0, 0, 0, 0);
   text-align: center;
   margin:10px auto;
}

.header {
   background: rgba(0, 0, 0, 0);
   text-align: center;
   margin:10px auto;
}

.table-header {
    color: #ffffff;
}

.topmenu {
    background-color: #00000000;
}

.iwidgets .widget {
    border: 0px;
    border-radius: 0px;
}

.iwidgets .widget:hover {
    border: 0px;
    border-radius: 0px;
    color: #000;
}

.finance-states .state {
    color: #fff;
}

.finance-states .state.inactive {
    color: #fff;
}

.table-container tr td {
    color: #fff;
    padding: 4px;
}

.table-container tr:nth-child(odd) {
    background-color: #f5f5f5;
}

.table-container tr td {
    color: #000;
    border-radius: 25px;
}

tspan, textPath {
    fill:#fff;
    color: #fff;
}

.table-container table {
    width: 85%;
    text-align: center;
    margin: auto;
}

.table-container thead tr td {
    padding: 2px;
}

.loader-small { background-image: url('http://bitcoinfaucet.ru/images/loader.gif');}
.content-loader { background-image: url('http://bitcoinfaucet.ru/images/loader.gif');}

.solver .params .loader {
    background-image: url('http://bitcoinfaucet.ru/images/loader.gif');
}

.settings-name {
    color: #fff;
}
.settings-hint {
    color: #fff;
}

.workmenu .buttons .bar {
    border-radius: 0px;
}

.fillwhite {
    border-radius: 0px;
}
}
