css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pokemongodb.net") {

@media only screen and (max-width: 1000px) {
  /* main layout */
  body {
    min-width: auto;
  }
  .region-inner {
    min-width: 0;
    max-width: none;
    width: auto;
  }
  .main-inner .columns {
    padding-left: 0;
    padding-right: 0;
  }
  .main-inner .columns {
    overflow: visible;
  }
  .date-outer {
    border-style: none;
  }
  
  /* not terribly important */
  header,
  .column-left-outer,
  .column-right-outer
  {
    display: none;
  }

  /* menu */
  .tabs-inner .widget li a {
    margin: .2em;
    border: 1px solid #ffd966; 
    font-size: 16px;
  }
  .tabs-cap-top {
    border-top-style: none;
  }
  .tabs-outer {
    background-image: none;
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#1313d0+0,0e0e98+100 */
    background: #1313d0; /* Old browsers */
    background: -moz-linear-gradient(top,  #1313d0 0%, #0e0e98 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top,  #1313d0 0%,#0e0e98 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom,  #1313d0 0%,#0e0e98 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  }
  .tabs-inner {
    padding: .5em .3em;
  }
  .tabs-inner .section {
    margin: 0;
    padding: 0;
  }

  
}

/* @-moz-document domain END */
}
