css:
@-moz-document regexp("(http|https)://www\\.furvilla\\.com/(?!(villager/[0-9]*$|profile/[0-9]*$)).*") {
/* compatibility mode for FVTK-sidebar */

/*
.right-column .widget:nth-of-type(2),.right-column .widget:nth-of-type(3), .right-column .widget:nth-of-type(4) {
display:none;
}
*/

/* end of compatibility mode */

body {
    background: none !important;
    -webkit-filter: brightness(70%);
}

img {
        filter: brightness(60%) contrast(0.85) !important;
}
img:hover {
        filter: brightness(100%)  contrast(1) !important;
}

.widget .forum-posts li:nth-child(odd), .widget .forum-posts li:nth-child(even) {
    background: none !important;
}

.villager-data-wrapper {
    background: none;
}

.villager-info {
    color: #fff;
}    

.panel {
background: rgba(0,0,0,0.5) !important;
}

.btn {
background: rgba(0,0,0,0.5) !important;
    color: #fff !important;
    border: none;
}   

.gender-radio input[type='radio']:empty ~ label {
    background:none;
}

.modal-backdrop {
    background: rgba(30,30,30,1) !important;
}

.progress-bar {
background: none;
}

.progress-bar .progress {
background: rgba(250,250,250,0.5) !important;
}
    

.warrior-view-sword:before, .warrior-view-shield:before {
    background: none;
}

.warrior-view-sword .color-thief-bg, .warrior-view-shield .color-thief-bg {
    background: none;
    border: none;
}

.warrior-view-sword a, .warrior-view-shield a {
        background: none;
    padding-top: 30px;
    border: none;
    display: block;
}

.btn.protection-quotient {
background: none !important;
}

label {
    color: #fff;
}

.warrior-info-section {
    display:none;
}

.job-description {
    display: none;
}

.table thead {
    background: none !important;
}

.forum-category {
 background: rgba(0,0,0,0.5) !important;
    border: none !important;
}

.forum tr:nth-child(odd) td:not(.forum-category) {
     background: rgba(0,0,0,0.5) !important;
}
.forum tr:nth-child(even) td:not(.forum-category) {
     background: rgba(30,30,30,0.5) !important;
}

.readable {
    color: #fff !important;
}

.readable img {
     filter: brightness(50%);
}

.thread-post {
    background: rgba(30,30,30,0.5);
}

.thread-user-info {
    background: rgba(0,0,0,0.5) !important;
}

.thread-user-post {
    border: none;
}

code {
    background: #000;
}

.toolbar-title {
text-align: center;
}                  

.toolbar-title h2:before {
    display: none;
}

input, textarea {
 background: rgba(0,0,0,0.5) !important;
    color: #fff !important;
}

.modal-header h2 {
    color: #fff !important;
}

h1, h2, h3, h4, h5, h6, .status {
    color: #fff !important;
}

.pagination > .active > a, .pagination > .active > a:hover, .pagination > .active > a:focus, .pagination > .active > span, .pagination > .active > span:hover, .pagination > .active > span:focus {
 background: rgba(0,0,0,0.5) !important;
    border-color: #fff;
}

.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    background: none;
    border: none;
}

.pagination > li > a, .pagination > li > span {
background: none;
    border: none;
    color: #fff;
}

.sod_select {
 background: rgba(0,0,0,0.6) !important;
color: #fff;
}

.sod_list_wrapper {
   background: rgba(0,0,0,0.9) !important;
}  

.modal-content {
 background: rgba(0,0,0,0.5) !important;
    color: #fff;
}   

.well, .registration-well, .durability, .item-block, .inventory-item-info {
 background: rgba(0,0,0,0.5) !important;
    border: none;
    color: #fff;
}

.counter {
    border: 1px solid #fff !important;
    padding: 0 8px !important;
    color: #fff !important;
}

.explore-item {
    background: none;
    color: #fff;
    border: none;
}

.btn:hover {
    background: rgba(90,90,90,0.5) !important;
    color: #fff !important;
    border: 1px solid #fff;
}
/*
header {
    position:fixed;
    top: 0px;
    width: 990px;
    left: 50%;
    margin-left: -495px;
    z-index: 99;
}
*/
nav {
position: absolute !important;
top: 0px !important;
left:0px !important;
margin-left: 0px !important;
width: 610px !important;
background: none !important;
z-index: 99;
}

nav ul {
margin-left: 0px !important;
}

.user-panel.clearfix {
position: absolute;
top: 3px;
right:0px;
left: 605px;
background: none !important;
width: 395px;
color: #fff !important;
}

.user-panel .float-left {
position: absolute;
top: 40px;
right: 0px;
background: none !important;
}

a, a:hover {
    color: #fff;
}

li div, .jobs li a, .jobs li a span, .crafting-list-item-info {
background: rgba(0,0,0,0.8) !important;
    color: #fff !important;
    border-color: #000 !important; 
}

.animal-item-info div, .animal-item-info div:hover, .inventory-item-info a:hover {
    background: none !important;
}

li div:hover, li a:hover, .jobs li a span:hover, .crafting-list-item-info:hover {
background: rgba(30,30,30,0.8) !important;
    color: #fff !important;
    border-color: #fff !important; 
}

.content:after {
    content: ' ';   
    visibility:visible;
    position: fixed;
    display:block;
    left: 0px;
    top: auto;
    bottom: -100px;
    z-index: -1;
    background:transparent url(http://i.imgur.com/OYFYYb6.png) no-repeat bottom left;
    height: 100%;
    width: 100%;
}
            
header:after {
    content: ' ';
    position: fixed;
    display:block;
    top: -200px;
    left: 0px;
    z-index: -2;
    background:transparent url(http://i.imgur.com/vtQSVFb.png) repeat top center;
    min-height: 15000px;
    height: 100%;
    width: 100%;
    opacity: .5;
    animation:clouds 1200s linear infinite;
}

body:before {
    content: ' ';   
    position: fixed;
    display:block;
    top: 0px;
    left: 0px;
    height: 500%;
    width: 100%;
    background-color: #f00;
    background:#000 url(http://i.imgur.com/etekPdd.png) repeat top center;
    z-index: -4;
}

body:after {
    content: ' ';
    position: fixed;
    display:block;
    top: -255px;
    left: 0px;
    z-index: -3;
    background:transparent url(http://i.imgur.com/7FCjeIt.png) repeat top center;
    min-height: 15000px;
    height: 100%;
    width: 100%;
    animation:twinkies 150s linear infinite;
} 

header:before {
    content: ' ';
    position: fixed;
    display:block;
    top: 200px;
    left: 0px;
    z-index: -2;
    background:transparent url(http://i.imgur.com/vtQSVFb.png) repeat top center;
    min-height: 15000px;
    height: 100%;
    width: 100%;
    animation:clouds 3600s linear infinite;
} 

.modal p img {
max-width: 210px !important;
max-height:210px !important;
}

.top {
    display: none;
}

.content {
    position: relative;
    top: -175px;
background: rgba(0,0,0,0.5);
    color: #fff;
}

.search-form {
position: relative !important;
top:45px !important;
left:350px !important;
z-index: 5;
}

#map-img {
display:none;
}

.right-column .widget:first-of-type {
position: fixed !important;
left: 25px! important;
top:0px;}

.right-column .widget-header {
display:none;
}


.adsbygoogle {
    display:none !important;
}



#logo {
    display:none;
}

.left-column {
    width:950px !important;
    float: none !important;
}
.header-left {
    float: none;
}
.right-column {
    position: fixed;
    right: 10px;
    top: 0px;
    margin-bottom: 50px;
    overflow:scroll;
    height: 105%;
    width: 230px !important;
    visibility: hidden;
}
.widget {
    visibility: visible;
    margin-bottom: 0px !important;
}

.notifications-widget {
    background: none !important;
    border-radius: 10px;
}

.table tr td {
    background: none !important;
    border: none !important;
    text-align: left;
    color:#fff;
}

.table-striped > tbody > tr:nth-of-type(odd), .table-striped > tbody > tr:nth-of-type(even) {
    background: none;
}


.table tr:hover td {
    background: none !important;
}

.alert {
    border-radius: 10px;
}

.widget-header {
    background: none !important;
    border: none !important;
}

.widget-header h3 {
    background: none !important;
    border: none !important;
}

.widget-content {
    padding: 0 0 !important;
    background: rgba(0,0,0,0.5) !important;
}

.user-info h4 {
    color: #000 !important;
    font-size: 1em !important;
    margin: 10px 0 5px 0 !important;
}

@keyframes twinkies {
    from {
        background-position:0 0;
        
    }
    to {
        background-position:-5000px 5000px;
        
    }
}

@keyframes clouds {
    from {
        background-position:0 0;
        
    }
    to {
        background-position:50000px 0;
        
    }
}
}
