css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tecnovortex.com") {

  
  /* ----------------- PORTADA ------------------- */
  
  
  .td-header-style-8 .td-header-top-menu-full {
    background-color: #222;  
}
  
  .td-header-style-8 .td-header-top-menu {
    border-bottom: 1px solid #999;
}

  .td-header-style-8 .td-header-menu-wrap {
    background-color: #222;
}
    
.td-header-style-8 .td-header-top-menu a {
    color: #999;
}

  a, .top-header-menu .current-menu-item > a {
    color: #f02475;
}
  
  ul.sf-menu > .td-menu-item > a {
    color: #999;
}
  
  .td-header-style-8 .td-social-icon-wrap .td-icon-font {
    color: #999;
}
  
  .td-header-style-8 .header-search-wrap .td-icon-search {
    color: #999;
}
  
  .td-main-content-wrap, .td-category-grid {
    background-color: #222;
}
  
  h1 > a, h2 > a, h3 > a, h4 > a, h5 > a, h6 > a {
    color: #999;
}
  
  .td-footer-wrapper {
    background-color: #222;
}
  
  .footer-text-wrap {
    color: #777;
}
  
  .td-footer-wrapper, .td-footer-wrapper a, .td-footer-wrapper .block-title a, .td-footer-wrapper .block-title span, .td-footer-wrapper .block-title label, .td-footer-wrapper .td-excerpt, .td-footer-wrapper .td-post-author-name span, .td-footer-wrapper .td-post-date {
    color: #777;
}
  
/*.widget {
    background-color: #555;
    overflow:visible
}*/
  
  .td_block_wrap {
    background-color: #222;
}
  
.td-mega-span h3 a {
    color: #999;
}
  
  .td_mega_menu_sub_cats {
    background-color: #333;
}
  
  .td_mega_menu_sub_cats a {
    color: #999;
    
}
  
 
/* ----------------- POSTS ------------------- */
  
  .post {
    background-color: #222;
}
  
  .td-post-content, .td-post-content p {
    color: #999;
}
  
  .td-post-content h1, .td-post-content h2, .td-post-content h3, .td-post-content h4, .td-post-content h5, .td-post-content h6 {
    color: #BDBDBD;
}
  
  ins {
    background: #222;
    text-decoration: none;
}
  
  
  .td-more-articles-box {

    background-color: #333;
    border-color: #555;

}
  
  .td-more-articles-box-title {

    color: #999;
  
  }
  
  
  [class*="td-icon-"] {
    color: #999;
}
  
  * {
    color: #999;
}
  
  .td-post-next-prev-content a {
    color: #999;
}
  
  
  .td-post-small-box a {
    color: #999;
}
  
  .td-post-small-box span {
    color: #999;
}
  
  cite {
    color: #999;
}
  
  p {
    color: #BDBDBD;
}
  
  
    h3 {
    color: #BDBDBD;
}
  
  textarea {
    background-color: #333;
    color: #999;
    border: 1px solid #555;
}
  

    input[type="text"] {
    background-color: #333;
    color: #999;
    border: 1px solid #555;
}
  
  
  input[type="submit"] {
    color: #BDBDBD;
}
  
  
  .td-footer-wrapper .td_block_wrap {
        background-color: #222;

}
  
  
  .td-category-header {
    background-color: #222;
}
  
  
      h1, h2, h5 {
    color: #BDBDBD;
}
  
  
  
  .td-post-template-6 .td-post-header-holder {
    background-color: #222;
}
  
  .white-popup-block {
    background-color: #999;
}
  
  
.td-login-tabs li a {
    background-color: #999;
}
  
  
  .td-login-wrap .td-login-input{
    background-color: #BDBDBD;
}
  
  
  .td-author-name a {
    color: #999;
}
  
  .post .author-box-wrap .td-author-url a {
    color: #888;
}
  
  .wp-caption-text, .wp-caption-dd{
    color: #999;
}
  
  cite a {
    color: #999;
}
  
}
