css:
@-moz-document domain("atelier801.com") {
/* Tema por Eshkation! 
Diga adeus as bordas e o verde feio do fÃ³rum */
@font-face {
    font-family: "Stellar Light";
    src: url("https://dl.dropboxusercontent.com/u/203976530/fonts/Stellar-light.otf") format("opentype");
}
body {
    background-color: #141413;
    font-family: Tahoma;
    font-size: 13px;
}
hr {
    border-top: 1px solid #141413;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
#formulaire_deconnexion > button {
    border-radius: 0px;
}
span.cadre-tribu-nom {
 	font-size: 30px;
    font-family: "Stellar Light";
}
.table-section-cellule {
 	padding-bottom: 0px;
    padding-top: 3px;
    margin-top: 1px;
}

.cadre-message-modere {
    opacity: 0.5;
}
.cadre-forum, .cadre-conversation  {
    background-color: #2A2B28;
    border: 1px solid #3F403F;
    border-radius: 0px;
    padding-left: 0px;
    padding-right: 0px;
}
.table-cadre-cellule-principale {
 	font-size: 13px;   
}
#formulaire_pagination > div > input.input-pagination {
    color: rgba(0, 0, 0, 0.6);
}
#barre_navigation > div > div.container-fluid.menu-principal.ltr > a.btn.btn-navbar.collapsed,
#barre_navigation > div > div.container-fluid.menu-principal.ltr > a.btn.btn-navbar {
    background-image: none;
    background-color: inherit;
    outline: none;
    box-shadow: none;
    border: 1px solid rgba(0, 0, 0, 0.3);
}
.cadre-utilisateur, .cadre-tribu {
    background-color: #2A2B28;
    border: 1px solid #3F403F;
    border-radius: 0px;   
}
div.btn-group.bouton-nom-profil > a > span {
    opacity: 1;
}
.cadre-upload-image-utilisateur {
    background-color: #2A2B28;
    border: 1px solid #3F403F;
    border-radius: 0px;
    padding-left: 0px;
    padding-right: 0px;  
}
.composant-selection-image-utilisateur-upload {
    background-image: none;
    background-color: #3F403F;
    border: 0px;
    font-family: "Stellar Light";
}

.cadre-dernieres-images-utilisateurs {
 	border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    background-color: rgba(255, 255, 255, 0.2);
}

.contenant-image-utilisateur-grille-sans-marge {
    background-image: none;
    background-color: #3F403F;
    border: 0px;
    font-family: "Stellar Light";  
}

.contenant-image-utilisateur-grille-avec-bordure:hover {
    border: 1px solid rgba(255, 255, 255, 0);
}

.contenant-interne-image-utilisateur-grille {
 	border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    background-color: rgba(255, 255, 255, 0.2);    
}

.composant-selection-image-utilisateur-upload:hover {
    background-image: none;
    background-color: #4E504E;
    border: 0px;   
    
}

.nav-tabs>.active>a, .nav-tabs>.active>a:hover, .nav-tabs>.active>a:focus {
    background-color: #52544E;
    border-radius: 0px;
	border: 1px solid rgba(255, 255, 255, 0.1);
    border-bottom-color: transparent;
    margin-left: 0px;
    margin-right: 0px;
    color: #327DCD;
}

.nav-tabs {
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.nav-tabs>li>a {
    border-radius: 0px;
    background-color: #333333;
    margin-left: 0px;
    margin-right: 0px;
    color: #32CD59;
}

.line-striped, .sorting_1, table.dataTable tbody tr {
    background-color: #323232;
}
.input-append .add-on:last-child, .input-append .btn:last-child, .input-append .btn-group:last-child>.dropdown-toggle {
    border-radius: 0px;
    padding-bottom: 2px;
}
.table-striped tbody tr:nth-child(odd) td, .table-striped tbody tr:nth-child(odd) th {
    background-color: #323232;
}
.table th, .table td {
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}
.cadre-sections-actu {
    background-color: #3F403F;
}
.table-section {
    background-color: #323232;
	margin-bottom: 1px;
}
.cadre-sections-actu {
    background-color: #666766;
}
.nombre-messages-nouveau {
    color: #5DC325;
}
.nombre-messages-reponses {
    color: #C3BC25;
}
.nombre-messages-lu {
    color: #686868;
}
.cadre-forum-titre {
    font-family: "Stellar Light";
    letter-spacing: 10px;
    color: #E8E8E8;
}
img.img32.espace-2-2 {
    padding-right: 15px;
}
.cadre-tribu-nom {
    padding-right: 0px;
}
#DataTables_Table_0 > thead > tr {
	font-family: "Stellar Light";   
}
#barre_navigation > .navbar-inner {
    background-image: none;
    background-color: #1F1F1F;
}
.menu-secondaire {
    background-color: #252525;
}
#formulaire_pagination > div > a:nth-child(1) {
    background-color: #404040;
}
#barre_navigation > div > div.container-fluid.menu-principal.ltr > div > ul.nav.pull-right.ltr > li.dropdown.open > ul > li > a:hover {
    background-color: #393939;
    background-image: none;
}
.popup-recherche {
    background-color: #2A2B28;
    color: #C2C2DA;
    border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}
.dropdown-menu {
    border-radius: 0px;
}
.modal-header {
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    font-family: "Stellar Light";
    font-weight: 100;
    color: #FFFFFF;
    background-color: #1F1F1F;
}
.modal-body {
 	background-color: #2A2B28;   
    color: #AAAAAA;
}
.modal-footer {
 	background-color: #1F1F1F;   
    border-radius: 0px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    -webkit-box-shadow: none;
}
.close {
    text-shadow: none;
    color: #C2C2DA;
}
input[type="number"], input[type="text"], input[type="password"], input[type="email"], input[type="search"] {
    border: 0px;
    border-radius: 0px;
    outline: none;
    background-color: #D8D8D8;
}
.input-xxlarge, .input-xlarge {
    background-color: #D8D8D8;
}
select {
    border-radius: 0px;
}
#form_recherche > a:nth-child(1) {
 	border-radius: 0px;
    padding-top: 2px;
    padding-left: 6px;
    padding-right: 6px;
}
#form_recherche > a:nth-child(9) {
    border-radius: 0px;
}
.input-xxlarge:hover, .input-xxlarge:focus, .input-xlarge:hover, .input-xlarge:focus {
  	outline: none !important; 
    box-shadow: none !important;
    transition: none;  
}
#formulaire_contact > div > h1 {
    font-family: "Stellar Light";
    letter-spacing: 10px;
    color: #F5F5F5;
}
.cadre-formulaire {
    border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

legend {
    font-family: "Stellar Light";
    margin-bottom: 20px;
    padding-bottom: 5px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

input:hover, input:focus {
 	outline: none !important; 
    box-shadow: none !important;
    transition: none;
}

.btn-group > .btn {
    border: 0px;
}
.btn-group>.btn:first-child,
.btn-group>.btn:last-child,
.btn-group>.dropdown-toggle {
    border-radius: 0px;
}
.barre-navigation, navigation-resultat-recherche {
    border-radius: 0px;
    background-color: #2b2b2b;
}
.cadre-resultat-recherche {
    background-color: #252525;
    border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}
.navigation-resultat-recherche {
    background-color: #2B2B2B;
}
.brand.lien-titre {
    font-family: "Stellar Light";
    letter-spacing: 3px;
}
.cadre-jeu {
    border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    background-color: #2A2B28;
}
.texte-jeu {
    font-size: 13px;
}
.cadre-section-titre-mini {
    font-family: "Stellar Light";
    letter-spacing: 3px;
}
#corps > div:nth-child(2) > div > div > h2 {
    font-family: "Stellar Light";
    letter-spacing: 3px;    
}
#cadre_nouveau_message > fieldset > legend {
    font-family: "Stellar Light";
    margin-bottom: 20px;
    padding-bottom: 5px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.input-message {
    border-radius: 0px;
    box-shadow: none;
}
.cadre-sujet {
    font-family: "Stellar Light";
    font-size: 15px;
    background-color: #2A2B28;
    border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}
.cadre-sujet-postit {
    background-color: #3C3D39;
}
.cadre-sondage {
    border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    background-color: #3C3D39;
}
.cadre-defaut {
    border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    background-color: #3C3D39;   
}
.bouton-action {
    width: 25px;
    height: 25px;
    outline: none;
    text-shadow: none;
    border: none;
}
.cadre-type-auteur-joueur {
    opacity: 0.6;
}
.cadre-type-auteur-moderateur {
    opacity: 0.6;
}
.cadre-type-auteur-mapcrew {
    opacity: 0.6;
}
.cadre-sujet-date {
    color: #626262;
}
.cadre-type-auteur-sentinelle {
    opacity: 0.6;
}
.cadre-type-auteur-admin {
    opacity: 0.6;
}
a,
a:hover,
a:focus {
    text-decoration: none;
    text-shadow: none;
    -webkit-transition: 500ms linear 0s;
    -moz-transition: 500ms linear 0s;
    -o-transition: 500ms linear 0s;
    transition: 500ms linear 0s;
    outline: 0 none;
}
.btn {
    border-radius: 0px;
    background-image: none;
    border-color: rgba(255, 255, 255, 0.1);
    box-shadow: none;
}
.element-bouton-profil {
    opacity: 1;
}
.btn-message {
    min-width: 50px;
    background-color: rgba(255, 255, 255, 0.2);
}
.btn-message:hover {
    background-color: rgba(255, 255, 255, 0.5);
    -webkit-transition: 500ms linear 0s;
    -moz-transition: 500ms linear 0s;
    -o-transition: 500ms linear 0s;
    transition: 500ms linear 0s;
}
.btn-message:focus {
    background-color: rgba(255, 255, 255, 0.5);
}
.cadre-repondre {
    border-radius: 0px;
}
.cadre-quote, .message-resultat-recherche  {
    background-color: rgba(60, 60, 60, 0.5);
    border-radius: 0px;
}
.cadre-presentation, .cadre-moderation {
    background-color: rgba(80, 80, 80, 0.5);
    border-radius: 0px;   
}
.cadre-quote small {
    color: #94B7DA;
}
.cadre-spoil, .cadre-spoil:active {
    background-color: rgba(40, 40, 40, 0.5);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 0px;
}
.cadre-message, .cadre-contact {
    border-radius: 0px;
    border: 1px solid rgba(255, 255, 255, 0.1);
    background-color: #21211F;
}
img.element-composant-auteur {
    outline: 2px solid #21211F;
  	outline-offset: -2px;
}
img.img100 {
    outline: 2px solid #2A2B28;
	outline-offset: -2px;
}
img.img50 {
    outline: 2px solid inherit;
	outline-offset: -2px;    
}

.odd, .even {
   margin-bottom: 1px;
}
.span-etire-hauteur {
    margin-left: 15px;
}

::-webkit-scrollbar {
  width: 4px;
  height: 4px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: #666766;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-thumb:hover {
  background: #c7c7c7;
}
::-webkit-scrollbar-thumb:active {
  background: #f5f5f5;
}
::-webkit-scrollbar-track {
  background: #1f1f1f;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-track:hover {
  background: #252525;
}
::-webkit-scrollbar-track:active {
  background: #333333;
}
::-webkit-scrollbar-corner {
  background: transparent;
}
}
