css:
@-moz-document domain("index.hu") {
body {
        background-color: #262626;
        color: #fff;
    }
    .normal-vezeto-blokk,
    .kisero-blokk,
    .nagykepes-vezeto-blokk,
    .dupla-vezeto-blokk,
    .szupervezeto-blokk,
    .medialepedo,
    .cikkgyujto-pp-container,
    .divany-header,
    .totalcar-header,
    .napi-doboz,
    .inforadio-doboz,
    .travelo-doboz,
    .porthu-doboz,
    .index2,
    .medialepedo-blokk,
    .kisero-medialepedo-blokk,
    .container.border,
    div#_iph_cp_popup > div.border {
        background-color: #000;
    }
    
    #pp-posts-container,
    .bom_container {
        background: #404040 none repeat scroll 0 0;
    }
    .index2-also-blokk article .cim .cikkcim,
    .index2-also-blokk article .ajanlo {
        background-color: #262626;
    }
    .index2-also-blokk .hasab-1 {
        background-color: #262626;
    }
    .index2-also-blokk .hasab-2 {
        background-color: #262626;
    }
    .index2-also-blokk .hasab-3 {
        background-color: #262626;
    }
    .index2-doboz .index2-atvezetes {
        background: #262626 none repeat scroll 0 0;
    }
    .post-title {
        background: #000;
    }
    .triangle {
        border-bottom: 10px solid transparent;
        border-right: 10px solid black;
    }
    .rovatok li {
        border-right: 2px solid #000;
        float: left;
    }
    .rovatok li:first-child {
        border-left: 10px solid #000;
    }
    .blokk-label {
        background: #000 none repeat scroll 0 0;
    }
    .index2-header .uj-cikkek-link {
        background-color: #262626;
    }
    .index-header-wrapper {
        background-color: #404040;
    }
    
    .container
    {
        background-color: #404040;
    }
    
    .cimlap-anyag {
        background-color: #262626;
    }
    .velvet.container.border {
        background-color: #404040;
    }
    .kiemelt-tetris .cikkcim a {
        background-color: #555555;
        box-shadow: 5px 0 0 #555555, -5px 0 0 #555555;
    }
    .index2-vezeto-blokk article.vezeto-anyag .ajanlo {
        color: #fff;
    }
    .index2-vezeto-blokk .index2-hasab-container {
        background: #000;
    }
    .index2-blogketrec-blokk .hasab-1,
    .index2-blogketrec-blokk .hasab-2,
    .index2-blogketrec-blokk .hasab-3 {
        background: #000;
    }
    .container,
    div#_iph_cp_popup > div {
        background-color: #262626;
    }
    .portfolio.container.border,
    div#_iph_cp_popup > div.portfolio.border {
        background: #000;
        border-top: 1px solid #000;
    }
    .totalcar-header .trending-topics,
    .velvet-header .trending-topics,
    .divany-header .trending-topics {
        background: #000;
        overflow: hidden;
    }
    .velvet-doboz .kiemelt-keretes .cim .cikkcim {
        background: white;
    }
    .napi-header .trending-topics,
    .portfolio-header .trending-topics,
    .inforadio-header .trending-topics,
    .travelo-header .trending-topics {
        background: #000;
    }
    .napi-header .trending-topics li a,
    .portfolio-header .trending-topics li a,
    .inforadio-header .trending-topics li a,
    .travelo-header .trending-topics li a {
        border-right: 4px solid #000;
    }
    .divany-doboz.normal-vezeto-blokk article .cikkcim a,
    .divany-header .header-hasab-2:not(.cimke) a {
        color: #9c288b;
    }
    .travelo-doboz.travelo-header .trending-topics {
        overflow: hidden;
        height: 19px;
        background: #000;
    }
    .travelo-doboz.travelo-header .trending-topics li a {
        border-right: 4px solid #000;
    }
    .travelo-doboz.normal-blokk .box_rotate_container {
        background-color: #000;
        border-right: 1px solid #000;
        border-bottom: 1px solid #000;
    }
    .travelo-doboz.normal-blokk > .blokk-hasab {
        background-color: #000;
    }
    .porthu-doboz,
    .porthu-doboz.container,
    div#_iph_cp_popup > div.porthu-doboz {
        background-color: #000;
    }
    .shopline-blokk {
        background: #000;
    }
    .shopline-header {
        background-color: #000;
    }
    #also_bannerek {
        background-color: #000;
    }
    .rovat_mindekozben .pp-post .content {
      background: #000;
    }
    
    .page_overlay_content {
       background-color: #000;
       box-shadow: 0 4px 23px 5px rgba( 0, 0, 0, 0.2 );
    }
    
    #social_wrapper {
       background-color: #000; 
    }
    
    #social_wrapper .sziget {
       background-color: #262626;
    }
    
    #social_wrapper .kep.nokep {
       background-color: #262626;
    }
    
    #social_wrapper .sziget .ajanlo {
    color: #fff;
    }
    
    .linkpreview {
        background-color: #262626;
    }
    
    .linkpreview a{
        color: #ff6600;
    }
    
    .nuidezet_container, .nuidezet_container.szelsojobb, blockquote, .cikk-torzs blockquote, .blog .blog-poszt .ajanlo .kopf blockquote {
       color: #777;
    }
    
    rovat_mindekozben .pp-post .content .triangle {
       border-right: 10px solid black;
    }
    
    .rovat_mindekozben .cikk-torzs ul.current-post .pp-post, .rovat_mindekozben .cikk-torzs ul.main .pp-post {
       background: #262626;
    }
        
    .pp-post .content .title a {
        color: #fff; 
    }
    
    .rovat_mindekozben .pp-post .content .triangle {
       border-right: 10px solid #000;
    }
    
    .index2-also-blokk .hasab-2 {
       border-left: 4px solid #262626;
       border-right: 4px solid #262626;
    }
    
    .index2-also-blokk .hasab-1 {
       border-right: 8px solid #262626;
    }
    
    .index2-also-blokk .hasab-3 {
       border-left: 8px solid #262626;
    }
    
    .index2-blogketrec-blokk .hasab-1 {
       border-right: 8px solid #262626;
    }
    
    .index2-blogketrec-blokk .hasab-2 {
       border-left: 4px solid #262626;
       border-right: 4px solid #262626;
    }
    
    .index2-blogketrec-blokk .hasab-3 {
       border-left: 8px solid #262626; 
    }
    
    a {
        color: #ff6600;
        text-decoration: none;
    }

	.rovat_mindekozben .pp-post .content .title a {
    	color: #fff;
	}

	.cikk_also_ajanlo .ajanlo {
    	color: #777;
	}

    body.blog {
      background-color: #000;
	}
  
	.rovat.rovat_nagykep .blog-poszt a {
    	color: #f60;
	}
}
