css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bsn.boards.net") {
  /* general */
  body {
    font-family: Segoe UI,Verdana,sans-serif;
  }
  
  .button,
  a.button,
  input[type="submit"] {
    background: none;
    background-color: #064066;
    border-radius: 0;
    font-weight: bold;
    border: none;
  }
  .button:hover,
  a.button:hover,
  input[type="submit"]:hover {
    background: none;
    background-color: #03314F;
    font-weight: bold;
    border:none;
  }
  a {
    color: white;
    font-weight: bold;
  }
  /* header */
  #navigation-menu {
    background: none;
    background-color: #064066;
    padding: 0 8px 0 0;
    border-radius: 0;
  }
  #navigation-menu > ul li a.state-active {
    background: none;
    background-color: #03314F;
  }
  #navigation-tree {
    border: 0;
    border-radius: 0;
  }
  #nav-tree > li:hover {
    background-color: #03314F;
  }
  #nav-tree > li:hover .menu_arrow span {
    border-color: transparent transparent transparent #03314F;
  }
  .popup_html ul {
    box-shadow: none;
  }
  .recent-threads-button {
    border: none;
    border-radius: 0;
    margin: 0 0 10px 0;
  }
  .recent-threads-button:hover {
    background-color: #03314F;
  }
  .new-icon,
  .new-icon:hover {
    border: none;
    background: none;
    margin: 1px 5px 0 0;
  }

  /* main board */
  .container > .title-bar {
    background-image: none;
    background-repeat: no-repeat;
    background-color: #064066;
    border-radius: 0;
  }
  .container > .content {
    background: #606060;
    border-color: #505050;
    padding: 0 8px;
    border-radius: 0;
  }
  table.list > tbody > tr > td,
  table.list > thead > tr > th {
    border-color: #505050;
    border-left: none;
    border-right: none;
  }
  .list .board.item {
    height: 70px;
  }
  .list .item:hover .main .link a {
    text-decoration: none;
  }
  .list .board.item:hover,
  .list .item.state-hover {
    background-color: #585858;
  }
  .ui-selectMenu-box,
  .ui-search .search-input,
  .ui-search .search-filters-button {
    border-radius: 0;
  }
  
  /* board info and stats */
  .stats .content > table > tbody > tr > td {
    border-color: #505050;
    border-left: none;
    border-right: none;
  }
  
  /* dialogs */
  .ui-dialog {
    background-color: #2A2A2A;
    border-radius: 0;
    padding: 10px;
    box-shadow: rgba(0,0,0,0.5) 0px 5px 10px;
  }
  .ui-dialog .ui-dialog-titlebar {
    background: none;
    background-color: #3C3C3C;
    border-radius: 0;
  }
  .ui-dialog .ui-dialog-content {
    border: none;
    background-color: #4D4D4D;
    border-radius: 0;
  }
  .ui-dialog .ui-dialog-content .users .micro-profile {
    border: 0;
    background: none;
  }
  .ui-dialog .container .control-bar,
  #recent-threads
  {
    background-color: #4D4D4D;
  }
  #recent-threads .container.threads {
    padding: 8px;
  }
  #recent-threads .container.threads tr {
    padding: 5px;
  }
  #recent-threads .container.threads tr.state-hover {
    background-color: #404040;
  }
  #recent-threads .container.threads td,
  #recent-threads .container.threads th {
    border: none;
    border-bottom: 1px solid #3C3C3C;
  }
  .ui-dialog div.ui-dialog-buttonpane {
    background: none;
    border: none;
  }
  .ui-dialog div.ui-dialog-buttonpane button {
    background: #064066;
    border-radius: 0;
    border:none;
    font-weight: bold;
  }
  .ui-dialog div.ui-dialog-buttonpane button:last-child {
    margin-right: 0;
  }
  .ui-dialog div.ui-dialog-buttonpane button:hover {
    background-color: #03314F;
  }
  .ui-dialog div.ui-dialog-buttonpane.ui-widget-content {
    padding: 0;
  }
  
  /* messages */
  .posts .post > td {
    border-color: #505050;
  }
  .posts .post .content-head {
    border: none;
  }
  .message a {
    text-decoration: underline;
    color: #FFFFFF !important;
  }
  /* no chrome support for styling blank elements */
  /* the ".likes" span has a \n inside, so it doesn't match :empty */
  /*  .content-head .info .likes {
    background-color: #4D4D4D;
    padding: 4px 8px;
    border-radius: 2px;
  }
  .content-head .info .likes:-moz-only-whitespace {
    display: none;
  }*/
  .mini-profile,
  .even .mini-profile {
    border: 1px solid #505050;
    border-radius: 0;
  }
  .posts .post .message,
  .posts .post.even .message {
    font-size: 1.2em;
    color: #F2F2F2;
  }

  body > .quote > .quote_body,
  body > .quote > .quote_body > .quote > .quote_body,
  #content .content .quote div.quote div.quote_body,
  #content .content .quote div.quote_body {
    border: 1px solid #4D4D4D;
    border-radius: 0;
    background-color: #707070 !important;
    
  }
  body > .quote > .quote_header,
  .quote div.quote_header {
    background-color: #575757;
    margin-top: -10px;
    margin-left: -10px;
    margin-right: -10px;
    margin-bottom: 10px;
    padding-top: 10px;
    padding-right: 12px;
    padding-bottom: 10px;
    padding-left: 40px;
  }
  body > .quote > .quote_body {
    position: relative;
  }
  .quote_avatar_container {
    position: absolute;
    top: 0;
    left: 0;
  }
  .avatar_size_quote {
    position: absolute;
    top: 0px;
    left: 0px;
    padding: 5px 6px;
}
  .container > .title-bar > .controls li a {
    background: none;
  }
  .container > .title-bar > .controls li a:hover {
    background-color: #03314F;
  }
  a.button.likes-button.js-likes-button {
    background-color: #064066;
  }
  a.button.likes-button.js-likes-button.liked {
    background-color: #064066;
    border: none;
    height: 23px;
  }
  a.button.likes-button.js-likes-button.liked img {
    display: none;
  }
  a.button.likes-button.js-likes-button.liked:hover {
    font-weight: normal;
  }
  a.button.likes-button.js-likes-button.liked:after {
    content: "You like this";
    line-height: 23px;
  }
  a.button.likes-button.js-likes-button:hover {
    background-color: #03314F;
  }
  
  /* create post */
  .wysiwyg-area .subject_line input {
    background-color: #4D4D4D;
    border: 1px solid #7D7D7D;
    color: #D8D8D8;
  }
  .wysiwyg-area .content > form > div {
    margin-top: 10px;
  }
  .wysiwyg-area .subject_line .controls .button {
    padding: 4px 8px;
  }
  .wysiwyg-area .subject_line .controls .button .status {
    padding: 0 11px 0 0;
  }
  .wysiwyg-area .subject_line .controls .button .icon {
    padding: 1px 0 0 6px;
  }
  .wysiwyg-area .submit.content-box.pad-all-double {
    background: none;
    border: none;
  }
  .wysiwyg-tabs li {
    background-color: #606060;
    border: none;
    border-radius: 0;
  }
  .wysiwyg-tabs li.ui-active:hover {
    background-color: #064066;
  }
  .wysiwyg-tabs li:hover {
    background-color: #585858;
  }
  
  /* quick reply */
  .quick-reply textarea {
    background-color: #505050;
    border: 1px solid #3C3C3C;
    color: #F2F2F2;
  }
    .quick-reply .content {
    padding: 8px;
  }
  
  /* normal reply */
  .ui-wysiwyg .editors {
    border: 1px solid #3C3C3C;
  }
  html {
    background-color: #505050;
  }
  body {
    background-color: #505050;
    color: #F2F2F2;
  }
  
  /* shoutbox */
  .shoutbox_name {
    padding: 0 10px;
  }
  .shoutbox_form {
    border-radius: 0;
    margin-bottom: 8px;
    background-color: #606060;
    border: 1px solid #505050;
    color: #F2F2F2;    
  }
  .shoutbox_messages {
    border-bottom: 1px solid #505050;
  }
  .shoutbox_messages .shoutbox-post {
    border: 0;
    border-radius: 0;
    background: 0 !important;
  }
  .shoutbox-post .content-box a, .container a {
    font-weight: bold;
  }
  .shoutbox_form textarea {
    background-color: #505050;
    border: 1px solid #3C3C3C;
    color: #F2F2F2;
    padding: 5px;
  }

  
  /* pagination */
  .ui-micro-pagination ul li > a {
    border: none;
    border-radius: 0;
    background: none;
  }
  .ui-micro-pagination ul li > a:hover {
    border: none;
    background: none;
    text-decoration: underline;
  }
  .ui-pagination li > a,
  .ui-pagination li.state-disabled > a {
    background: none;
    border: none;
    border-radius: 0;
  }
  .ui-pagination li.state-selected > a {
    background-color: #F2F2F2;
  }
  .ui-pagination li.state-selected > a:hover {
    background-color: #F2F2F2;
  }
  .ui-pagination li > a:hover {
    background-color: #03314F;
  }
  .ui-pagination .ui-pagination-prev.state-over a,
  .ui-pagination .ui-pagination-prev.state-hover a,
  .ui-pagination .ui-pagination-next.state-over a,
  .ui-pagination .ui-pagination-next.state-hover a,
  .ui-pagination .ui-pagination-slot.state-over a,
  .ui-pagination .ui-pagination-slot.state-hover a {
    border: none;
  }
  
  /* search page */
  .search .content .content-box {
    border: none;
    border-radius: 0;
  }
  
  /* user page */
  .show-user .content-box {
    background: none;
    border-color: #505050;
    border-radius: 0;
  }
  .ui-tabMenu {
    margin: 0 -8px;
  }
  .ui-tabMenu ul {
    background-color: #064066;
  }
  .ui-tabMenu ul li,
  .ui-tabMenu ul li:hover,
  .ui-tabMenu ul li.ui-active {
    border-radius: 0;
  }
  .ui-tabMenu ul li:hover {
    background-color: #585858;
  }
  .ui-tabMenu ul li.ui-active a {
    border: none;
  }
}
