@-moz-document domain("www.roblox.com"), domain("web.roblox.com"), domain("forum.roblox.com") {
#groups-switcher,.btn-control-md,.btn-control-xs,.btn-primary-md,.btn-secondary-md,.btn-secondary-xs,.dialog-container.dialog-visible,.dropdown-menu,.form-control,.form-group,.game-card-container,.game-card-native-ad,.game-card-thumb,.input-dropdown-btn,.item-card-container,.menu-vertical,.nav-menu-title,.popover,.popover-content,.profile-container .asset-item img,.profile-container .asset-item span[title],.scroller,.section-content,.stack-row,.vertical-menu,div.col-sm-6.text-overflow.slide-item-container-right.groups,img.header-thumb{border-radius:14px!important}.section-content-off{border-radius:14px}.btn-primary-lg{border-radius:30px;padding:12px}div.col-sm-6.slide-item-container-left div a img{border-radius:30px!important}#chat-header,#chat-main,#dialog-container,.chat-windows-header,.dialog-container.group-dialog.ng-scope.dialog-visible{border-radius:14px 14px 0 0!important}.dialog-message{border-radius:10px!important}.small.dialog-message.dialog-message-content.dialog-triangle.ng-binding{box-shadow:none}#navbar-search-input{border:none}.nav-menu-title,.nav-menu-title:hover{background:rgba(0,0,0,0)!important}#navbar-search-btn{visibility:hidden}#CreateGroupBtn,.translate{border:none;border-radius:10px;box-shadow:0 0 4px 0 rgba(0,0,0,.2);background:#fcfcfc}#CreateGroupBtn,#ctl00_cphRoblox_GroupSearchBar_SearchButton,.btn-control.btn-control-medium,.btn-control.btn-control-medium:hover{background-image:none;background-color:#00A2FF;color:#fff;font-family:'Source Sans Pro',Arial,Helvetica,sans-serif;font-size:14px;padding-left:8px;padding-right:8px;border:none;height:25px!important}#ctl00_cphRoblox_GroupSearchBar_SearchKeyword,#ctl00_cphRoblox_GroupStatusPane_StatusTextBox{padding:10px;border:none}.GroupControlsBox{border:none;background:#fff;padding:8px;border-radius:20px;box-shadow:0 0 4px 0 rgba(0,0,0,.2)}#CreateGroupBtn{background-image:none!important;font-size:18px!important;height:35px!important;border-radius:100px}#navigation{transform:translate(0);transiti